INSERT INTO "" ("'INSERT INTO """" (""insert_statement"") VALUES (''' ||
       REPLACE(
           'INSERT INTO ""Exercise"" (""id"", ""question"", ""answer"", ""concise_answer"", ""source_id"", ""order_index"") VALUES (' ||
           id || ', ' ||
           quote(question) || ', ' ||
           quote(answer) || ', ' ||
           quote(concise_answer) || ', ' ||
           source_id || ', ' ||
           COALESCE(order_index, 'NULL') ||
           ');', '''', '''''') ||
       ''');'") VALUES
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (1, ''''Wo sind die Grundrechte festgelegt?'''', ''''Die Grundrechte sind in den Artikeln 1 bis 19 des Grundgesetzes festgelegt. Darüber hinaus werden sie auch in Urteilen des Verfassungsgerichts und in der Europäischen Menschenrechtskonvention geschützt.'''', ''''In den Artikeln 1 bis 19 des Grundgesetzes, in Urteilen des Verfassungsgerichts und in der Europäischen Menschenrechtskonvention.'''', 1, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (2, ''''Warum wurden die Grundrechte in Deutschland an die Spitze des Grundgesetzes gestellt?<br>A) Weil sie leicht durch Notverordnungen außer Kraft gesetzt werden können.<br>B) Aufgrund der Erfahrungen der Weimarer Republik und der nationalsozialistischen Herrschaft.<br>C) Weil sie keine besondere Bedeutung haben.<br>D) Um die Macht des Reichspräsidenten zu stärken.'''', ''''B) Aufgrund der Erfahrungen der Weimarer Republik und der nationalsozialistischen Herrschaft. Die Verfassungsgeber maßen den Grundrechten einen hohen Rang bei, weil die Weimarer Verfassung zwar Grundrechte enthielt, diese aber nicht einklagbar waren und durch Notverordnungen außer Kraft gesetzt werden konnten. Dies führte zur nationalsozialistischen Willkürherrschaft und zur Entwürdigung des Menschen.'''', ''''B) Aufgrund der Erfahrungen der Weimarer Republik und der nationalsozialistischen Herrschaft.'''', 2, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (3, ''''Welche der folgenden Aussagen beschreibt korrekt die Unterscheidung zwischen Menschenrechten und Bürgerrechten?<br>A) Menschenrechte gelten nur für Staatsangehörige.<br>B) Bürgerrechte sind überstaatliche Rechte.<br>C) Menschenrechte sind natürliche, angeborene Rechte.<br>D) Bürgerrechte umfassen die Meinungsfreiheit.'''', ''''C) Menschenrechte sind natürliche, angeborene Rechte.'''', ''''C) Menschenrechte sind natürliche, angeborene Rechte.'''', 3, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (4, ''''Welches Grundrecht ist nicht ausdrücklich im Grundgesetz verankert?<br>A) Widerstandsrecht<br>B) Wahlrecht<br>C) Datenschutz<br>D) Gleicher Zugang zu öffentlichen Ämtern'''', ''''C) Datenschutz. Der Datenschutz ist in Deutschland noch nicht ausdrücklich im Grundgesetz verankert. In viele Verfassungen der Länder ist das Recht auf den Schutz der personenbezogenen Daten allerdings aufgenommen. Mit dem Volkszählungsurteil des Bundesverfassungsgerichts von 1983 wurde früh das ''''''''Recht auf informationelle Selbstbestimmung'''''''' festgeschrieben. Das Bundesdatenschutzgesetz von 1990 trug in der Folge dazu bei, ''''''''den Einzelnen davor zu schützen, dass er durch den Umgang mit seinen personenbezogenen Daten in seinem Persönlichkeitsrecht beeinträchtigt wird'''''''' (Paragraf 1). Mit zwei weiteren Urteilen hat das Bundesverfassungsgericht 2008 das Recht auf informationelle Selbstbestimmung erheblich gestärkt und sogar ein neues Grundrecht zum Schutz der digitalen Kommunikation geschaffen.'''', ''''C) Datenschutz'''', 4, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (5, ''''Wie sind die Grundrechte im Grundgesetz im Vergleich zur Weimarer Verfassung ausgestattet?'''', ''''Im Vergleich zur Weimarer Verfassung sind die Grundrechte im Grundgesetz mit besonderen Garantien ausgestattet. Artikel 1 (3) des Grundgesetzes besagt, dass die Grundrechte die Gesetzgebung, die vollziehende Gewalt und die Rechtsprechung als unmittelbar geltendes Recht binden. Das bedeutet, dass keine der drei Staatsgewalten etwas tun oder unterlassen kann, was im Widerspruch zu den Grundrechten steht. Diese Grundrechte können notfalls bis zum Bundesverfassungsgericht eingeklagt werden.'''', ''''Die Grundrechte im Grundgesetz sind mit besonderen Garantien ausgestattet und binden alle Staatsgewalten als unmittelbar geltendes Recht. Sie können bis zum Bundesverfassungsgericht eingeklagt werden.'''', 5, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (6, ''''Welche der folgenden Aussagen über die Änderung des Grundgesetzes ist korrekt?<br>A) Das Grundgesetz kann durch ein einfaches Gesetz geändert werden.<br>B) Eine Änderung des Grundgesetzes bedarf der Zustimmung von zwei Dritteln der Mitglieder des Bundestages und des Bundesrates.<br>C) Grundrechte können ohne Einschränkungen geändert werden.<br>D) Die Gliederung des Bundes in Länder kann jederzeit geändert werden.'''', ''''B) Eine Änderung des Grundgesetzes bedarf der Zustimmung von zwei Dritteln der Mitglieder des Bundestages und des Bundesrates.'''', ''''B) Eine Änderung des Grundgesetzes bedarf der Zustimmung von zwei Dritteln der Mitglieder des Bundestages und des Bundesrates.'''', 6, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (7, ''''Welche Aussage über die Grundrechte in Deutschland ist korrekt?<br>A) Grundrechte können jederzeit aberkannt werden.<br>B) Grundrechte dürfen nicht missbraucht werden, um die demokratische Ordnung zu bekämpfen.<br>C) Es gibt kein Recht zum Widerstand gegen verfassungsfeindliche Bestrebungen.<br>D) Soziale Grundrechte sind nicht Teil der deutschen Verfassung.'''', ''''B) Grundrechte dürfen nicht missbraucht werden, um die demokratische Ordnung zu bekämpfen.'''', ''''B) Grundrechte dürfen nicht missbraucht werden, um die demokratische Ordnung zu bekämpfen.'''', 7, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (8, ''''Wie unterscheiden sich soziale Grundrechte von Freiheits- und Gleichheitsrechten?'''', ''''Soziale Grundrechte unterscheiden sich von Freiheits- und Gleichheitsrechten in einem zentralen Punkt: Während Freiheits- und Gleichheitsrechte die Bürger vor staatlichen Eingriffen schützen und gerichtlich einklagbar sind, begründen soziale Grundrechte Ansprüche der Bürger gegenüber dem Staat und fordern staatliche Eingriffe. Beispielsweise schützt das Grundrecht auf Unverletzlichkeit der Wohnung (Art. 13) den Einzelnen vor ungerechtfertigten staatlichen Eingriffen, während ein Grundrecht auf Wohnung den Staat verpflichten würde, jedem Bürger eine angemessene Wohnung zu verschaffen. Ebenso würde ein Grundrecht auf Arbeit den Staat verpflichten, jedem einen Arbeitsplatz zu garantieren, was staatliche Eingriffe in den Arbeitsmarkt und mögliche Einschränkungen der freien Berufswahl und Tarifautonomie zur Folge hätte.'''', ''''Soziale Grundrechte begründen Ansprüche der Bürger und fordern staatliche Eingriffe, während Freiheits- und Gleichheitsrechte die Bürger vor staatlichen Eingriffen schützen und gerichtlich einklagbar sind.'''', 8, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (9, ''''Welche Aussage über soziale Grundrechte in den Verfassungen der Bundesländer ist korrekt?<br>A) Soziale Grundrechte sind in allen Bundesländern uneingeschränkt garantiert.<br>B) Soziale Grundrechte sind an die finanziellen Möglichkeiten des Landes gebunden.<br>C) In der brandenburgischen Verfassung gibt es keine sozialen Grundrechte.<br>D) Soziale Grundrechte sind nur in den alten Bundesländern verankert.'''', ''''B) Soziale Grundrechte sind an die finanziellen Möglichkeiten des Landes gebunden. In den Verfassungen zahlreicher Bundesländer, insbesondere in den neuen Bundesländern, sind soziale Rechte wie Arbeit, soziale Sicherheit und Wohnung festgelegt. Diese Rechte sind jedoch Staatszielbestimmungen, die allgemein verpflichtend sind und an die finanziellen Möglichkeiten des jeweiligen Landes gebunden sind. Zum Beispiel verpflichtet die brandenburgische Verfassung das Land, im Rahmen seiner Kräfte für die Verwirklichung des Rechts auf soziale Sicherung, eine angemessene Wohnung und Arbeit zu sorgen.'''', ''''B) Soziale Grundrechte sind an die finanziellen Möglichkeiten des Landes gebunden.'''', 9, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (10, ''''Welche Pflichten sind im Grundgesetz ausdrücklich aufgeführt?'''', ''''Im Grundgesetz sind einige wenige Pflichten ausdrücklich aufgeführt. Dazu gehören die Pflicht zur Verfassungstreue für Inhaber des (wissenschaftlichen) Lehramts (Art. 5 Abs. 3), die Pflicht der Eltern zur Pflege und Erziehung der Kinder (Art. 6 Abs. 2), die Wehrpflicht und die Pflicht zum zivilen Ersatzdienst (Art. 12a). Diese Pflichten sind Beispiele dafür, wie das Grundgesetz bestimmte Verantwortlichkeiten festlegt, während es gleichzeitig betont, dass Demokratie auf der Eigenverantwortung der Bürger basiert, ihre Pflichten gegenüber der Gemeinschaft wahrzunehmen.'''', ''''Die Pflichten im Grundgesetz umfassen die Verfassungstreue für Lehramtsinhaber, die elterliche Pflege und Erziehung, die Wehrpflicht und den zivilen Ersatzdienst.'''', 10, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (11, ''''Welche Aussage über den Europäischen Gerichtshof für Menschenrechte (EGMR) ist korrekt?<br>A) Der EGMR besteht aus Richtern, die von der Europäischen Union ernannt werden.<br>B) Bürger können nur dann Klage erheben, wenn sie den innerstaatlichen Rechtsweg ausgeschöpft haben.<br>C) Die Amtszeit der Richter beträgt zehn Jahre.<br>D) Russland hat die Reform zur Entlastung des EGMR ratifiziert.'''', ''''B) Bürger können nur dann Klage erheben, wenn sie den innerstaatlichen Rechtsweg ausgeschöpft haben.'''', ''''B) Bürger können nur dann Klage erheben, wenn sie den innerstaatlichen Rechtsweg ausgeschöpft haben.'''', 11, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (12, ''''Welche Auswirkungen haben die Entscheidungen des Gerichtshofes auf die Mitgliedsstaaten?<br>A) Sie sind unverbindlich und dienen nur als Empfehlungen.<br>B) Sie müssen von den Staaten befolgt werden und führen oft zu Änderungen der Gesetzgebung.<br>C) Sie betreffen nur wirtschaftliche Angelegenheiten.<br>D) Sie haben keinen Einfluss auf die Menschenrechte.'''', ''''B) Sie müssen von den Staaten befolgt werden und führen oft zu Änderungen der Gesetzgebung. Die Tätigkeit des Gerichtshofes hat wesentlich dazu beigetragen, dass Menschenrechte und Grundfreiheiten in Europa einen hohen Wert darstellen. Viele grundlegende Urteile, in denen Staaten verurteilt wurden, haben zu Änderungen der Gesetzgebung und des Umganges der Mitgliedsstaaten mit den Menschenrechten geführt.'''', ''''B) Sie müssen von den Staaten befolgt werden und führen oft zu Änderungen der Gesetzgebung.'''', 12, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (13, ''''Wo sind die Grundrechte in Deutschland festgelegt?'''', ''''Die Grundrechte sind in den Artikeln 1 bis 19 des Grundgesetzes festgelegt. Darüber hinaus werden sie auch durch Urteile des Verfassungsgerichts und die Europäische Menschenrechtskonvention geschützt.'''', ''''In den Artikeln 1-19 des Grundgesetzes, in Verfassungsgerichtsurteilen und der Europäischen Menschenrechtskonvention.'''', 13, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (14, ''''Warum haben die Verfassungsgeber den Grundrechten im Grundgesetz einen hohen Rang eingeräumt?'''', ''''Die Verfassungsgeber haben den Grundrechten im Grundgesetz einen hohen Rang eingeräumt, weil sie aus den Erfahrungen der Weimarer Republik und der Zeit des nationalsozialistischen Regimes gelernt haben. Die Weimarer Verfassung enthielt zwar einen Katalog von Grundrechten, aber diese waren nicht einklagbar und konnten durch Notverordnungen außer Kraft gesetzt werden. Dies führte zur Entwürdigung des Menschen und zur Willkürherrschaft der Nationalsozialisten.'''', ''''Die Verfassungsgeber haben den Grundrechten einen hohen Rang eingeräumt, um die Erfahrungen der Weimarer Republik und des nationalsozialistischen Regimes zu berücksichtigen und die Menschenwürde zu schützen.'''', 14, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (15, ''''Was ist der Zweck von Grundrechten in Deutschland?'''', ''''Der Zweck von Grundrechten in Deutschland ist es, den Freiheitsraum des Einzelnen vor Übergriffen der öffentlichen Gewalt zu schützen und als Abwehrrechte des Bürgers gegen den Staat zu dienen. Sie sind zugleich die Grundlage der Wertordnung der Bundesrepublik Deutschland und gehören zum Kern der freiheitlichen demokratischen Grundordnung des Grundgesetzes.'''', ''''Grundrechte schützen den Freiheitsraum des Einzelnen vor staatlichen Übergriffen und sind die Grundlage der Wertordnung Deutschlands.'''', 15, 10);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (16, ''''Welche drei Arten von Grundrechten werden in Deutschland unterschieden?'''', ''''In Deutschland werden drei Arten von Grundrechten unterschieden: Freiheitsrechte, wie das Recht auf freie Meinungsäußerung, Gleichheitsrechte, wie das Recht auf Gleichheit vor dem Gesetz und die Gleichberechtigung von Mann und Frau, und Unverletzlichkeitsrechte oder Abwehrrechte, wie die Unverletzlichkeit der Wohnung, Freizügigkeit, Brief-, Post- und Fernmeldegeheimnis.'''', ''''Freiheitsrechte, Gleichheitsrechte und Unverletzlichkeitsrechte.'''', 16, 9);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (17, ''''Was ist die Bedeutung der unmittelbaren Geltung der Grundrechte im Grundgesetz?'''', ''''Die unmittelbare Geltung der Grundrechte im Grundgesetz bedeutet, dass sie für alle Staatsgewalten, also Gesetzgebung, vollziehende Gewalt und Rechtsprechung, bindend sind. Keine der drei Staatsgewalten kann etwas tun oder unterlassen, was im Widerspruch zu ihnen steht. Die Grundrechte können notfalls bis zum Bundesverfassungsgericht eingeklagt werden.'''', ''''Die Grundrechte sind unmittelbar geltendes Recht, bindend für alle Staatsgewalten, und können notfalls bis zum Bundesverfassungsgericht eingeklagt werden.'''', 17, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (18, ''''Was sind die Bedingungen für eine Änderung des Grundgesetzes in Deutschland?'''', ''''Eine Änderung des Grundgesetzes in Deutschland bedarf der Zustimmung von zwei Dritteln der Mitglieder des Bundestages und zwei Dritteln der Stimmen des Bundesrates. Darüber hinaus sind bestimmte Grundsätze, wie die Gliederung des Bundes in Länder, die Mitwirkung der Länder bei der Gesetzgebung und die in den Artikeln 1 und 20 niedergelegten Grundsätze, unveränderlich.'''', ''''Eine Verfassungsänderung benötigt eine Zweidrittelmehrheit im Bundestag und Bundesrat und darf bestimmte Grundsätze nicht antasten.'''', 18, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (19, ''''Was passiert, wenn jemand bestimmte Grundrechte zum Kampf gegen die freiheitliche demokratische Grundordnung missbraucht?'''', ''''Wenn jemand bestimmte Grundrechte zum Kampf gegen die freiheitliche demokratische Grundordnung missbraucht, verwirkt er sie. Das bedeutet, dass er sich beim Einschreiten der Staatsgewalt nicht auf das betreffende Grundrecht berufen kann.'''', ''''Er verwirkt sie und kann sich nicht auf das Grundrecht berufen.'''', 19, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (20, ''''Was ist der Hauptunterschied zwischen sozialen Grundrechten und den klassischen Freiheits- und Gleichheitsrechten?'''', ''''Der Hauptunterschied zwischen sozialen Grundrechten und den klassischen Freiheits- und Gleichheitsrechten liegt darin, dass die Letzteren Rechte der Bürger schützen und vom Staat fordern, Eingriffe zu unterlassen, während soziale Grundrechte Ansprüche von Bürgern begründen und vom Staat fordern, Eingriffe vorzunehmen.'''', ''''Soziale Grundrechte begründen Ansprüche auf staatliche Leistungen, während klassische Rechte den Staat zu Unterlassung verpflichten.'''', 20, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (21, ''''Was ist das Ziel von Staatszielbestimmungen in den Verfassungen der Bundesländer?'''', ''''Das Ziel von Staatszielbestimmungen in den Verfassungen der Bundesländer ist es, die Verwirklichung sozialer Rechte, wie das Recht auf soziale Sicherung, auf eine angemessene Wohnung und auf Arbeit, zu gewährleisten. Diese Bestimmungen haben allgemein verpflichtenden Charakter und sind an die finanziellen Möglichkeiten des Landes gebunden.'''', ''''Das Ziel von Staatszielbestimmungen ist die Verwirklichung sozialer Rechte, wie soziale Sicherung, Wohnung und Arbeit, innerhalb der finanziellen Möglichkeiten.'''', 21, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (22, ''''Warum enthält das Grundgesetz keinen Katalog von Grundpflichten?'''', ''''Ein Katalog von Grundpflichten würde dem Geist einer demokratischen Verfassung widersprechen, da Demokratie voraussetzt, dass jeder aus eigener Verantwortung seinen Pflichten gegenüber der Gemeinschaft nachkommt. Die selbstverständliche Verbindung von Rechten und Pflichten wird in Art. 33 Abs. 1 des Grundgesetzes hergestellt.'''', ''''Ein Katalog von Grundpflichten widerspricht dem Geist der Demokratie, da jeder Bürger aus eigener Verantwortung seine Pflichten erfüllen soll.'''', 22, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (23, ''''Wie viele Richter sitzen im Europäischen Gerichtshof für Menschenrechte?'''', ''''Im Europäischen Gerichtshof für Menschenrechte sitzen 47 Richter, jeweils einer pro Mitgliedsstaat des Europarats.'''', ''''47 Richter, jeweils einer pro Mitgliedsstaat.'''', 23, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (24, ''''Welche Auswirkungen haben die Entscheidungen des Gerichtshofes auf die Menschenrechte und Grundfreiheiten in Europa?'''', ''''Die Entscheidungen des Gerichtshofes haben wesentlich dazu beigetragen, dass Menschenrechte und Grundfreiheiten in Europa einen hohen Wert darstellen. Viele grundlegende Urteile, in denen Staaten verurteilt wurden, haben zu Änderungen der Gesetzgebung und des Umganges der Mitgliedsstaaten mit den Menschenrechten geführt.'''', ''''Die Entscheidungen des Gerichtshofes haben dazu beigetragen, dass Menschenrechte und Grundfreiheiten in Europa einen hohen Wert darstellen und haben zu Änderungen der Gesetzgebung und des Umganges der Mitgliedsstaaten mit den Menschenrechten geführt.'''', 24, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (25, ''''Was ist die Hauptursache für den Anstieg der globalen Mitteltemperatur seit der Industrialisierung?'''', ''''Die Hauptursache für den Anstieg der globalen Mitteltemperatur seit der Industrialisierung ist die anthropogene Klimaänderung, also die vom Menschen verursachte Klimaänderung.'''', ''''Die vom Menschen verursachte Klimaänderung.'''', 25, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (26, ''''Welche Gase werden durch die Verbrennung fossiler Energieträger und die Land- und Viehwirtschaft in der Atmosphäre freigesetzt?'''', ''''Durch die Verbrennung fossiler Energieträger werden Kohlendioxid (CO2) und durch die Land- und Viehwirtschaft werden Methan (CH4) und Distickstoffmonoxid (Lachgas, N2O) in der Atmosphäre freigesetzt. Diese Gase gehören zu den treibhauswirksamen Gasen, die zu einer Erwärmung der unteren Luftschichten beitragen.'''', ''''Kohlendioxid, Methan und Lachgas.'''', 26, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (27, ''''Was versteht man unter anthropogener Klimaänderung?'''', ''''Unter anthropogener Klimaänderung versteht man die vom Menschen verursachte Veränderung des Klimas. Seit der Industrialisierung steigt die globale Mitteltemperatur der Luft in Bodennähe allmählich an. Wissenschaftliche Forschungen belegen, dass Menschen für einen bedeutenden Teil dieses Temperaturanstiegs verantwortlich sind.'''', ''''Vom Menschen verursachte Veränderung des Klimas.'''', 27, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (28, ''''Welche der folgenden Gase gehören zu den treibhauswirksamen Gasen?<br>A) Sauerstoff (O2)<br>B) Kohlendioxid (CO2)<br>C) Methan (CH4)<br>D) Distickstoffmonoxid (N2O)'''', ''''B) Kohlendioxid (CO2), C) Methan (CH4), und D) Distickstoffmonoxid (N2O) gehören zu den treibhauswirksamen Gasen. Diese Gase tragen zur Erwärmung der unteren Luftschichten bei, indem sie in der Atmosphäre angereichert werden. Kohlendioxid wird hauptsächlich durch das Verbrennen fossiler Energieträger und Entwaldung freigesetzt, während Methan und Distickstoffmonoxid vor allem aus der Land- und Viehwirtschaft stammen.'''', ''''B) Kohlendioxid (CO2), C) Methan (CH4), und D) Distickstoffmonoxid (N2O)'''', 28, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (29, ''''Welche der folgenden Aussagen beschreibt korrekt die Bedeutung von Kipp-Punkten im Klimasystem?<br>A) Kipp-Punkte sind stabile Zustände im Klimasystem.<br>B) Kipp-Punkte führen zu allmählichen Klimaänderungen.<br>C) Kipp-Punkte können abrupte und starke Klimaänderungen verursachen.<br>D) Kipp-Punkte sind unwichtig für das Verständnis des Klimawandels.'''', ''''C) Kipp-Punkte können abrupte und starke Klimaänderungen verursachen. Diese Punkte im Klimasystem sind kritische Schwellen, bei deren Überschreitung das Klima sich stark und möglicherweise abrupt ändern kann. Bereits geringe Änderungen im Klimasystem können bewirken, dass diese Kipp-Punkte erreicht werden, was zu signifikanten Klimaänderungen führt.'''', ''''C) Kipp-Punkte können abrupte und starke Klimaänderungen verursachen.'''', 29, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (30, ''''Wie kann man nach Ansicht des UBA-Präsidenten Dirk Messner den Urbanen Hitzeinseleffekt in Innenstädten mildern?'''', ''''Laut UBA-Präsident Dirk Messner kann man den Urbanen Hitzeinseleffekt in Innenstädten mildern, indem man mehr Grün, insbesondere neue Bäume, anpflanzt und mehr Verschattung durch außenliegenden Sonnenschutz sowie Dach- und Fassadenbegrünung schafft. Darüber hinaus muss man den alten Baumbestand in den Städten schützen und ihn bei anhaltender Trockenheit regelmäßig bewässern.'''', ''''Durch mehr Grün, Verschattung und Schutz des Baumbestands kann man den Urbanen Hitzeinseleffekt mildern.'''', 30, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (31, ''''Welche Maßnahme wird laut der Studie des Umweltbundesamtes NICHT zur Minderung des Hitzeinseleffekts in Städten empfohlen?<br>A) Mehr Grünflächen und neue Bäume<br>B) Verschattung durch Sonnenschutz<br>C) Erhöhung der Bebauungsdichte<br>D) Dach- und Fassadenbegrünung'''', ''''C) Erhöhung der Bebauungsdichte'''', ''''C) Erhöhung der Bebauungsdichte'''', 31, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (32, ''''Welche Maßnahme hat laut der Studie die thermische Behaglichkeit im Außenraum am meisten verbessert?<br>A) Dachbegrünungen<br>B) Bäume mit großen Kronen<br>C) Helle Anstriche der Gebäude<br>D) Versprühen von Wasser'''', ''''B) Bäume mit großen Kronen haben laut der Studie die thermische Behaglichkeit im Außenraum am meisten verbessert. Sie bewirkten im Sommer eine Minderung der Physiologisch Äquivalenten Temperatur (PET) um 10 Kelvin und mehr. Diese Verschattungselemente sind besonders effektiv, um die Hitzeinseln in den Quartieren zu reduzieren und ein angenehmeres Mikroklima zu schaffen.'''', ''''B) Bäume mit großen Kronen'''', 32, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (33, ''''Welcher Faktor trägt NICHT zur Bildung des urbanen Hitzeinseleffekts bei?<br>A) Großflächige Bodenversiegelungen<br>B) Fehlende Begrünung<br>C) Dunkle Flächen wie Asphalt<br>D) Hohe Luftzirkulation'''', ''''D) Hohe Luftzirkulation'''', ''''D) Hohe Luftzirkulation'''', 33, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (34, ''''Welcher Begriff wird oft mit „negativen Emissionen“ gleichgesetzt?<br>A) Klimaneutral<br>B) Treibhausgasneutral<br>C) CCS<br>D) Senken'''', ''''C) CCS (Carbon Capture and Storage) wird oft mit „negativen Emissionen“ gleichgesetzt. CCS ist eine Technologie, die Kohlendioxid (CO2) aus der Atmosphäre entfernt und in unterirdischen geologischen Formationen speichert, um die Menge an Treibhausgasen in der Atmosphäre zu reduzieren.'''', ''''C) CCS'''', 34, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (35, ''''Welcher Begriff beschreibt Produkte, die nicht nur treibhausgasneutral sind, sondern auch indirekte Klimaeffekte bei der Herstellung ausgleichen?<br>A) Klimaneutral<br>B) Treibhausgasneutral<br>C) Kompensation<br>D) Carbon Capture and Storage (CCS)'''', ''''A) Klimaneutral. Klimaneutralität bedeutet, dass Produkte nicht nur treibhausgasneutral sind, sondern auch indirekte Klimaeffekte bei der Herstellung beachtet und ausgeglichen werden. Dies umfasst beispielsweise die verminderte Sonnenlichtrückstrahlung durch Bebauung und Landnutzung an der Produktionsstätte.'''', ''''A) Klimaneutral'''', 35, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (36, ''''Welche der folgenden Aussagen über CCS (Carbon Capture and Storage) ist korrekt?<br>A) CCS führt zu negativen Emissionen.<br>B) CCS ist eine Methode, um Emissionen abzufangen und in unterirdische Lagerstätten zu pumpen.<br>C) CCS ist eine natürliche Senke.<br>D) CCS eignet sich für alle Arten von Emissionen.'''', ''''B) CCS ist eine Methode, um Emissionen abzufangen und in unterirdische Lagerstätten zu pumpen.'''', ''''B) CCS ist eine Methode, um Emissionen abzufangen und in unterirdische Lagerstätten zu pumpen.'''', 36, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (37, ''''Was bedeutet es, wenn ein Produkt als ''''''''klimaneutral'''''''' oder ''''''''treibhausgasneutral'''''''' bezeichnet wird?'''', ''''Wenn ein Produkt als ''''''''klimaneutral'''''''' oder ''''''''treibhausgasneutral'''''''' bezeichnet wird, bedeutet dies, dass die durch die Produktion und den Gebrauch des Produkts entstehenden Treibhausgasemissionen ausgeglichen oder kompensiert werden. Dies kann durch die Verwendung von erneuerbaren Energien, die Implementierung von Emissionsminderungstechnologien oder die Kompensation von Emissionen durch den Kauf von Emissionszertifikaten erreicht werden.'''', ''''Ein ''''''''klimaneutral'''''''' oder ''''''''treibhausgasneutral'''''''' Produkt bedeutet, dass die entstehenden Treibhausgasemissionen ausgeglichen oder kompensiert werden.'''', 37, 6);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (38, ''''Was ist der Unterschied zwischen Klimaneutralität und Treibhausgasneutralität?'''', ''''Klimaneutralität bedeutet, dass ein Produkt nicht nur die direkten Treibhausgasemissionen bei der Herstellung ausgleicht, sondern auch indirekte Klimaeffekte, wie zum Beispiel die verminderte Sonnenlicht-Rückstrahlung durch Bebauung und Landnutzung, beachtet und ausgeglichen werden. Treibhausgasneutralität hingegen bezieht sich nur auf den Ausgleich der direkten Treibhausgasemissionen.'''', ''''Klimaneutralität umfasst den Ausgleich von direkten und indirekten Klimaeffekten, während Treibhausgasneutralität nur die direkten Emissionen betrifft.'''', 38, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (39, ''''Was bedeutet es, wenn ein Land netto-null Emissionen erreichen will?'''', ''''Wenn ein Land netto-null Emissionen erreichen will, bedeutet dies, dass es seine Treibhausgasemissionen so reduzieren muss, dass sie in der zweiten Hälfte des 21. Jahrhunderts auf null sinken. Dies kann erreicht werden, indem die verbleibenden Emissionen durch natürliche oder technische Senken wieder der Atmosphäre entzogen werden.'''', ''''Netto-null Emissionen bedeutet, dass ein Land seine Treibhausgasemissionen auf null reduziert, indem es verbleibende Emissionen durch Senken wieder entfernt.'''', 39, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (40, ''''Wie viele Bundesländer gibt es in Deutschland?'''', ''''Deutschland besteht aus 16 Bundesländern.'''', ''''16'''', 40, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (41, ''''Wie viele Bundesländer gibt es in Deutschland?<br>A) 10<br>B) 15<br>C) 16<br>D) 20'''', ''''C) 16'''', ''''16'''', 41, 1);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (42, ''''Welche vier Landschaftsräume gehören zu Deutschland?'''', ''''Deutschland gehört vier unterschiedlichen Landschaftsräumen an: dem Norddeutschen Tiefland, dem Alpenvorland, der Mittelgebirgsschwelle und den Alpen.'''', ''''Norddeutsches Tiefland, Alpenvorland, Mittelgebirgsschwelle und Alpen.'''', 42, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (43, ''''Welche geografischen Regionen gliedern sich in die Mittelgebirgsschwelle in Deutschland?'''', ''''Die Mittelgebirgsschwelle in Deutschland gliedert sich in einige größere Mittelgebirgssysteme und einzelne Bergzüge, wie das Rheinische Schiefergebirge, das Weserbergland, das Hessische Bergland, den Harz, den Thüringer Wald, das Erzgebirge und das Elbsandsteingebirge.'''', ''''Die Mittelgebirgsschwelle gliedert sich in Rheinisches Schiefergebirge, Weserbergland, Hessisches Bergland, Harz, Thüringer Wald, Erzgebirge und Elbsandsteingebirge.'''', 43, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (44, ''''Welche ist die höchste Erhebung Deutschlands?'''', ''''Die höchste Erhebung Deutschlands ist die 2962 m hohe Zugspitze im Wettersteingebirge.'''', ''''Die Zugspitze (2962 m) im Wettersteingebirge.'''', 44, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (45, ''''Welcher Fluss ist der längste und wasserreichste in Deutschland?'''', ''''Der längste und wasserreichste Fluss Deutschlands ist der Rhein, der 1320 km lang ist und auf deutschem Gebiet 865 km lang verläuft.'''', ''''Der Rhein'''', 45, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (46, ''''Was charakterisiert das Klima Deutschlands?'''', ''''Das Klima Deutschlands ist ein Übergangsklima zwischen dem maritimen Klima Westeuropas und dem Kontinentalklima Osteuropas. Es ist durch eine gemäßigte Temperatur und eine ausreichende Feuchtigkeit gekennzeichnet, die durch Westwinde vom Atlantik gebracht wird. Lokale Abweichungen im Temperaturverlauf und in der Niederschlagshöhe sind häufig.'''', ''''Ein Übergangsklima zwischen maritimem und kontinentalem Klima mit gemäßigter Temperatur und ausreichender Feuchtigkeit.'''', 46, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (47, ''''Welche Gruppen von Menschen trugen zum Bevölkerungswachstum in den westlichen Bundesländern nach dem Zweiten Weltkrieg bei?'''', ''''Zum Bevölkerungswachstum in den westlichen Bundesländern nach dem Zweiten Weltkrieg trugen vor allem Heimatvertriebene und Flüchtlinge aus den ehemaligen deutschen Ostgebieten und den angrenzenden Staaten Ost- und Südosteuropas bei, die bis 1953 etwa 10,6 Millionen Menschen umfassten. Darüber hinaus kamen auch Menschen aus der DDR hinzu, insbesondere bis 1961. Ab den 1960er-Jahren spielte die Zuwanderung ausländischer Arbeitskräfte die größte Rolle.'''', ''''Heimatvertriebene, Flüchtlinge und ausländische Arbeitskräfte.'''', 47, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (48, ''''Welche vier Volksgruppen sind in Deutschland als nationale Minderheiten anerkannt?'''', ''''Die vier anerkannten nationalen Minderheiten in Deutschland sind die Dänen, die Friesen, die Sorben und die Roma.'''', ''''Die vier anerkannten nationalen Minderheiten in Deutschland sind die Dänen, die Friesen, die Sorben und die Roma.'''', 48, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (49, ''''Welche vier Volksgruppen sind in Deutschland als nationale Minderheiten anerkannt?'''', ''''Die vier anerkannten nationalen Minderheiten in Deutschland sind die Sinti und Roma, die Dänen in Südschleswig, die Sorben in der Lausitz und die deutschen Friesen.'''', ''''Sinti und Roma, Dänen, Sorben und deutsche Friesen'''', 49, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (50, ''''Wie lange dauert die allgemeine Schulpflicht in Deutschland?'''', ''''Die allgemeine Schulpflicht in Deutschland dauert je nach Bundesland 9 bis 10 Jahre.'''', ''''9 bis 10 Jahre'''', 50, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (51, ''''Welche landwirtschaftlichen Produkte sind in Deutschland besonders wichtig?'''', ''''In Deutschland sind landwirtschaftliche Produkte wie Getreide, Zuckerrüben, Milch, Butter, Käse, Quark, Schweine- und Rindfleisch von großer Bedeutung. Deutschland ist zusammen mit Frankreich an der Spitze der EU-Staaten in der Erzeugung hochwertiger tierischer Veredelungsprodukte.'''', ''''Getreide, Zuckerrüben, Milch, Butter, Käse, Quark, Schweine- und Rindfleisch.'''', 51, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (52, ''''Welche Energieträger sind in Deutschland am wichtigsten?'''', ''''In Deutschland sind Erdöl, Erdgas, Steinkohle und Braunkohle die wichtigsten Energieträger. Erdöl ist der wichtigste Energieträger, gefolgt von Erdgas, Steinkohle und Braunkohle.'''', ''''Erdöl, Erdgas, Steinkohle und Braunkohle'''', 52, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (53, ''''Wie viele Menschen sind allein im Wirtschaftszweig Tourismus beschäftigt?'''', ''''Im Wirtschaftszweig Tourismus sind etwa 2,6 Millionen Menschen beschäftigt.'''', ''''2,6 Millionen Menschen'''', 53, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (54, ''''Wann trat die Wirtschafts-, Währungs- und Sozialunion zwischen der DDR und der BRD in Kraft?'''', ''''Die Wirtschafts-, Währungs- und Sozialunion zwischen der DDR und der BRD trat am 1. Juli 1990 in Kraft.'''', ''''1. Juli 1990'''', 54, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (55, ''''Wie hoch ist die höchste Erhebung Deutschlands?'''', ''''Die höchste Erhebung Deutschlands ist die 2962 m hohe Zugspitze in den Alpen.'''', ''''2962 m hohe Zugspitze'''', 55, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (56, ''''Wie viele Bundesländer gibt es in Deutschland?'''', ''''In Deutschland gibt es 16 Bundesländer, von denen drei – Berlin, Hamburg, Bremen – Stadtstaaten sind.'''', ''''16 Bundesländer'''', 56, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (57, ''''Welche vier Landschaftsräume gehören zu Deutschland?'''', ''''Deutschland gehört vier unterschiedlichen Landschaftsräumen an: dem Norddeutschen Tiefland, dem Alpenvorland, der Mittelgebirgsschwelle und den Alpen.'''', ''''Norddeutsches Tiefland, Alpenvorland, Mittelgebirgsschwelle und Alpen.'''', 57, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (58, ''''Welche geografischen Strukturen gehören zur Mittelgebirgsschwelle in Deutschland?'''', ''''Die Mittelgebirgsschwelle gliedert sich in einige größere Mittelgebirgssysteme und einzelne Bergzüge, wie das Rheinische Schiefergebirge mit Eifel, Hunsrück, Bergischem Land, Sauerland, Rothaargebirge, Westerwald und Taunus, sowie Weserbergland, Hessisches Bergland, Harz, Thüringer Wald, Erzgebirge und Elbsandsteingebirge.'''', ''''Die Mittelgebirgsschwelle besteht aus verschiedenen Mittelgebirgssystemen und Bergzügen, wie dem Rheinischen Schiefergebirge, Weserbergland, Harz und Erzgebirge.'''', 58, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (59, ''''Was ist die höchste Erhebung Deutschlands?'''', ''''Die höchste Erhebung Deutschlands ist die 2962 m hohe Zugspitze im Wettersteingebirge.'''', ''''Die Zugspitze (2962 m) im Wettersteingebirge.'''', 59, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (60, ''''Welcher Fluss ist der längste und wasserreichste in Deutschland?'''', ''''Der Rhein ist der längste und wasserreichste Fluss Deutschlands mit einer Länge von 1320 km, davon 865 km auf deutschem Gebiet. Er ist die Hauptader eines ausgedehnten mitteleuropäischen Wasserstraßennetzes.'''', ''''Der Rhein'''', 60, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (61, ''''Was charakterisiert das Klima Deutschlands?'''', ''''Das Klima Deutschlands ist ein Übergangsklima zwischen dem maritimen Klima Westeuropas und dem Kontinentalklima Osteuropas. Es ist gekennzeichnet durch eine gemäßigte Klimazone mit genügend Feuchtigkeit vom Atlantik, die zu Niederschlägen zu allen Jahreszeiten führt. Die Temperaturen erreichen ein Maximum im Juli und ein Minimum im Januar.'''', ''''Ein Übergangsklima zwischen maritimem und kontinentalem Klima mit gemäßigten Temperaturen und Niederschlägen zu allen Jahreszeiten.'''', 61, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (62, ''''Welche Faktoren haben zum Bevölkerungswachstum in den westlichen Bundesländern Deutschlands nach dem Zweiten Weltkrieg beigetragen?'''', ''''Die Faktoren, die zum Bevölkerungswachstum in den westlichen Bundesländern Deutschlands nach dem Zweiten Weltkrieg beigetragen haben, waren vor allem der Zuzug von Heimatvertriebenen und Flüchtlingen aus den ehemaligen deutschen Ostgebieten und den angrenzenden Staaten Ost- und Südosteuropas, sowie die Zuwanderung ausländischer Arbeitskräfte seit den 1960er-Jahren.'''', ''''Zuzug von Heimatvertriebenen und Flüchtlingen, Zuwanderung ausländischer Arbeitskräfte'''', 62, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (63, ''''Wie viele Ausländer lebten Anfang 2001 in Deutschland?'''', ''''Anfang 2001 lebten 7,3 Millionen Ausländer in Deutschland, was etwa 8,9 Prozent der Gesamtbevölkerung entspricht.'''', ''''7,3 Millionen'''', 63, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (64, ''''Welche vier Volksgruppen sind in Deutschland als nationale Minderheiten anerkannt?'''', ''''Die vier anerkannten nationalen Minderheiten in Deutschland sind die Sinti und Roma, die Dänen in Südschleswig, die Sorben in der Lausitz und die deutschen Friesen.'''', ''''Sinti und Roma, Dänen, Sorben und deutsche Friesen.'''', 64, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (65, ''''Wie lange dauert die allgemeine Schulpflicht in Deutschland?'''', ''''Die allgemeine Schulpflicht in Deutschland dauert je nach Bundesland 9 bis 10 Jahre.'''', ''''9 bis 10 Jahre'''', 65, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (66, ''''Welche landwirtschaftlichen Produkte sind in Deutschland besonders wichtig?'''', ''''In Deutschland sind landwirtschaftliche Produkte wie Getreide, Zuckerrüben, Milch, Butter, Käse, Quark, Schweine- und Rindfleisch von großer Bedeutung. Deutschland ist zusammen mit Frankreich führend in der EU bei der Erzeugung hochwertiger tierischer Veredelungsprodukte.'''', ''''Getreide, Zuckerrüben, Milch, Butter, Käse, Quark, Schweine- und Rindfleisch.'''', 66, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (67, ''''Welche Energieträger sind in Deutschland am wichtigsten?'''', ''''Die wichtigsten Energieträger in Deutschland sind Erdöl, gefolgt von Erdgas, Steinkohle und Braunkohle.'''', ''''Erdöl, Erdgas, Steinkohle und Braunkohle'''', 67, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (68, ''''Wie viele Menschen sind allein im Wirtschaftszweig Tourismus beschäftigt?'''', ''''Etwa 2,6 Millionen Menschen sind allein im Wirtschaftszweig Tourismus beschäftigt.'''', ''''2,6 Millionen Menschen'''', 68, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (69, ''''Wann trat die Wirtschafts-, Währungs- und Sozialunion zwischen der DDR und der BRD in Kraft?'''', ''''Die Wirtschafts-, Währungs- und Sozialunion zwischen der DDR und der BRD trat am 1. Juli 1990 in Kraft.'''', ''''1. Juli 1990'''', 69, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (70, ''''Welche vier Landschaftsräume gehören zu Deutschland?'''', ''''Deutschland gehört vier unterschiedlichen Landschaftsräumen an: dem Norddeutschen Tiefland, dem Alpenvorland, der Mittelgebirgsschwelle und den Alpen.'''', ''''Norddeutsches Tiefland, Alpenvorland, Mittelgebirgsschwelle und Alpen.'''', 70, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (71, ''''Was sind die Hauptbestandteile der Mittelgebirgsschwelle in Deutschland?'''', ''''Die Mittelgebirgsschwelle gliedert sich in einige größere Mittelgebirgssysteme und einzelne Bergzüge, wie das Rheinische Schiefergebirge, das Weserbergland, das Hessische Bergland, den Harz, den Thüringer Wald, das Erzgebirge und das Elbsandsteingebirge.'''', ''''Die Mittelgebirgsschwelle besteht aus Systemen wie Rheinisches Schiefergebirge, Weserbergland, Hessisches Bergland, Harz, Thüringer Wald, Erzgebirge und Elbsandsteingebirge.'''', 71, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (72, ''''Welche ist die höchste Erhebung Deutschlands?'''', ''''Die höchste Erhebung Deutschlands ist die 2962 m hohe Zugspitze im Wettersteingebirge.'''', ''''Die Zugspitze (2962 m) im Wettersteingebirge.'''', 72, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (73, ''''Welcher Fluss ist der längste und wasserreichste in Deutschland?'''', ''''Der Rhein ist der längste und wasserreichste Fluss Deutschlands und einer der wichtigsten Ströme Europas. Er hat eine Länge von 1320 km, davon entfallen 865 km auf deutsches Gebiet.'''', ''''Der Rhein'''', 73, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (74, ''''Was charakterisiert das Klima Deutschlands?'''', ''''Das Klima Deutschlands ist ein Übergangsklima zwischen dem maritimen Klima Westeuropas und dem Kontinentalklima Osteuropas. Es ist durch eine gemäßigte Temperatur und eine ausreichende Feuchtigkeit vom Atlantik gekennzeichnet. Die Temperaturen erreichen ein Maximum im Juli und ein Minimum im Januar. Lokale Abweichungen im Temperaturverlauf und in der Niederschlagshöhe sind häufig.'''', ''''Ein Übergangsklima zwischen maritimem und kontinentalem Klima mit gemäßigter Temperatur und ausreichender Feuchtigkeit.'''', 74, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (75, ''''Was waren die Hauptgründe für das Bevölkerungswachstum in den westlichen Bundesländern Deutschlands nach dem Zweiten Weltkrieg?'''', ''''Die Hauptgründe für das Bevölkerungswachstum in den westlichen Bundesländern Deutschlands nach dem Zweiten Weltkrieg waren der Zuzug von Heimatvertriebenen und Flüchtlingen aus den ehemaligen deutschen Ostgebieten und den angrenzenden Staaten Ost- und Südosteuropas, sowie die Zuwanderung ausländischer Arbeitskräfte seit den 1960er-Jahren.'''', ''''Der Zuzug von Heimatvertriebenen und Flüchtlingen sowie die Zuwanderung ausländischer Arbeitskräfte.'''', 75, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (76, ''''Welche vier Volksgruppen sind in Deutschland als nationale Minderheiten anerkannt?'''', ''''Die vier Volksgruppen, die in Deutschland als nationale Minderheiten anerkannt sind, sind nicht explizit im Text genannt. Allerdings hat Deutschland im Mai 1995 das Europaratsabkommen zum Schutz nationaler Minderheiten unterzeichnet.'''', ''''Die vier Volksgruppen sind nicht explizit genannt.'''', 76, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (77, ''''Welche vier Volksgruppen werden in Deutschland als nationale Minderheiten anerkannt?'''', ''''Die vier anerkannten nationalen Minderheiten in Deutschland sind die Sinti und Roma, die Dänen in Südschleswig, die Sorben in der Lausitz und die deutschen Friesen.'''', ''''Sinti und Roma, Dänen, Sorben und deutsche Friesen.'''', 77, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (78, ''''Wie lange dauert die allgemeine Schulpflicht in Deutschland?'''', ''''Die allgemeine Schulpflicht in Deutschland dauert je nach Bundesland 9 bis 10 Jahre.'''', ''''9 bis 10 Jahre'''', 78, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (79, ''''Welche Bedeutung hat Deutschland in der Erzeugung von tierischen Veredelungsprodukten in der EU?'''', ''''Deutschland steht zusammen mit Frankreich an der Spitze der EU-Staaten in der Erzeugung hochwertiger tierischer Veredelungsprodukte wie Milch, Butter, Käse und Quark sowie Schweine- und Rindfleisch.'''', ''''Deutschland ist Spitzenreiter in der EU bei der Erzeugung von Milch, Butter, Käse, Quark, Schweine- und Rindfleisch.'''', 79, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (80, ''''Welche Energieträger sind in Deutschland am wichtigsten?'''', ''''Die wichtigsten Energieträger in Deutschland sind Erdöl, gefolgt von Erdgas, Steinkohle und Braunkohle.'''', ''''Erdöl, Erdgas, Steinkohle und Braunkohle'''', 80, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (81, ''''Welcher Verkehrsträger überwiegt im Gütertransport und öffentlichen Personenverkehr in Deutschland?<br>A) Schienenverkehr<br>B) Straßenverkehr<br>C) Binnenschifffahrt<br>D) Luftverkehr'''', ''''B) Straßenverkehr'''', ''''Straßenverkehr'''', 81, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (82, ''''Wann trat die Wirtschafts-, Währungs- und Sozialunion zwischen der DDR und der BRD in Kraft?'''', ''''Die Wirtschafts-, Währungs- und Sozialunion zwischen der DDR und der BRD trat am 1. Juli 1990 in Kraft.'''', ''''1. Juli 1990'''', 82, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (83, ''''Was bedeutet der Begriff ''''''''Demokratie'''''''' wörtlich?'''', ''''Der Begriff ''''''''Demokratie'''''''' bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. Dies bedeutet, dass die Bürger in einer Demokratie das Recht haben, über ihre Regierung mitzubestimmen und ihre Meinung frei zu äußern.'''', ''''''''''''Herrschaft des Volkes'''''''''''', 83, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (84, ''''Wie übt das Volk in einer modernen Demokratie die Herrschaft aus?'''', ''''In einer modernen Demokratie übt das Volk die Herrschaft mittelbar und indirekt aus, indem es sie auf Vertreter (Repräsentanten) überträgt.'''', ''''Indirekt durch Vertreter.'''', 84, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (85, ''''Was ist die Grundlage der staatlichen Macht in Deutschland nach Artikel 20 des Grundgesetzes?'''', ''''Die Grundlage der staatlichen Macht in Deutschland nach Artikel 20 des Grundgesetzes ist das Volk. Alle Staatsgewalt geht vom Volke aus und wird durch Wahlen, Abstimmungen und besondere Organe der Gesetzgebung, der vollziehenden Gewalt und der Rechtsprechung ausgeübt.'''', ''''Das Volk ist die Grundlage der staatlichen Macht in Deutschland.'''', 85, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (86, ''''Warum enthält das Grundgesetz keine Elemente direkter Demokratie?'''', ''''Die Entscheidung gegen die Aufnahme von Elementen direkter Demokratie in das Grundgesetz wurde durch die negativen Erfahrungen in der Zeit der Weimarer Republik beeinflusst. Die Verfassungsgeber haben sich für ein reines Repräsentativsystem entschieden, in dem die Staatsgewalt durch Abgeordnete ausgeübt wird, die durch Wahlen bestimmt werden.'''', ''''Die negativen Erfahrungen in der Weimarer Republik führten zur Entscheidung für ein reines Repräsentativsystem ohne direkte Demokratie.'''', 86, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (87, ''''Was ist ein wichtiger Unterschied zwischen der Weimarer Republik und der Bundesrepublik Deutschland?<br>A) Die Weimarer Republik war eine direkte Demokratie, während die Bundesrepublik Deutschland eine repräsentative Demokratie ist.<br>B) Die Weimarer Republik hatte keine unantastbaren Grundrechte, während die Bundesrepublik Deutschland sie hat.<br>C) Die Weimarer Republik war eine formale Demokratie, während die Bundesrepublik Deutschland eine Wertordnung mit freiheitlicher demokratischer Grundordnung ist.<br>D) Die Weimarer Republik war eine Diktatur, während die Bundesrepublik Deutschland eine Demokratie ist.'''', ''''C) Die Weimarer Republik war eine formale Demokratie, während die Bundesrepublik Deutschland eine Wertordnung mit freiheitlicher demokratischer Grundordnung ist.'''', ''''C) Die Weimarer Republik war formale Demokratie, die Bundesrepublik Deutschland eine Wertordnung mit freiheitlicher demokratischer Grundordnung.'''', 87, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (88, ''''Welcher der folgenden Punkte ist ein Argument gegen die Einführung von Volksabstimmungen in Deutschland?<br>A) Es können lange Fristen für eine umfassende Information der Bevölkerung vorgesehen werden.<br>B) Aktive, gut organisierte Minderheiten können ihre Sonderinteressen durchsetzen.<br>C) Das repräsentative System wird durch direkte Bürgerbeteiligung nicht abgeschafft, sondern ergänzt.<br>D) Volksabstimmungen können das Parlament zwingen, sich mit Themen zu befassen, die die Gesellschaft bewegen.'''', ''''B) Aktive, gut organisierte Minderheiten können ihre Sonderinteressen durchsetzen.'''', ''''B) Aktive Minderheiten können ihre Interessen durchsetzen.'''', 88, 7);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (89, ''''Wie viele Prozent der Deutschen halten die Demokratie für die beste Staatsform?'''', ''''Laut einer Umfrage des Instituts für Demoskopie, Allensbach, im Jahr 2009 halten drei Viertel der Bürgerinnen und Bürger der Bundesrepublik (West) die Demokratie für die beste Staatsform. In den neuen Bundesländern hielten 36 Prozent die Demokratie für die beste Staatsform.'''', ''''75 Prozent in Westdeutschland und 36 Prozent in Ostdeutschland.'''', 89, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (90, ''''Warum ist die Demokratie in Deutschland nicht ungefährdet?'''', ''''Die Demokratie in Deutschland ist nicht ungefährdet, weil sie keine ungebrochene Tradition hat und die Geschichte der Demokratie in Deutschland eine Geschichte gescheiterter Versuche war. Erst der zweite demokratische Staat ist bisher, trotz aller Schwächen und Mängel, eine Erfolgsgeschichte gewesen. Viele Bürger betrachten diese Erfolge als selbstverständlich und haben ein idealisiertes Bild von einer harmonischen, problemlos funktionierenden Demokratie.'''', ''''Die Demokratie in Deutschland ist nicht ungefährdet, weil sie keine Tradition hat und die Geschichte der Demokratie in Deutschland eine Geschichte gescheiterter Versuche war.'''', 90, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (91, ''''Was bedeutet der Begriff ''''''''Demokratie'''''''' wörtlich?'''', ''''Der Begriff ''''''''Demokratie'''''''' bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. Dies bedeutet, dass die Bürger in einer Demokratie das Recht haben, über ihre Regierung mitzubestimmen und ihre Meinung frei zu äußern.'''', ''''''''''''Herrschaft des Volkes'''''''''''', 91, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (92, ''''Wie kann das Volk in einer modernen Demokratie die Herrschaft ausüben?'''', ''''In einer modernen Demokratie kann das Volk die Herrschaft nicht direkt ausüben, wie es in den kleinen Stadtstaaten des antiken Griechenlands der Fall war. Stattdessen übt das Volk die Herrschaft mittelbar und indirekt aus, indem es seine Vertreter (Repräsentanten) wählt, die dann in seinem Namen Entscheidungen treffen.'''', ''''Das Volk übt die Herrschaft mittelbar und indirekt aus, indem es Vertreter wählt.'''', 92, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (93, ''''Was ist die Grundlage der staatlichen Machtausübung in Deutschland gemäß Artikel 20 des Grundgesetzes?'''', ''''Die Grundlage der staatlichen Machtausübung in Deutschland ist die Volkssouveränität. Jede staatliche Machtausübung muss durch das Volk legitimiert sein. Die staatlichen Organe müssen entweder durch Volkswahlen hervorgehen oder von den gewählten Repräsentanten eingesetzt werden.'''', ''''Die Volkssouveränität ist die Grundlage der staatlichen Machtausübung in Deutschland.'''', 93, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (94, ''''Was ist das Mehrheitsprinzip in einer Demokratie?'''', ''''Das Mehrheitsprinzip in einer Demokratie besagt, dass bei Wahlen und Abstimmungen die Mehrheit entscheidet und dass die Minderheit die Mehrheitsentscheidung anerkennt. Dieser Grundsatz gewährleistet, dass Konflikte friedlich ausgetragen werden. Die Minderheit hat die Chance, bei künftigen Wahlen und Abstimmungen ihre Meinung durchzusetzen und kann erwarten, dass dann ihre Entscheidungen respektiert werden.'''', ''''Das Mehrheitsprinzip: Die Mehrheit entscheidet, die Minderheit akzeptiert die Entscheidung.'''', 94, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (95, ''''Was ist der Hauptunterschied zwischen der Weimarer Republik und der Demokratie des Grundgesetzes?'''', ''''Der Hauptunterschied zwischen der Weimarer Republik und der Demokratie des Grundgesetzes liegt darin, dass die Weimarer Republik eine formale Demokratie war, die ihre Feinde zerstören konnte, während die Demokratie des Grundgesetzes eine Wertordnung mit unantastbaren Prinzipien ist, die freiheitliche demokratische Grundordnung schützt und absolute Werte und unverzichtbare Schutzgüter anerkennt.'''', ''''Die Weimarer Republik war eine formale Demokratie, die zerstört werden konnte, während die Demokratie des Grundgesetzes eine Wertordnung mit unantastbaren Prinzipien ist.'''', 95, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (96, ''''Welcher der folgenden Nachteile direkter Demokratie wird in dem Text genannt?<br>A) Die Bevölkerung ist nicht informiert genug.<br>B) Aktive Minderheiten können ihre Sonderinteressen durchsetzen.<br>C) Die Parlamentarier verlieren ihre Macht.<br>D) Die Medien haben zu viel Einfluss auf die Politik.'''', ''''B) Aktive Minderheiten können ihre Sonderinteressen durchsetzen.'''', ''''B) Aktive Minderheiten können ihre Sonderinteressen durchsetzen.'''', 96, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (97, ''''Wie viele Prozent der Deutschen halten die Demokratie in Deutschland für die beste Staatsform?<br>A) Über 50%<br>B) Zwischen 60% und 70%<br>C) Drei Viertel (75%)<br>D) Über 90%'''', ''''C) Drei Viertel (75%) - Seit mehr als 40 Jahren antworten drei Viertel der Bürgerinnen und Bürger der Bundesrepublik (West) auf die Frage, ob die Demokratie die beste Staatsform ist, mit ''''''''Ja''''''''.'''', ''''C) Drei Viertel (75%)'''', 97, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (98, ''''Warum kann die Demokratie in Deutschland nicht auf eine ungebrochene Tradition zurückblicken?'''', ''''Die Demokratie in Deutschland kann nicht auf eine ungebrochene Tradition zurückblicken, weil die Geschichte der Demokratie in Deutschland eine Geschichte gescheiterter Versuche war. Erst der zweite demokratische Staat ist bisher, trotz aller Schwächen und Mängel, eine Erfolgsgeschichte gewesen.'''', ''''Die Demokratie in Deutschland hat keine ungebrochene Tradition, weil es vorher gescheiterte Versuche gab und erst der zweite demokratische Staat erfolgreich war.'''', 98, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (99, ''''Was bedeutet Demokratie wörtlich?'''', ''''Demokratie bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. Es beschreibt ein politisches System, in dem die Bürger die Möglichkeit haben, über ihre Regierung mitzubestimmen und ihre Meinung frei zu äußern.'''', ''''Herrschaft des Volkes'''', 99, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (100, ''''Welche Aussage beschreibt die Form der Demokratie in modernen Großstaaten korrekt?<br>A) Das Volk stimmt direkt über Gesetze ab.<br>B) Das Volk überträgt die Herrschaft auf gewählte Vertreter.<br>C) Nur freie Männer dürfen abstimmen.<br>D) Demokratie ist in modernen Großstaaten nicht praktikabel.'''', ''''B) Das Volk überträgt die Herrschaft auf gewählte Vertreter. In modernen Großstaaten ist direkte Demokratie, wie sie in den antiken griechischen Stadtstaaten praktiziert wurde, nicht mehr praktikabel. Stattdessen übt das Volk die Herrschaft mittelbar und indirekt aus, indem es Vertreter wählt, die in ihrem Namen Entscheidungen treffen.'''', ''''B) Das Volk überträgt die Herrschaft auf gewählte Vertreter.'''', 100, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (101, ''''Was bedeutet Volkssouveränität im Kontext des Grundgesetzes?'''', ''''Volkssouveränität im Kontext des Grundgesetzes bedeutet, dass alle Staatsgewalt vom Volke ausgeht. Dies wird durch Wahlen und Abstimmungen sowie durch besondere Organe der Gesetzgebung, der vollziehenden Gewalt und der Rechtsprechung ausgeübt. Jede staatliche Machtausübung muss durch das Volk legitimiert sein, und die staatlichen Organe müssen entweder direkt aus Volkswahlen hervorgehen oder von gewählten Repräsentanten eingesetzt werden. Die Amtsinhaber sind dem Volk bzw. seinen Repräsentanten verantwortlich und können aus ihrem Amt entfernt werden.'''', ''''Volkssouveränität bedeutet, dass alle Staatsgewalt vom Volke ausgeht und durch Wahlen, Abstimmungen und staatliche Organe ausgeübt wird, die vom Volk legitimiert sind.'''', 101, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (102, ''''Warum wurde in das Grundgesetz keine direkte Demokratie aufgenommen?<br>A) Wegen der positiven Erfahrungen in der Weimarer Republik.<br>B) Wegen der negativen Erfahrungen in der Weimarer Republik.<br>C) Weil die Bevölkerung das nicht wollte.<br>D) Weil es keine Mehrheit im Bundestag gab.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', 102, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (103, ''''Welche der folgenden Aussagen beschreibt einen Unterschied zwischen der Weimarer Republik und der Demokratie des Grundgesetzes?<br>A) Beide erlauben die Änderung der Grundrechte mit einfacher Mehrheit.<br>B) Die Weimarer Republik hatte unantastbare Prinzipien, die Demokratie des Grundgesetzes nicht.<br>C) Die Demokratie des Grundgesetzes ist eine Wertordnung mit unantastbaren Prinzipien.<br>D) Beide Systeme haben keine Schutzmechanismen gegen Angriffe auf die Demokratie.'''', ''''C) Die Demokratie des Grundgesetzes ist eine Wertordnung mit unantastbaren Prinzipien.'''', ''''C) Die Demokratie des Grundgesetzes ist eine Wertordnung mit unantastbaren Prinzipien.'''', 103, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (104, ''''Welches der folgenden Argumente ist ein Nachteil der direkten Demokratie?<br>A) Sie ergänzt das repräsentative System.<br>B) Sie kann zu Manipulationen führen.<br>C) Sie ermöglicht umfassende Information der Bevölkerung.<br>D) Sie zwingt das Parlament, sich mit wichtigen Themen zu befassen.'''', ''''B) Sie kann zu Manipulationen führen. Ein Nachteil der direkten Demokratie ist, dass sie Manipulationen Tür und Tor öffnen kann. Macht würde denen zufallen, die die dem Volk vorzulegenden Fragen formulieren und Zugang zu den Medien haben. Direkte Demokratie ist eine ''''''''Prämie für jeden Demagogen'''''''' (Theodor Heuss).'''', ''''B) Sie kann zu Manipulationen führen.'''', 104, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (105, ''''Welcher Prozentsatz der Bürger in den neuen Bundesländern hielt 2009 die Demokratie für die beste Staatsform?<br>A) 24 Prozent<br>B) 36 Prozent<br>C) 62 Prozent<br>D) 78 Prozent'''', ''''B) 36 Prozent der Bürger in den neuen Bundesländern hielten 2009 die Demokratie für die beste Staatsform. Diese Zahl zeigt, dass in den neuen Bundesländern weniger Menschen von der Demokratie überzeugt sind als in den alten Bundesländern, wo drei Viertel der Bürger die Demokratie als die beste Staatsform betrachten. Die Umfrageergebnisse verdeutlichen auch, dass die Zufriedenheit mit der Demokratie stark von der wirtschaftlichen Lage der Befragten abhängt.'''', ''''B) 36 Prozent'''', 105, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (106, ''''Warum wird die Demokratie in Deutschland als nicht ungefährdet angesehen?'''', ''''Die Demokratie in Deutschland wird als nicht ungefährdet angesehen, weil sie nicht auf eine ungebrochene Tradition zurückblicken kann und ihre Geschichte von gescheiterten Versuchen geprägt ist. Erst der zweite demokratische Staat hat sich als Erfolgsgeschichte erwiesen, trotz aller Schwächen und Mängel. Viele Bürger betrachten die Erfolge der Demokratie als selbstverständlich und halten Distanz zur Politik, da sie glauben, dass die Politiker ''''''''doch machen, was sie wollen''''''''. Zudem haben manche ein idealisiertes Bild von einer harmonischen, konfliktfreien Demokratie, was in der Realität nicht existiert.'''', ''''Die Demokratie in Deutschland gilt als nicht ungefährdet, da sie eine Geschichte gescheiterter Versuche hat und Bürger skeptisch gegenüber Politikern sind, während sie eine unrealistische, konfliktfreie Demokratie erwarten.'''', 106, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (107, ''''Was bedeutet der Begriff ''''''''Demokratie''''''''?'''', ''''Dies bedeutet, dass die Bürger eines Landes die Möglichkeit haben, über ihre Regierung mitzubestimmen und ihre Meinung frei zu äußern. '''', ''''Herrschaft des Volkes'''', 107, 4);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (108, ''''Wie übt das Volk in einer modernen Demokratie die Herrschaft aus?'''', ''''In einer modernen Demokratie übt das Volk die Herrschaft mittelbar und indirekt aus, indem es seine Vertreter (Repräsentanten) wählt, die dann in seinem Namen Entscheidungen treffen. Dies ist im Gegensatz zu den kleinen Stadtstaaten des antiken Griechenlands, in denen das Volk direkt auf dem Marktplatz zusammenkam und über die Gesetze abstimmte.'''', ''''Das Volk übt die Herrschaft in einer modernen Demokratie mittelbar und indirekt aus, indem es Vertreter wählt.'''', 108, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (109, ''''Was bedeutet die Volkssouveränität in Deutschland?'''', ''''Die Volkssouveränität bedeutet, dass jede staatliche Machtausübung durch das Volk legitimiert sein muss. Die staatlichen Organe müssen entweder direkt durch Volkswahlen hervorgehen oder von den gewählten Repräsentanten eingesetzt werden. Die Amtsinhaber sind dem Volk bzw. seinen Repräsentanten verantwortlich und können aus ihrem Amt entfernt werden.'''', ''''Die Volkssouveränität bedeutet, dass die staatliche Macht vom Volk ausgeht und dass die Amtsinhaber dem Volk verantwortlich sind.'''', 109, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (110, ''''Was ist das Hauptmerkmal des politischen Systems in Deutschland?'''', ''''Das Hauptmerkmal des politischen Systems in Deutschland ist das reine Repräsentativsystem, bei dem das Volk die Staatsgewalt nicht direkt ausübt, sondern durch Wahlen Repräsentanten, die Abgeordneten, wählt, die dann im Auftrag des Volkes die Entscheidungen im Staat treffen.'''', ''''Das reine Repräsentativsystem, bei dem das Volk Repräsentanten wählt, die Entscheidungen treffen.'''', 110, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (111, ''''Was sind die Hauptgründe, warum die Demokratie des Grundgesetzes stabiler ist als die Weimarer Republik?'''', ''''Die Hauptgründe, warum die Demokratie des Grundgesetzes stabiler ist als die Weimarer Republik, sind die unantastbaren Prinzipien der freiheitlichen demokratischen Grundordnung, die im Grundgesetz verankert sind. Im Gegensatz zur Weimarer Republik, die ihre Feinde die Demokratie zerstören ließ, hat das Grundgesetz absolute Werte und unverzichtbare Schutzgüter, die entschlossen gegen alle Angriffe verteidigt werden sollen. Dies schützt die Demokratie vor Missbrauch und Zerstörung.'''', ''''Die Demokratie des Grundgesetzes ist stabiler, weil sie auf unantastbaren Prinzipien der freiheitlichen demokratischen Grundordnung basiert, die absolute Werte und Schutzgüter schützen.'''', 111, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (112, ''''Was sind die Hauptargumente gegen die Einführung von Elementen direkter Demokratie auf Bundesebene in Deutschland?'''', ''''Die Hauptargumente gegen die Einführung von Elementen direkter Demokratie auf Bundesebene in Deutschland sind, dass die Parlamentarische Demokratie aufgrund der Erfahrungen mit Volksentscheiden in der Weimarer Republik als geeignetes System etabliert wurde, dass direkte Demokratie auf kommunaler und Landesebene wegen der Überschaubarkeit der zu entscheidenden Fragen und der geringen Zahl der Abstimmungsberechtigten praktikabel ist, aber für die komplexen Probleme der Bundespolitik nicht geeignet, dass Volksbefragungen komplizierte politische Probleme auf eine einfache Ja-oder-Nein-Alternative reduzieren müssen, dass aktive, gut organisierte Minderheiten ihre Sonderinteressen durchsetzen können, und dass die Manipulation Tür und Tor geöffnet würde.'''', ''''Die Hauptargumente gegen direkte Demokratie auf Bundesebene sind, dass sie für komplexe Probleme nicht geeignet ist, zu Vereinfachung komplexer Probleme führt, Minderheiten bevorzugt und Manipulation ermöglicht.'''', 112, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (113, ''''Welche der folgenden Aussagen gegen die direkte Demokratie ist korrekt?<br>A) Direkte Demokratie führt zu einer stärkeren Rolle des Parlaments.<br>B) Direkte Demokratie kann zu Manipulation und Demagogie führen.<br>C) Direkte Demokratie ist die einzige Form der Demokratie, die wirklich funktioniert.<br>D) Direkte Demokratie führt zu einer Schwächung der Medien.'''', ''''B) Direkte Demokratie kann zu Manipulation und Demagogie führen.'''', ''''B) Direkte Demokratie kann zu Manipulation und Demagogie führen.'''', 113, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (114, ''''Not suitable'''', ''''Not suitable'''', ''''Not suitable'''', 114, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (115, ''''Wie viele Prozent der Deutschen halten die Demokratie für die beste Staatsform?'''', ''''Laut einer Umfrage des Instituts für Demoskopie, Allensbach, aus dem Jahr 2009 halten drei Viertel der Bürgerinnen und Bürger der Bundesrepublik (West) die Demokratie für die beste Staatsform. In den neuen Bundesländern hielten 36 Prozent die Demokratie für die beste Staatsform.'''', ''''Drei Viertel der Deutschen im Westen und 36 Prozent im Osten halten die Demokratie für die beste Staatsform.'''', 115, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (116, ''''Warum ist die Demokratie in Deutschland nicht auf eine ungebrochene Tradition zurückblicken?'''', ''''Die Demokratie in Deutschland kann nicht auf eine ungebrochene Tradition zurückblicken, weil die Geschichte der Demokratie in Deutschland eine Geschichte gescheiterter Versuche war. Erst der zweite demokratische Staat ist bisher, trotz aller Schwächen und Mängel, eine Erfolgsgeschichte gewesen. Im Gegensatz zu den alten Demokratien des Westens, die auf eine lange Tradition zurückblicken können, hat Deutschland eine wechselvolle Geschichte der Demokratie erlebt.'''', ''''Die Demokratie in Deutschland hat keine ungebrochene Tradition, weil die Geschichte der Demokratie in Deutschland von gescheiterten Versuchen geprägt ist.'''', 116, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (117, ''''Not suitable'''', ''''Not suitable'''', ''''Not suitable'''', 117, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (118, ''''Was bedeutet Demokratie wörtlich und welche zwei wichtigen Rechte sind in Deutschland relativ junge Errungenschaften?'''', ''''Demokratie bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. In Deutschland sind die Mitbestimmung über die eigene Regierung und die freie Meinungsäußerung relativ junge Errungenschaften.'''', ''''Demokratie bedeutet ''''''''Herrschaft des Volkes''''''''. Mitbestimmung und freie Meinungsäußerung sind in Deutschland relativ junge Errungenschaften.'''', 118, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (119, ''''Warum ist die direkte Demokratie in modernen Großstaaten nicht praktikabel?'''', ''''Die direkte Demokratie ist in modernen Großstaaten nicht praktikabel, weil die Bevölkerung zu groß und zu verstreut ist, um sich regelmäßig an einem Ort zu versammeln und über Gesetze abzustimmen, wie es in den kleinen Stadtstaaten des antiken Griechenlands der Fall war. Stattdessen übt das Volk in modernen Massendemokratien die Herrschaft mittelbar und indirekt aus, indem es Vertreter (Repräsentanten) wählt, die in ihrem Namen Entscheidungen treffen.'''', ''''Die direkte Demokratie ist in modernen Großstaaten nicht praktikabel, weil die Bevölkerung zu groß und verstreut ist. Stattdessen wählt das Volk Vertreter, die in ihrem Namen Entscheidungen treffen.'''', 119, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (120, ''''Was bedeutet Volkssouveränität gemäß Artikel 20 des Grundgesetzes?'''', ''''Volkssouveränität gemäß Artikel 20 des Grundgesetzes bedeutet, dass alle Staatsgewalt vom Volke ausgeht. Dies wird durch Wahlen und Abstimmungen sowie durch besondere Organe der Gesetzgebung, der vollziehenden Gewalt und der Rechtsprechung ausgeübt. Jede staatliche Machtausübung muss durch das Volk legitimiert sein, und die staatlichen Organe müssen entweder direkt aus Volkswahlen hervorgehen oder von gewählten Repräsentanten eingesetzt werden. Die Amtsinhaber sind dem Volk bzw. seinen Repräsentanten verantwortlich und können aus ihrem Amt entfernt werden.'''', ''''Volkssouveränität bedeutet, dass alle Staatsgewalt vom Volke ausgeht und durch Wahlen und Abstimmungen sowie durch staatliche Organe ausgeübt wird.'''', 120, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (121, ''''Was bedeutet Demokratie wörtlich und welche Rechte sind damit in Deutschland verbunden?'''', ''''Demokratie bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. In Deutschland sind damit die Rechte verbunden, über die eigene Regierung mitzubestimmen und die eigene Meinung frei zu äußern. Diese Rechte sind relativ junge Errungenschaften in der deutschen Geschichte.'''', ''''Demokratie bedeutet ''''''''Herrschaft des Volkes'''''''' und umfasst das Recht, die Regierung mitzubestimmen und die eigene Meinung frei zu äußern.'''', 121, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (122, ''''Welche Aussage beschreibt die Art und Weise, wie Demokratie in modernen Großstaaten ausgeübt wird?<br>A) Das Volk stimmt direkt über Gesetze ab.<br>B) Das Volk überträgt die Herrschaft auf Vertreter.<br>C) Nur freie Männer dürfen abstimmen.<br>D) Demokratie wird nur in kleinen Stadtstaaten praktiziert.'''', ''''B) Das Volk überträgt die Herrschaft auf Vertreter.'''', ''''B) Das Volk überträgt die Herrschaft auf Vertreter.'''', 122, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (123, ''''Welches Prinzip wird im Artikel 20 des Grundgesetzes beschrieben?<br>A) Föderalismus<br>B) Volkssouveränität<br>C) Gewaltenteilung<br>D) Rechtsstaatlichkeit'''', ''''B) Volkssouveränität. Artikel 20 des Grundgesetzes beschreibt, dass alle Staatsgewalt vom Volke ausgeht. Dies bedeutet, dass jede staatliche Machtausübung durch das Volk legitimiert sein muss. Die staatlichen Organe müssen entweder direkt aus Volkswahlen hervorgehen oder von gewählten Repräsentanten eingesetzt werden.'''', ''''B) Volkssouveränität'''', 123, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (124, ''''Warum haben die Verfassungsgeber in Deutschland auf Elemente direkter Demokratie im Grundgesetz verzichtet?<br>A) Wegen der positiven Erfahrungen in der Weimarer Republik.<br>B) Wegen der negativen Erfahrungen in der Weimarer Republik.<br>C) Weil die Bürgerbewegungen in der ehemaligen DDR keinen Einfluss hatten.<br>D) Weil das Mehrheitsprinzip keine Kompromisslösung ist.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', 124, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (125, ''''Welche der folgenden Aussagen beschreibt die Demokratie des Grundgesetzes korrekt?<br>A) Sie erlaubt es, die Grundrechte mit einer Zweidrittelmehrheit außer Kraft zu setzen.<br>B) Sie ist eine formale Demokratie ohne unantastbare Prinzipien.<br>C) Sie ist eine Wertordnung mit unantastbaren Prinzipien, die gegen Angriffe verteidigt werden.<br>D) Sie ist identisch mit der Demokratie der Weimarer Republik.'''', ''''C) Sie ist eine Wertordnung mit unantastbaren Prinzipien, die gegen Angriffe verteidigt werden.'''', ''''C) Sie ist eine Wertordnung mit unantastbaren Prinzipien, die gegen Angriffe verteidigt werden.'''', 125, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (126, ''''Welche der folgenden Aussagen ist ein Argument gegen direkte Demokratie?<br>A) Die Mindestbeteiligung kann hoch angesetzt werden, um Minderheitsinteressen zu verhindern.<br>B) Direkte Demokratie kann zu einer Missachtung von Interessen nicht durchsetzungsfähiger Mehrheiten führen.<br>C) Das repräsentative System wird durch direkte Bürgerbeteiligung ergänzt.<br>D) Lange Fristen für umfassende Information der Bevölkerung können Manipulationen erschweren.'''', ''''B) Direkte Demokratie kann zu einer Missachtung von Interessen nicht durchsetzungsfähiger Mehrheiten führen.'''', ''''B) Direkte Demokratie kann zu einer Missachtung von Interessen nicht durchsetzungsfähiger Mehrheiten führen.'''', 126, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (127, ''''Welche der folgenden Aussagen ist ein Argument gegen die direkte Demokratie?<br>A) Direkte Demokratie fördert die Beteiligung der Bürger.<br>B) Direkte Demokratie kann Manipulation und Demagogie begünstigen.<br>C) Direkte Demokratie führt zu schnelleren Entscheidungen.<br>D) Direkte Demokratie stärkt das Vertrauen in das Parlament.'''', ''''B) Direkte Demokratie kann Manipulation und Demagogie begünstigen. Dies liegt daran, dass diejenigen, die die Fragen formulieren und Zugang zu den Medien haben, Macht erlangen könnten. Außerdem könnte ein per Volksabstimmung beschlossenes Gesetz den Anschein größerer Legitimität gewinnen und schwerer zu korrigieren sein als parlamentarische Entscheidungen.'''', ''''B) Direkte Demokratie kann Manipulation und Demagogie begünstigen.'''', 127, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (128, ''''Welche Gefährdungen der Demokratie werden im Text beschrieben?'''', ''''Im Text werden mehrere Gefährdungen der Demokratie beschrieben. Erstens wird darauf hingewiesen, dass die Demokratie in Deutschland keine ungebrochene Tradition hat und die Geschichte der Demokratie eine Geschichte gescheiterter Versuche war. Zweitens wird die Distanz und Skepsis der Bürger gegenüber der Politik als Gefährdung genannt, da viele Bürger meinen, ''''''''die da oben machen doch, was sie wollen''''''''. Drittens wird die idealisierte Vorstellung von einer harmonischen, konfliktfreien Demokratie als problematisch angesehen, da sie zu Enttäuschungen führt, wenn die Realität nicht diesem Ideal entspricht.'''', ''''Die Gefährdungen der Demokratie sind die fehlende ungebrochene Tradition, die Distanz und Skepsis der Bürger gegenüber der Politik und die unrealistische Erwartung einer konfliktfreien Demokratie.'''', 128, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (129, ''''Welche Aussage beschreibt die Zufriedenheit mit der Demokratie in Deutschland korrekt?<br>A) In Westdeutschland sind 69 Prozent der Menschen mit der Demokratie zufrieden.<br>B) In Ostdeutschland sind 69 Prozent der Menschen mit der Demokratie zufrieden.<br>C) In Westdeutschland sind 39 Prozent der Menschen mit der Demokratie zufrieden.<br>D) In Ostdeutschland sind 78 Prozent der Menschen mit der Demokratie zufrieden.'''', ''''A) In Westdeutschland sind 69 Prozent der Menschen mit der Demokratie zufrieden.'''', ''''A) In Westdeutschland sind 69 Prozent der Menschen mit der Demokratie zufrieden.'''', 129, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (130, ''''Warum wird die Demokratie in Deutschland als nicht ungefährdet angesehen?'''', ''''Die Demokratie in Deutschland wird als nicht ungefährdet angesehen, weil sie nicht auf eine ungebrochene Tradition zurückblicken kann und ihre Geschichte von gescheiterten Versuchen geprägt ist. Erst der zweite demokratische Staat hat sich als Erfolgsgeschichte erwiesen. Viele Bürger betrachten die Erfolge der Demokratie als selbstverständlich und halten Distanz zur Politik, da sie glauben, dass die politischen Entscheidungsträger ''''''''machen, was sie wollen''''''''. Diese Distanz und die idealisierte Vorstellung einer harmonischen, konfliktfreien Demokratie führen zu Enttäuschungen, wenn die Realität diesen Idealen nicht entspricht.'''', ''''Die Demokratie in Deutschland gilt als nicht ungefährdet, da sie keine ungebrochene Tradition hat und viele Bürger skeptisch gegenüber der Politik sind.'''', 130, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (131, ''''Warum wird die Demokratie in Deutschland als nicht ungefährdet angesehen?'''', ''''Die Demokratie in Deutschland wird als nicht ungefährdet angesehen, weil sie im Gegensatz zu den alten Demokratien des Westens keine ungebrochene Tradition hat. Die Geschichte der Demokratie in Deutschland ist geprägt von gescheiterten Versuchen und erst der zweite demokratische Staat, die Bundesrepublik Deutschland, hat sich als Erfolgsgeschichte erwiesen. Zudem gibt es eine gewisse Distanz und Skepsis der Bürger gegenüber der Politik, da viele glauben, dass die Politiker ''''''''doch machen, was sie wollen''''''''. Diese kritische Haltung und die idealisierte Vorstellung von einer perfekt funktionierenden Demokratie tragen zur Wahrnehmung bei, dass die Demokratie in Deutschland gefährdet ist.'''', ''''Die Demokratie in Deutschland gilt als nicht ungefährdet, da sie keine ungebrochene Tradition hat und von gescheiterten Versuchen geprägt ist. Zudem gibt es Skepsis der Bürger gegenüber der Politik und unrealistische Erwartungen an eine perfekte Demokratie.'''', 131, 3);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (132, ''''Was bedeutet der Begriff ''''''''Demokratie'''''''' wörtlich?<br>A) Herrschaft des Königs<br>B) Herrschaft des Volkes<br>C) Herrschaft der Regierung<br>D) Herrschaft der Elite'''', ''''B) Herrschaft des Volkes'''', ''''B) Herrschaft des Volkes'''', 132, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (133, ''''Welche Aussage beschreibt die Art und Weise, wie Demokratie in modernen Großstaaten ausgeübt wird?<br>A) Das Volk stimmt direkt auf dem Marktplatz über Gesetze ab.<br>B) Das Volk überträgt die Herrschaft auf gewählte Vertreter.<br>C) Nur freie Männer dürfen über Gesetze abstimmen.<br>D) Demokratie wird nur in kleinen Stadtstaaten praktiziert.'''', ''''B) Das Volk überträgt die Herrschaft auf gewählte Vertreter. In modernen Großstaaten ist die direkte Demokratie, wie sie in den antiken griechischen Stadtstaaten praktiziert wurde, nicht mehr praktikabel. Stattdessen übt das Volk die Herrschaft mittelbar und indirekt aus, indem es Vertreter wählt, die in ihrem Namen Entscheidungen treffen.'''', ''''B) Das Volk überträgt die Herrschaft auf gewählte Vertreter.'''', 133, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (134, ''''Welches Prinzip wird durch Artikel 20 des Grundgesetzes beschrieben?<br>A) Monarchie<br>B) Diktatur<br>C) Volkssouveränität<br>D) Anarchie'''', ''''C) Volkssouveränität. Artikel 20 des Grundgesetzes beschreibt, dass alle Staatsgewalt vom Volke ausgeht und durch das Volk in Wahlen und Abstimmungen sowie durch besondere Organe der Gesetzgebung, der vollziehenden Gewalt und der Rechtsprechung ausgeübt wird. Dies bedeutet, dass jede staatliche Machtausübung durch das Volk legitimiert sein muss.'''', ''''C) Volkssouveränität'''', 134, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (135, ''''Warum haben die Verfassungsgeber in Deutschland auf Elemente direkter Demokratie im Grundgesetz verzichtet?<br>A) Wegen der positiven Erfahrungen in der Weimarer Republik.<br>B) Wegen der negativen Erfahrungen in der Weimarer Republik.<br>C) Weil die Bevölkerung keine direkte Demokratie wollte.<br>D) Weil es keine erfolgreichen Bürgerbewegungen gab.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', 135, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (136, ''''Welche der folgenden Aussagen beschreibt einen Unterschied zwischen der Weimarer Republik und der Demokratie des Grundgesetzes?<br>A) In der Weimarer Republik konnten Grundrechte nicht außer Kraft gesetzt werden.<br>B) Die Demokratie des Grundgesetzes ist eine formale Demokratie ohne unantastbare Prinzipien.<br>C) Das Grundgesetz schützt die freiheitliche demokratische Grundordnung als absolute Werte.<br>D) Das Bundesverfassungsgericht hat keine Rolle in der Verteidigung der Demokratie des Grundgesetzes.'''', ''''C) Das Grundgesetz schützt die freiheitliche demokratische Grundordnung als absolute Werte.'''', ''''C) Das Grundgesetz schützt die freiheitliche demokratische Grundordnung als absolute Werte.'''', 136, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (137, ''''Welche der folgenden Aussagen ist ein Argument gegen direkte Demokratie?<br>A) Die Mindestbeteiligung kann hoch angesetzt werden, um Minderheitsinteressen zu verhindern.<br>B) Lange Fristen für umfassende Information der Bevölkerung können Manipulationen erschweren.<br>C) Direkte Demokratie ist eine ''''''''Prämie für jeden Demagogen''''''''.<br>D) Das repräsentative System wird durch direkte Bürgerbeteiligung ergänzt.'''', ''''C) Direkte Demokratie ist eine ''''''''Prämie für jeden Demagogen''''''''.'''', ''''C) Direkte Demokratie ist eine ''''''''Prämie für jeden Demagogen''''''''.'''', 137, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (138, ''''Was bedeutet der Begriff ''''''''Demokratie'''''''' wörtlich und welche Errungenschaften sind damit in Deutschland verbunden?'''', ''''Der Begriff ''''''''Demokratie'''''''' bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. In Deutschland sind damit die Errungenschaften verbunden, dass die Bürger über ihre eigene Regierung mitbestimmen können und die Freiheit haben, ihre eigene Meinung zu äußern. Diese Errungenschaften sind in Deutschland relativ jung.'''', ''''Demokratie bedeutet ''''''''Herrschaft des Volkes'''''''' und umfasst in Deutschland die Mitbestimmung über die Regierung und die Meinungsfreiheit.'''', 138, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (139, ''''Welche Aussage beschreibt die Ausübung der Demokratie in modernen Großstaaten korrekt?<br>A) Das Volk stimmt direkt über alle Gesetze ab.<br>B) Das Volk überträgt die Herrschaft auf gewählte Vertreter.<br>C) Nur freie Männer dürfen über Gesetze abstimmen.<br>D) Demokratie wird nur in westlichen Ländern praktiziert.'''', ''''B) Das Volk überträgt die Herrschaft auf gewählte Vertreter.'''', ''''B) Das Volk überträgt die Herrschaft auf gewählte Vertreter.'''', 139, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (140, ''''Welches Prinzip wird im Artikel 20 des Grundgesetzes beschrieben?<br>A) Föderalismus<br>B) Volkssouveränität<br>C) Gewaltenteilung<br>D) Rechtsstaatlichkeit'''', ''''B) Volkssouveränität. Artikel 20 des Grundgesetzes beschreibt, dass alle Staatsgewalt vom Volke ausgeht und durch Wahlen, Abstimmungen und besondere Organe der Gesetzgebung, der vollziehenden Gewalt und der Rechtsprechung ausgeübt wird. Dies bedeutet, dass jede staatliche Machtausübung durch das Volk legitimiert sein muss.'''', ''''B) Volkssouveränität'''', 140, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (141, ''''Warum haben die Verfassungsgeber in Deutschland auf Elemente direkter Demokratie im Grundgesetz verzichtet?<br>A) Wegen der positiven Erfahrungen in der Weimarer Republik.<br>B) Wegen der negativen Erfahrungen in der Weimarer Republik.<br>C) Weil die Bevölkerung keine direkte Demokratie wollte.<br>D) Weil es keine Forderungen nach direkter Demokratie gab.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', 141, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (142, ''''Was bedeutet der Begriff ''''''''Demokratie'''''''' und welche Errungenschaften sind damit in Deutschland verbunden?'''', ''''Der Begriff ''''''''Demokratie'''''''' bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. In einer Demokratie haben die Bürger das Recht, über ihre eigene Regierung mitzubestimmen und ihre Meinung frei zu äußern. In Deutschland sind diese Rechte relativ junge Errungenschaften, die erst im Laufe der letzten Jahrzehnte vollständig etabliert wurden.'''', ''''Demokratie bedeutet ''''''''Herrschaft des Volkes''''''''. In Deutschland können Bürger über die Regierung mitbestimmen und ihre Meinung frei äußern.'''', 142, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (143, ''''Welche der folgenden Aussagen beschreibt die moderne Demokratie korrekt?<br>A) Das Volk stimmt direkt über alle Gesetze ab.<br>B) Das Volk übt die Herrschaft durch gewählte Vertreter aus.<br>C) Nur freie Männer dürfen über Gesetze abstimmen.<br>D) Demokratie bedeutet, dass das Volk keine Vertreter braucht.'''', ''''B) Das Volk übt die Herrschaft durch gewählte Vertreter aus.'''', ''''B) Das Volk übt die Herrschaft durch gewählte Vertreter aus.'''', 143, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (144, ''''Was bedeutet Volkssouveränität gemäß Artikel 20 des Grundgesetzes?'''', ''''Volkssouveränität gemäß Artikel 20 des Grundgesetzes bedeutet, dass alle Staatsgewalt vom Volke ausgeht. Dies bedeutet, dass jede staatliche Machtausübung durch das Volk legitimiert sein muss. Die staatlichen Organe, wie Parlamente, müssen aus Volkswahlen hervorgehen oder, wie die Regierung und die Verwaltung, von den gewählten Repräsentanten eingesetzt werden. Die Amtsinhaber sind dem Volk bzw. seinen Repräsentanten verantwortlich und können aus ihrem Amt entfernt werden.'''', ''''Volkssouveränität bedeutet, dass alle Staatsgewalt vom Volke ausgeht und staatliche Organe durch das Volk oder gewählte Repräsentanten legitimiert sind.'''', 144, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (145, ''''Warum wurde in Deutschland ein reines Repräsentativsystem gewählt?<br>A) Wegen der positiven Erfahrungen in der Weimarer Republik.<br>B) Wegen der negativen Erfahrungen in der Weimarer Republik.<br>C) Weil die Bevölkerung direkte Demokratie bevorzugt.<br>D) Weil es keine Forderungen nach direkter Demokratie gab.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', ''''B) Wegen der negativen Erfahrungen in der Weimarer Republik.'''', 145, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (146, ''''Welche der folgenden Aussagen beschreibt einen Unterschied zwischen der Weimarer Republik und der Demokratie des Grundgesetzes?<br>A) In der Weimarer Republik konnten Grundrechte nicht außer Kraft gesetzt werden.<br>B) Die Demokratie des Grundgesetzes ist eine formale Demokratie ohne unantastbare Prinzipien.<br>C) Das Grundgesetz schützt die freiheitliche demokratische Grundordnung entschlossen gegen alle Angriffe.<br>D) Das Bundesverfassungsgericht hat keine Rolle im Schutz der Demokratie des Grundgesetzes.'''', ''''C) Das Grundgesetz schützt die freiheitliche demokratische Grundordnung entschlossen gegen alle Angriffe. Anders als die Weimarer Republik, die es zuließ, dass ihre Feinde die Demokratie zerstörten, ist die Demokratie des Grundgesetzes nicht nur eine formale Demokratie, sondern eine Wertordnung mit unantastbaren Prinzipien. Das Bundesverfassungsgericht hat im KPD-Urteil von 1956 klargestellt, dass das Grundgesetz Grundprinzipien der Staatsgestaltung als absolute Werte anerkennt und diese entschlossen gegen alle Angriffe verteidigt.'''', ''''C) Das Grundgesetz schützt die freiheitliche demokratische Grundordnung entschlossen gegen alle Angriffe.'''', 146, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (147, ''''Welche der folgenden Aussagen ist ein Argument gegen direkte Demokratie?<br>A) Die Mindestbeteiligung kann hoch angesetzt werden, um die Durchsetzung von Minderheitsinteressen zu verhindern.<br>B) Das repräsentative System wird durch direkte Bürgerbeteiligung ergänzt.<br>C) Für Volksbefragungen müssen komplizierte politische Probleme auf eine einfache Ja-oder-Nein-Alternative reduziert werden.<br>D) Es können lange Fristen für eine umfassende Information der Bevölkerung vorgesehen werden.'''', ''''C) Für Volksbefragungen müssen komplizierte politische Probleme auf eine einfache Ja-oder-Nein-Alternative reduziert werden.'''', ''''C) Für Volksbefragungen müssen komplizierte politische Probleme auf eine einfache Ja-oder-Nein-Alternative reduziert werden.'''', 147, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (148, ''''Welche der folgenden Aussagen beschreibt ein Risiko der direkten Demokratie?<br>A) Direkte Demokratie fördert die Macht von Demagogen.<br>B) Per Volksabstimmung beschlossene Gesetze sind leichter zu korrigieren.<br>C) Das Parlament entscheidet über alle wichtigen Fragen.<br>D) Direkte Demokratie verhindert Manipulation.'''', ''''A) Direkte Demokratie fördert die Macht von Demagogen.'''', ''''A) Direkte Demokratie fördert die Macht von Demagogen.'''', 148, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (149, ''''Welche Gefährdungen der Demokratie werden im Text beschrieben?'''', ''''Im Text werden mehrere Gefährdungen der Demokratie beschrieben. Erstens wird darauf hingewiesen, dass die Demokratie in Deutschland keine ungebrochene Tradition hat und die Geschichte der Demokratie eine Geschichte gescheiterter Versuche war. Zweitens wird die Distanz und Skepsis der Bürger gegenüber den politischen Entscheidungsträgern als Gefährdung genannt. Viele Bürger haben das Gefühl, dass ''''''''die da oben machen doch, was sie wollen'''''''', und halten sich daher von politischer Beteiligung fern. Drittens wird die idealisierte Vorstellung von einer harmonischen und konfliktfreien Demokratie als problematisch angesehen, da sie zu Enttäuschungen führt, wenn die Realität diesen Erwartungen nicht entspricht.'''', ''''Die Gefährdungen der Demokratie sind die fehlende ungebrochene Tradition, die Distanz und Skepsis der Bürger gegenüber Politikern und die unrealistischen Erwartungen an eine konfliktfreie Demokratie.'''', 149, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (150, ''''Welche Aussage über die Zufriedenheit mit der Demokratie in Deutschland ist korrekt?<br>A) In Westdeutschland waren 39 Prozent mit der Demokratie zufrieden.<br>B) In Ostdeutschland hielten 62 Prozent die Demokratie für die beste Staatsform.<br>C) 78 Prozent der Deutschen halten unsere Gesellschaftsordnung für verteidigungswert.<br>D) 54 Prozent der Deutschen glauben, dass die Demokratie schlecht funktioniert.'''', ''''C) 78 Prozent der Deutschen halten unsere Gesellschaftsordnung für verteidigungswert.'''', ''''C) 78 Prozent der Deutschen halten unsere Gesellschaftsordnung für verteidigungswert.'''', 150, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (151, ''''Warum wird die Demokratie in Deutschland als gefährdet angesehen?'''', ''''Die Demokratie in Deutschland wird als gefährdet angesehen, weil sie nicht auf eine ungebrochene Tradition zurückblicken kann und ihre Geschichte von gescheiterten Versuchen geprägt ist. Erst der zweite demokratische Staat hat sich als Erfolgsgeschichte erwiesen, trotz aller Schwächen und Mängel. Viele Bürger betrachten die Erfolge der Demokratie als selbstverständlich und halten Distanz zur Politik, da sie glauben, dass die Politiker ''''''''machen, was sie wollen''''''''. Zudem haben manche Bürger ein idealisiertes Bild von einer harmonischen, konfliktfreien Demokratie, das in der Realität nicht existiert. Diese Diskrepanz zwischen Ideal und Realität führt zu Enttäuschung und Skepsis.'''', ''''Die Demokratie in Deutschland gilt als gefährdet, weil sie keine ungebrochene Tradition hat, Bürger skeptisch sind und ein unrealistisches Idealbild von einer konfliktfreien Demokratie haben.'''', 151, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (152, ''''Warum wird die Demokratie in Deutschland als nicht ungefährdet angesehen?'''', ''''Die Demokratie in Deutschland wird als nicht ungefährdet angesehen, weil sie nicht auf eine ungebrochene Tradition zurückblicken kann. Die Geschichte der Demokratie in Deutschland war eine Geschichte gescheiterter Versuche, und erst der zweite demokratische Staat ist bisher eine Erfolgsgeschichte gewesen. Viele Bürger betrachten die Erfolge der Demokratie als selbstverständlich und halten Distanz zur Politik, weil sie meinen, dass die Politiker ''''''''doch machen, was sie wollen''''''''. Zudem haben manche ein idealisiertes Bild von einer harmonischen, problemlos funktionierenden Demokratie, was in der Realität nicht existiert. Diese kritische Haltung und die hohen Erwartungen führen dazu, dass die Demokratie als gefährdet wahrgenommen wird.'''', ''''Die Demokratie in Deutschland gilt als gefährdet, weil sie auf keine ungebrochene Tradition zurückblicken kann, Bürger skeptisch gegenüber Politikern sind und unrealistische Erwartungen an eine konfliktfreie Gesellschaft haben.'''', 152, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (153, ''''Was bedeutet der Begriff ''''''''Demokratie'''''''' wörtlich?'''', ''''Der Begriff ''''''''Demokratie'''''''' bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. Dies bedeutet, dass die Bürger eines Landes die Macht haben, über ihre Regierung mitzubestimmen und ihre Meinung frei zu äußern.'''', ''''''''''''Herrschaft des Volkes'''''''''''', 153, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (154, ''''Wie kann das Volk in einer modernen Demokratie die Herrschaft ausüben?'''', ''''In einer modernen Demokratie kann das Volk die Herrschaft nicht direkt ausüben, wie es in den kleinen Stadtstaaten des antiken Griechenlands der Fall war. Stattdessen übt das Volk die Herrschaft mittelbar und indirekt aus, indem es seine Vertreter (Repräsentanten) wählt, die dann in seinem Namen Entscheidungen treffen.'''', ''''Das Volk übt die Herrschaft in einer modernen Demokratie mittelbar und indirekt aus, indem es seine Vertreter wählt.'''', 154, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (155, ''''Was bedeutet die Volkssouveränität in der deutschen Demokratie?'''', ''''Die Volkssouveränität bedeutet, dass jede staatliche Machtausübung durch das Volk legitimiert sein muss. Die staatlichen Organe müssen entweder direkt durch Volkswahlen hervorgehen oder von den gewählten Repräsentanten eingesetzt werden. Die Amtsinhaber sind dem Volk bzw. seinen Repräsentanten verantwortlich und können aus ihrem Amt entfernt werden.'''', ''''Die Volkssouveränität bedeutet, dass die staatliche Macht vom Volk ausgeht und dass alle Amtsinhaber dem Volk verantwortlich sind.'''', 155, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (156, ''''Warum haben die Verfassungsgeber in Deutschland ein reines Repräsentativsystem gewählt?'''', ''''Die Verfassungsgeber haben sich für ein reines Repräsentativsystem entschieden, weil sie durch die negativen Erfahrungen in der Zeit der Weimarer Republik beeinflusst waren. Sie wollten vermeiden, dass die direkte Demokratie zu Instabilität und politischen Krisen führt. Stattdessen haben sie ein System geschaffen, in dem die Bürger durch Wahlen Repräsentanten wählen, die dann in ihrem Auftrag die Entscheidungen im Staat treffen.'''', ''''Die Verfassungsgeber wählten ein reines Repräsentativsystem, um Instabilität und politische Krisen zu vermeiden, nach negativen Erfahrungen in der Weimarer Republik.'''', 156, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (157, ''''Was ist ein wichtiger Unterschied zwischen der Weimarer Republik und der Bundesrepublik Deutschland?<br>A) Die Weimarer Republik hatte eine stärkere Exekutive.<br>B) Die Bundesrepublik Deutschland hat eine formale Demokratie.<br>C) Die Weimarer Republik ermöglichte die Änderung der Grundrechte mit Zweidrittelmehrheit.<br>D) Die Bundesrepublik Deutschland hat keine unantastbaren Prinzipien.'''', ''''C) Die Weimarer Republik ermöglichte die Änderung der Grundrechte mit Zweidrittelmehrheit.'''', ''''C) Die Weimarer Republik ermöglichte die Änderung der Grundrechte mit Zweidrittelmehrheit.'''', 157, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (158, ''''Welche zwei Hauptargumente sprechen gegen die Einführung einer direkten Demokratie?'''', ''''Zwei Hauptargumente sprechen gegen die Einführung einer direkten Demokratie: Erstens könnte die direkte Demokratie der Manipulation Tür und Tor öffnen, indem Macht denen zufällt, die die Fragen formulieren und Zugang zu den Medien haben. Zweitens könnte ein per Volksabstimmung beschlossenes Gesetz leicht den Anschein größerer Legitimität gewinnen und weniger korrigierbar sein als parlamentarische Entscheidungen, was zu einer Tendenz führen könnte, das Parlament nur noch weniger wichtige Fragen entscheiden zu lassen.'''', ''''Zwei Argumente gegen die direkte Demokratie: Manipulation durch Fragesteller und Medienzugang, sowie unkorrigierbare Gesetze mit Anschein größerer Legitimität.'''', 158, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (159, ''''Not suitable'''', ''''Not suitable'''', ''''Not suitable'''', 159, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (160, ''''Was ist die Mehrheitsmeinung der Deutschen über die Demokratie in Deutschland?'''', ''''Die Mehrheitsmeinung der Deutschen über die Demokratie in Deutschland ist, dass sie die beste Staatsform ist. Laut Umfrageergebnissen von 2009 glauben drei Viertel der Bürgerinnen und Bürger in Westdeutschland und 36 Prozent in Ostdeutschland, dass die Demokratie die beste Staatsform ist. Eine weitere Umfrage von 2008 zeigt, dass 62 Prozent aller Deutschen zufrieden mit der Demokratie sind.'''', ''''Die Mehrheit der Deutschen hält die Demokratie für die beste Staatsform.'''', 160, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (161, ''''Warum ist die Demokratie in Deutschland nicht ungefährdet?'''', ''''Die Demokratie in Deutschland ist nicht ungefährdet, weil sie keine ungebrochene Tradition hat. Die Geschichte der Demokratie in Deutschland war eine Geschichte gescheiterter Versuche, und erst der zweite demokratische Staat ist bisher eine Erfolgsgeschichte gewesen. Viele Bürger betrachten diese Erfolge als selbstverständlich und haben ein idealisiertes Bild von einer harmonischen, problemlos funktionierenden Demokratie.'''', ''''Die Demokratie in Deutschland ist nicht ungefährdet, weil sie keine lange Tradition hat und die Bürger unrealistische Erwartungen haben.'''', 161, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (162, ''''Not suitable'''', ''''Not applicable'''', ''''Not applicable'''', 162, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (163, ''''Was bedeutet der Begriff ''''''''Demokratie'''''''' wörtlich?'''', ''''Der Begriff ''''''''Demokratie'''''''' bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. Dies bedeutet, dass die Bürger in einer Demokratie das Recht haben, über ihre Regierung mitzubestimmen und ihre Meinung frei zu äußern.'''', ''''Herrschaft des Volkes'''', 163, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (164, ''''Was bedeutet der Begriff ''''''''Demokratie'''''''' wörtlich?'''', ''''Der Begriff ''''''''Demokratie'''''''' bedeutet wörtlich ''''''''Herrschaft des Volkes''''''''. Dies bedeutet, dass die Bürger in einer Demokratie das Recht haben, über ihre Regierung mitzubestimmen und ihre Meinung frei zu äußern.'''', ''''''''''''Herrschaft des Volkes'''''''''''', 164, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (165, ''''Wie kann das Volk in einer modernen Demokratie die Herrschaft ausüben?'''', ''''In einer modernen Demokratie kann das Volk die Herrschaft nicht direkt ausüben, wie es in den kleinen Stadtstaaten des antiken Griechenlands der Fall war. Stattdessen übt das Volk die Herrschaft mittelbar und indirekt aus, indem es seine Vertreter (Repräsentanten) wählt, die dann in seinem Namen Entscheidungen treffen.'''', ''''Indirekt durch Wahl von Vertretern.'''', 165, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (166, ''''Was ist die Bedeutung der Volkssouveränität in der Demokratie nach Artikel 20 des Grundgesetzes?'''', ''''Die Volkssouveränität bedeutet, dass jede staatliche Machtausübung durch das Volk legitimiert sein muss. Die staatlichen Organe müssen entweder direkt durch Volkswahlen oder indirekt durch die gewählten Repräsentanten eingesetzt werden. Die Amtsinhaber sind dem Volk bzw. seinen Repräsentanten verantwortlich und können aus ihrem Amt entfernt werden.'''', ''''Die Volkssouveränität bedeutet, dass die staatliche Macht vom Volk ausgeht und dass alle Amtsinhaber dem Volk verantwortlich sind.'''', 166, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (167, ''''Warum enthält das Grundgesetz keine Elemente direkter Demokratie?'''', ''''Die Entscheidung gegen die Aufnahme von Elementen direkter Demokratie in das Grundgesetz wurde durch die negativen Erfahrungen in der Zeit der Weimarer Republik beeinflusst. Die Verfassungsgeber wollten ein reines Repräsentativsystem etablieren, in dem die Staatsgewalt durch Wahlen an Repräsentanten übertragen wird.'''', ''''Die negativen Erfahrungen in der Weimarer Republik führten dazu, dass das Grundgesetz keine Elemente direkter Demokratie enthält.'''', 167, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (168, ''''Was ist der Hauptunterschied zwischen der Weimarer Republik und der Demokratie des Grundgesetzes?'''', ''''Der Hauptunterschied zwischen der Weimarer Republik und der Demokratie des Grundgesetzes liegt darin, dass die Weimarer Republik eine formale Demokratie war, die ihre Feinde zerstören konnte, während die Demokratie des Grundgesetzes eine Wertordnung ist, die freiheitliche demokratische Grundordnung mit ihren unantastbaren Prinzipien schützt.'''', ''''Die Weimarer Republik war eine formale Demokratie, die zerstört werden konnte, während die Demokratie des Grundgesetzes eine Wertordnung mit unantastbaren Prinzipien ist.'''', 168, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (169, ''''Welche Nachteile sehen Kritiker bei der direkten Demokratie und Volksabstimmung?'''', ''''Kritiker sehen bei der direkten Demokratie und Volksabstimmung Nachteile wie die Möglichkeit der Manipulation durch die Formulierung von Fragen und den Zugang zu Medien, die Gefahr von Demagogie und die Schwierigkeit, beschlossene Gesetze zu korrigieren. Darüber hinaus könnte die Tendenz entstehen, das Parlament nur noch weniger wichtige Fragen entscheiden zu lassen.'''', ''''Nachteile der direkten Demokratie und Volksabstimmung sind die Manipulationsmöglichkeit, die Gefahr von Demagogie und die Schwierigkeit, Gesetze zu korrigieren.'''', 169, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (170, ''''Wie viele Prozent der Deutschen halten die Demokratie für die beste Staatsform?'''', ''''Laut einer Umfrage des Instituts für Demoskopie, Allensbach, im Jahr 2009 halten drei Viertel der Bürgerinnen und Bürger der Bundesrepublik (West) die Demokratie für die beste Staatsform. In den neuen Bundesländern hielten 36 Prozent die Demokratie für die beste Staatsform.'''', ''''Drei Viertel der Westdeutschen und 36 Prozent der Ostdeutschen halten die Demokratie für die beste Staatsform.'''', 170, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (171, ''''Warum ist die Demokratie in Deutschland nicht ungefährdet?'''', ''''Die Demokratie in Deutschland ist nicht ungefährdet, weil sie keine lange, ununterbrochene Tradition hat. Die Geschichte der Demokratie in Deutschland war eine Geschichte gescheiterter Versuche, und erst der zweite demokratische Staat hat bisher Erfolg gehabt. Viele Bürger betrachten diese Erfolge als selbstverständlich und haben ein idealisiertes Bild von einer harmonischen, problemlos funktionierenden Demokratie.'''', ''''Die Demokratie in Deutschland ist nicht ungefährdet, weil sie keine lange Tradition hat und die Bürger hohe Erwartungen haben, aber skeptisch gegenüber Politikern sind.'''', 171, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (172, ''''Warum hat der Bundestag eine besondere Legitimation im politischen System Deutschlands?'''', ''''Der Bundestag hat eine besondere Legitimation, weil er das einzige Verfassungsorgan ist, das direkt vom Volk gewählt wird. Durch diese direkte Wahl erhält das Parlament und seine Mitglieder eine besondere Legitimation, da das Volk seine politische Macht auf sie überträgt.'''', ''''Der Bundestag hat eine besondere Legitimation, weil er direkt vom Volk gewählt wird.'''', 172, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (173, ''''Wer wählt den Bundespräsidenten?'''', ''''Der Bundespräsident wird vom Parlament gewählt, zusammen mit Vertretern der Länderparlamente.'''', ''''Parlament und Länderparlamente'''', 173, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (174, ''''Welche vier Hauptaufgaben hat das Parlament in einem parlamentarischen Regierungssystem?'''', ''''Die vier Hauptaufgaben des Parlaments sind: die Gesetzgebung (einschließlich Haushaltsbewilligung), die Wahlfunktion (Wahl der Regierung und anderer Verfassungsorgane, Auswahl des politischen Personals), die Kommunikation (Teilnahme an der öffentlichen Debatte, politische Artikulation öffentlicher Meinungen und Interessen, politisch-staatliche Informationen und Programme) und die Kontrolle und Initiative (Kontrolle von Regierung und Verwaltung, Politikinitiativen und -konzepte).'''', ''''Die vier Hauptaufgaben des Parlaments sind: Gesetzgebung, Wahlfunktion, Kommunikation und Kontrolle und Initiative.'''', 174, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (175, ''''Welche der folgenden Aussagen zur Gesetzgebung in Deutschland ist korrekt?<br>A) Der Bundestag ist allein für die Gesetzgebung des Bundes zuständig.<br>B) Die Länder haben die ausschließliche Zuständigkeit für die Gesetzgebung in bestimmten Bereichen.<br>C) Der Bundesrat ist nur bei Verfassungsänderungen zustimmungspflichtig.<br>D) Die Zustimmungsgesetze bedürfen der Zustimmung des Bundesrats.'''', ''''D) Die Zustimmungsgesetze bedürfen der Zustimmung des Bundesrats.'''', ''''D) Zustimmungsgesetze benötigen die Zustimmung des Bundesrats.'''', 175, 8);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (176, ''''Welche Rolle spielt das Parlament in der politischen Führung in Deutschland?'''', ''''Das Parlament spielt eine wichtige Rolle in der politischen Führung in Deutschland, indem es die Regierungsbildung, die Programmfestlegung, die Mittelbeschaffung und die öffentliche Debatte und Kommunikation der Führungsthemen beeinflusst. Es wirkt auch am politischen Entscheidungsprozess mit und kontrolliert die Regierung und Verwaltung.'''', ''''Das Parlament beeinflusst die Regierungsbildung, Programmfestlegung, Mittelbeschaffung und öffentliche Debatte und kontrolliert die Regierung und Verwaltung.'''', 176, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (177, ''''Was bedeutet das freie Mandat für Abgeordnete im Deutschen Bundestag?'''', ''''Das freie Mandat bedeutet, dass Abgeordnete im Deutschen Bundestag unabhängig von ihrer Wähler- und Parteibasis sind und nur ihrem Gewissen unterworfen sind. Sie sind Vertreter des ganzen Volkes und nicht an Aufträge und Weisungen gebunden. Dies bewahrt sie auch davor, in einem Konflikt mit der Fraktion das Parlamentsmandat zu verlieren.'''', ''''Das freie Mandat bedeutet, dass Abgeordnete unabhängig und nur ihrem Gewissen unterworfen sind.'''', 177, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (178, ''''Was ist die Mindeststärke einer Fraktion im Bundestag?'''', ''''Die Mindeststärke einer Fraktion im Bundestag beträgt 5 % der Parlamentarier.'''', ''''5 % der Parlamentarier'''', 178, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (179, ''''Welche besondere Legitimation haben die Mitglieder des Bundestags?'''', ''''Die Mitglieder des Bundestags haben eine besondere Legitimation, weil sie direkt vom Volk gewählt werden. Dies verleiht dem Parlament als Institution und den Parlamentsmitgliedern eine höhere Legitimität, da das Volk seine politische Macht nicht direkt ausübt, sondern in Wahlen auf Volksvertretungen auf den drei Ebenen von Bundestag, Länderparlamenten und kommunalen Vertretungen überträgt.'''', ''''Direkte Wahl durch das Volk'''', 179, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (180, ''''Wer wählt den Bundespräsidenten?'''', ''''Der Bundespräsident wird zusammen mit Vertretern der Länderparlamente gewählt.'''', ''''Der Bundespräsident wird zusammen mit Vertretern der Länderparlamente gewählt.'''', 180, 18);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (181, ''''Welche vier Hauptaufgaben hat das Parlament in einem parlamentarischen Regierungssystem?'''', ''''Die vier Hauptaufgaben des Parlaments sind: die Gesetzgebung (einschließlich Haushaltsbewilligung), die Wahlfunktion (Wahl der Regierung und anderer Verfassungsorgane, Auswahl des politischen Personals), die Kommunikation (Teilnahme an der öffentlichen Debatte, politische Artikulation öffentlicher Meinungen und Interessen, politisch-staatliche Informationen und Programme) und die Kontrolle und Initiative (Kontrolle von Regierung und Verwaltung, Politikinitiativen und -konzepte).'''', ''''Die vier Hauptaufgaben des Parlaments sind Gesetzgebung, Wahlfunktion, Kommunikation und Kontrolle und Initiative.'''', 181, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (182, ''''Welche der folgenden Aussagen zur Gesetzgebung in Deutschland ist korrekt?<br>A) Der Bundestag hat die alleinige Zuständigkeit für die Gesetzgebung.<br>B) Die Länder haben die ausschließliche Zuständigkeit für die Gesetzgebung in bestimmten Themengebieten.<br>C) Der Bundesrat hat nur eine beratende Funktion bei der Gesetzgebung.<br>D) Die Zustimmung des Bundesrats ist für alle Gesetze erforderlich.'''', ''''B) Die Länder haben die ausschließliche Zuständigkeit für die Gesetzgebung in bestimmten Themengebieten.'''', ''''B) Die Länder haben die ausschließliche Zuständigkeit für die Gesetzgebung in bestimmten Themengebieten.'''', 182, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (183, ''''Welche Aufgaben übernimmt die Opposition im politischen Prozess in Deutschland?'''', ''''Die Opposition übernimmt vor allem parlamentarische Aufgaben der Kontrolle und Kritik. Sie hält den Parteien- bzw. Fraktionenwettbewerb aufrecht und kann ihren Einfluss ausbauen, wenn sie einen starken parteilichen Rückhalt in der Ländervertretung (Bundesrat) findet.'''', ''''Die Opposition kontrolliert und kritisiert die Regierung und hält den Parteienwettbewerb aufrecht.'''', 183, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (184, ''''Was bedeutet das ''''''''freie Mandat'''''''' für Abgeordnete im Deutschen Bundestag?'''', ''''Das ''''''''freie Mandat'''''''' bedeutet, dass Abgeordnete im Deutschen Bundestag unabhängig von ihrer Wähler- und Parteibasis sind und nur ihrem Gewissen unterworfen sind. Sie sind Vertreter des ganzen Volkes und nicht an Aufträge und Weisungen gebunden. Dieses Mandat bewahrt sie auch davor, in einem Konflikt mit der Fraktion das Parlamentsmandat zu verlieren.'''', ''''Das ''''''''freie Mandat'''''''' bedeutet, dass Abgeordnete unabhängig und nur ihrem Gewissen unterworfen sind.'''', 184, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (185, ''''Was ist die Mindeststärke einer Fraktion im Deutschen Bundestag?'''', ''''Die Mindeststärke einer Fraktion im Deutschen Bundestag beträgt 5 % der Parlamentarier.'''', ''''5 % der Parlamentarier'''', 185, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (186, ''''Wer wählt den Bundespräsidenten?'''', ''''Der Bundespräsident wird vom Parlament gewählt, zusammen mit Vertretern der Länderparlamente.'''', ''''Parlament und Länderparlamente'''', 186, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (187, ''''Welche vier Hauptaufgaben hat das Parlament in einem parlamentarischen Regierungssystem?'''', ''''Die vier Hauptaufgaben des Parlaments sind: die Gesetzgebung (einschließlich Haushaltsbewilligung), die Wahlfunktion (Wahl der Regierung und anderer Verfassungsorgane, Auswahl des politischen Personals), die Kommunikation (Teilnahme an der öffentlichen Debatte, politische Artikulation öffentlicher Meinungen und Interessen, politisch-staatliche Informationen und Programme) und die Kontrolle und Initiative (Kontrolle von Regierung und Verwaltung, Politikinitiativen und -konzepte).'''', ''''Die vier Hauptaufgaben des Parlaments sind Gesetzgebung, Wahlfunktion, Kommunikation und Kontrolle und Initiative.'''', 187, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (188, ''''Welche Rolle spielt der Bundesrat bei der Gesetzgebung in Deutschland?'''', ''''Der Bundesrat spielt eine wichtige Rolle bei der Gesetzgebung in Deutschland, insbesondere bei Zustimmungsgesetzen, die seine Zustimmung benötigen. Etwa Dreiviertel aller Zustimmungsgesetze sind von den Länderverwaltungen auszuführen, was dem Bundesrat weitgehende Mitwirkungsrechte bei der Gesetzgebung verschafft.'''', ''''Der Bundesrat hat Mitwirkungsrechte bei der Gesetzgebung, insbesondere bei Zustimmungsgesetzen, die seine Zustimmung benötigen.'''', 188, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (189, ''''Welche Rolle spielt das Parlament in der politischen Führung in Deutschland?'''', ''''Das Parlament spielt eine wichtige Rolle in der politischen Führung in Deutschland. Es ist Teil der gesamtpolitischen Führung und übt seine Führungsfunktionen aus, indem es die Regierungsbildung, die Programmfestlegung, die Mittelbeschaffung, die öffentliche Debatte und Kommunikation der Führungsthemen, die Mitwirkung am politischen Entscheidungsprozess und die Kontrolle von Regierung und Verwaltung durchführt.'''', ''''Das Parlament ist Teil der politischen Führung in Deutschland und übt Führungsfunktionen wie Regierungsbildung, Programmfestlegung und Kontrolle von Regierung und Verwaltung aus.'''', 189, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (190, ''''Was bedeutet das ''''''''freie Mandat'''''''' für Abgeordnete im Deutschen Bundestag?'''', ''''Das ''''''''freie Mandat'''''''' bedeutet, dass Abgeordnete im Deutschen Bundestag unabhängig von ihrer Wähler- und Parteibasis sind und nur ihrem Gewissen unterworfen sind. Sie sind Vertreter des ganzen Volkes und nicht an Aufträge und Weisungen gebunden. Dieses Mandat bewahrt sie auch davor, in einem Konflikt mit der Fraktion das Parlamentsmandat zu verlieren.'''', ''''Das ''''''''freie Mandat'''''''' bedeutet, dass Abgeordnete unabhängig und nur ihrem Gewissen unterworfen sind.'''', 190, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (191, ''''Welche der folgenden Aussagen über die Funktion von Fraktionen im Bundestag ist korrekt?<br>A) Die Mindeststärke einer Fraktion beträgt 3 % der Parlamentarier.<br>B) Die Fraktionen entscheiden über den parlamentarischen Einsatz und Einfluss der Abgeordneten.<br>C) Die Gesetzesberatung findet nur im Parlamentsplenum statt.<br>D) Die Abgeordneten sind ehrenamtlich tätig.'''', ''''B) Die Fraktionen entscheiden über den parlamentarischen Einsatz und Einfluss der Abgeordneten.'''', ''''B) Die Fraktionen entscheiden über den parlamentarischen Einsatz und Einfluss der Abgeordneten.'''', 191, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (192, ''''Was war eine Folge des wirtschaftlichen Aufschwungs in den Industrieländern in den 1950er- und 1960er-Jahren?'''', ''''Eine Folge des wirtschaftlichen Aufschwungs in den Industrieländern in den 1950er- und 1960er-Jahren waren erhebliche Umweltbelastungen.'''', ''''Erhebliche Umweltbelastungen'''', 192, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (193, ''''Was ist das Ziel der Umweltpolitik?'''', ''''Das Ziel der Umweltpolitik ist es, die Umweltqualität zu verbessern oder aufrechtzuerhalten, indem sie langfristig den Ausgleich zwischen Ökonomie und Ökologie herstellt. Dies beinhaltet, sparsam mit knappen Rohstoffen umzugehen, sie wieder zu verwenden, den Produktionsprozess in Form geschlossener Kreisläufe zu organisieren, erneuerbare Energieträger zu entwickeln, ökologische Fehlentwicklungen und Umweltbelastungen zu vermeiden und bereits aufgetretene Schädigungen zu beseitigen.'''', ''''Das Ziel der Umweltpolitik ist es, die Umweltqualität zu verbessern oder aufrechtzuerhalten, indem sie den Ausgleich zwischen Ökonomie und Ökologie herstellt.'''', 193, 5);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (194, ''''Was ist das Hauptproblem bei der Festlegung einer richtigen Wachstumsrate in Bezug auf den Umweltschutz?'''', ''''Das Hauptproblem bei der Festlegung einer richtigen Wachstumsrate in Bezug auf den Umweltschutz besteht darin, dass es schwierig ist, ein Gleichgewicht zwischen wirtschaftlichem Wachstum und Umweltschutz zu finden. Ein Nullwachstum würde bei einer wachsenden Bevölkerung zu einem Rückgang des Pro-Kopf-Sozialprodukts führen, während ein qualitatives Wachstum, das auf umweltorientierten Umstrukturierungen der Wirtschaft basiert, schwierig zu berechnen ist.'''', ''''Das Hauptproblem ist das Finden eines Gleichgewichts zwischen Wachstum und Umweltschutz, da Nullwachstum zu einem Rückgang des Pro-Kopf-Sozialprodukts führen würde und qualitatives Wachstum schwierig zu berechnen ist.'''', 194, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (195, ''''Was war eine Folge des wirtschaftlichen Aufschwungs in den Industrieländern in den 1950er- und 1960er-Jahren?'''', ''''Eine Folge des wirtschaftlichen Aufschwungs in den Industrieländern in den 1950er- und 1960er-Jahren waren erhebliche Umweltbelastungen.'''', ''''Erhebliche Umweltbelastungen'''', 195, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (196, ''''Was ist das Hauptziel der Umweltpolitik?'''', ''''Das Hauptziel der Umweltpolitik ist die Verbesserung oder Aufrechterhaltung der Umweltqualität durch wirtschaftspolitische Maßnahmen, die auf den Ausgleich des Gegensatzes zwischen Ökonomie und Ökologie gerichtet sind.'''', ''''Verbesserung oder Aufrechterhaltung der Umweltqualität'''', 196, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (197, ''''Was ist das Hauptproblem bei der Festlegung einer richtigen Wachstumsrate in Bezug auf den Umweltschutz?'''', ''''Das Hauptproblem bei der Festlegung einer richtigen Wachstumsrate in Bezug auf den Umweltschutz besteht darin, dass Wirtschaftswachstum und Umweltschutz oft als konkurrierende Ziele angesehen werden. Ein Nullwachstum würde bei einer wachsenden Bevölkerung zu einem Rückgang des Pro-Kopf-Sozialprodukts führen, während umweltorientierte Umstrukturierungen der Wirtschaft notwendig sind, um die Umweltbelastung zu verringern. Die Festlegung einer richtigen Wachstumsrate ist deshalb kontrovers diskutiert und erfordert eine Abwägung zwischen wirtschaftlichen und umweltbezogenen Zielen.'''', ''''Das Hauptproblem ist die Abwägung zwischen Wirtschaftswachstum und Umweltschutz, da Nullwachstum bei wachsender Bevölkerung zu einem Rückgang des Pro-Kopf-Sozialprodukts führt, während umweltorientierte Umstrukturierungen notwendig sind.'''', 197, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (198, ''''Was ist das Hauptziel der Umweltpolitik?'''', ''''Das Hauptziel der Umweltpolitik ist es, die Umweltqualität zu verbessern oder aufrechtzuerhalten, indem sie den Ausgleich zwischen Ökonomie und Ökologie herstellt. Dies bedeutet, dass wirtschaftspolitisches Handeln auf den Erhalt der Umweltmedien wie Boden, Gewässer, Klima und Luft in guter Qualität ausgerichtet sein muss.'''', ''''Das Hauptziel der Umweltpolitik ist die Verbesserung oder Aufrechterhaltung der Umweltqualität durch den Ausgleich zwischen Ökonomie und Ökologie.'''', 198, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (199, ''''Was ist das Ziel des qualitativen Wachstums?'''', ''''Das Ziel des qualitativen Wachstums ist es, eine Zunahme der Pro-Kopf-Lebensqualität ohne zunehmende Umweltbelastungen bzw. sogar mit steigender Umweltqualität zu erreichen. Dies soll durch umweltorientierte Umstrukturierungen der Wirtschaft und die Entwicklung von Technologien, die die Umweltbelastung verringern, erreicht werden.'''', ''''Eine Zunahme der Pro-Kopf-Lebensqualität ohne Umweltbelastungen.'''', 199, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (200, ''''Wie viele Amtssprachen hat die Europäische Union?<br>A) 10<br>B) 20<br>C) 24<br>D) 27'''', ''''C) 24'''', ''''24'''', 200, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (201, ''''Welche der folgenden Währungen ist keine Währung eines EU-Landes?<br>A) Bulgarischer Lew (BGN)<br>B) Dänische Krone (DKK)<br>C) US-Dollar (USD)<br>D) Schwedische Krone (SEK)'''', ''''C) US-Dollar (USD)'''', ''''C) US-Dollar (USD)'''', 201, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (202, ''''Wie viele Staaten gehören der Europäischen Union an?<br>A) 25<br>B) 26<br>C) 27<br>D) 28'''', ''''C) 27'''', ''''27'''', 202, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (203, ''''Was war das Hauptziel der Gründung der Europäischen Wirtschaftsgemeinschaft (EWG) in den 1950er-Jahren?'''', ''''Das Hauptziel der Gründung der Europäischen Wirtschaftsgemeinschaft (EWG) in den 1950er-Jahren war es, militärische Konflikte für die Zukunft zu verhindern und durch den größeren Markt das Wirtschaftswachstum zu beschleunigen, um damit den Wohlstand der Bürger zu steigern.'''', ''''Militärische Konflikte verhindern und Wirtschaftswachstum fördern.'''', 203, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (204, ''''Welchen Rang nimmt die Europäische Union nach nominalem Bruttoinlandsprodukt ein?'''', ''''Die Europäische Union nimmt nach nominalem Bruttoinlandsprodukt den dritten Rang ein, hinter den Vereinigten Staaten und der Volksrepublik China.'''', ''''Dritter Rang'''', 204, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (205, ''''Welche der folgenden Institutionen ist nicht Teil der Organe der Europäischen Union?<br>A) Europäischer Rat<br>B) Europäisches Parlament<br>C) Europäische Kommission<br>D) Bundesverfassungsgericht'''', ''''D) Bundesverfassungsgericht. Das Bundesverfassungsgericht ist ein deutsches Verfassungsgericht und nicht Teil der Organe der Europäischen Union.'''', ''''D) Bundesverfassungsgericht'''', 205, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (206, ''''Was war der entscheidende Ausgangspunkt für die europäische Integration?'''', ''''Der entscheidende Ausgangspunkt für die europäische Integration war das Ende des Zweiten Weltkrieges 1945. Durch eine Vernetzung der militärisch relevanten Wirtschaftssektoren sollte ein neuer Krieg zwischen den früheren Gegnern unmöglich gemacht und in der Folge auch die politische Annäherung und dauerhafte Versöhnung der beteiligten Staaten erreicht werden.'''', ''''Das Ende des Zweiten Weltkrieges 1945'''', 206, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (207, ''''Was war das Hauptziel von Jean Monnets Vorschlag zur Gründung der Europäischen Gemeinschaft für Kohle und Stahl?'''', ''''Das Hauptziel von Jean Monnets Vorschlag war es, ein erneutes Wettrüsten zwischen Deutschland und Frankreich zu verhindern, indem die gesamte französisch-deutsche Kohle- und Stahlproduktion einer gemeinsamen Behörde unterstellt wurde.'''', ''''Ein erneutes Wettrüsten zwischen Deutschland und Frankreich verhindern.'''', 207, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (208, ''''Was war das Ziel der Europäischen Wirtschaftsgemeinschaft (EWG), die 1957 durch die Römischen Verträge gegründet wurde?'''', ''''Das Ziel der Europäischen Wirtschaftsgemeinschaft (EWG) war die Schaffung eines gemeinsamen Marktes, in dem sich Waren, Dienstleistungen, Kapital und Arbeitskräfte frei bewegen konnten.'''', ''''Schaffung eines gemeinsamen Marktes mit freiem Waren-, Dienstleistungs-, Kapital- und Arbeitskräfteverkehr.'''', 208, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (209, ''''Was war die Eurosklerose?'''', ''''Die Eurosklerose war eine Phase in der ersten Hälfte der 1980er-Jahre, in der die britische Premierministerin Margaret Thatcher die Forderung nach einer Absenkung der britischen Beitragszahlungen stellte und damit weitere Integrationsfortschritte verhinderte.'''', ''''Die Eurosklerose war eine Phase der Stagnation in den 1980er-Jahren, verursacht durch britische Forderungen nach Beitragszahlungsabsenkungen.'''', 209, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (210, ''''Was war das Hauptziel des Vertrags von Maastricht im Jahr 1992?'''', ''''Der Vertrag von Maastricht im Jahr 1992 hatte das Hauptziel, die Europäische Union (EU) zu gründen. Darüber hinaus beschloss der Vertrag die Gründung einer Wirtschafts- und Währungsunion, die später zur Einführung des Euro führte, sowie eine engere Koordinierung in der Außen- und Sicherheitspolitik und im Bereich Inneres und Justiz.'''', ''''Die Gründung der Europäischen Union und die Schaffung einer Wirtschafts- und Währungsunion.'''', 210, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (211, ''''Was war ein wichtiger Schritt in der Entwicklung der Europäischen Union im Jahr 1999?'''', ''''Ein wichtiger Schritt in der Entwicklung der Europäischen Union im Jahr 1999 war die Einführung des Euro als standardmäßige Währung. Dieser Schritt trug zur wirtschaftlichen Integration der EU-Mitgliedstaaten bei und stärkte die europäische Wirtschaft.'''', ''''Die Einführung des Euro als standardmäßige Währung.'''', 211, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (212, ''''Was war das strategische Ziel der Lissabon-Strategie für die Europäische Union?'''', ''''Das strategische Ziel der Lissabon-Strategie war, die Europäische Union zum wettbewerbsfähigsten und dynamischsten wissensbasierten Wirtschaftsraum in der Welt zu machen.'''', ''''Die EU zum wettbewerbsfähigsten und dynamischsten wissensbasierten Wirtschaftsraum in der Welt machen.'''', 212, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (213, ''''Was war das Ziel des Europäischen Konvents, der 2001 auf dem Gipfel von Laeken beschlossen wurde?'''', ''''Das Ziel des Europäischen Konvents war es, einen neuen Grundvertrag auszuarbeiten, mit dem die Entscheidungsverfahren der EU effizienter und zugleich demokratischer werden sollten.'''', ''''Ein effizienteres und demokratischeres Entscheidungsverfahren in der EU.'''', 213, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (214, ''''Wem wurde 2012 der Friedensnobelpreis verliehen?'''', ''''Der Europäischen Union wurde 2012 der Friedensnobelpreis verliehen, für ihren Beitrag zur Förderung von Frieden und Versöhnung, Demokratie und Menschenrechten in Europa über einen Zeitraum von mehr als sechs Jahrzehnten.'''', ''''Der Europäischen Union'''', 214, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (215, ''''Welche Maßnahmen hat die Europäische Union zur Bewältigung der Finanzkrise und der Eurokrise ergriffen?'''', ''''Zur Bewältigung der Finanzkrise und der Eurokrise hat die Europäische Union eine Reihe von Maßnahmen ergriffen, darunter den Europäischen Stabilitätsmechanismus (ESM) als Teil des Euro-Rettungsschirms, den Europäischen Fiskalpakt, der den teilnehmenden Mitgliedsstaaten Haushaltsdisziplin und Schuldenbegrenzung auferlegte, und die Europäische Bankenunion, die einheitliche Richtlinien und Regelungen im Bereich der Finanzmarktaufsicht und der Sanierung oder Abwicklung von Kreditinstituten innerhalb der EU geschaffen hat.'''', ''''Die EU hat den Europäischen Stabilitätsmechanismus, den Europäischen Fiskalpakt und die Europäische Bankenunion eingerichtet, um die Finanzkrise und die Eurokrise zu bewältigen.'''', 215, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (216, ''''Was ist das Ziel der EU im Rahmen des ''''''''europäischen grünen Deals'''''''' bezüglich des Treibhausgasausstoßes bis 2030?'''', ''''Das Ziel der EU im Rahmen des ''''''''europäischen grünen Deals'''''''' ist es, den Treibhausgasausstoß in der EU bis 2030 um 50 Prozent zum Vergleichsjahr 1990 zu senken.'''', ''''50 Prozent Reduktion des Treibhausgasausstoßes bis 2030 im Vergleich zu 1990.'''', 216, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (217, ''''Wie lang ist die Küstenlinie der Europäischen Union?'''', ''''Die Küstenlinie der Europäischen Union beträgt im Ganzen 67.770,9 km.'''', ''''67.770,9 km'''', 217, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (218, ''''Wie viele biogeographische Regionen wurden in der Europäischen Union durch die Europäische Umweltagentur definiert?'''', ''''Die Europäische Umweltagentur unterteilte die Europäische Union biogeographisch in neun terrestrische Regionen und fünf angrenzende Meeresregionen.'''', ''''9 terrestrische und 5 Meeresregionen'''', 218, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (219, ''''Welche Länder traten der Europäischen Gemeinschaft 1973 bei?<br>A) Belgien, Deutschland, Frankreich, Italien, Luxemburg und die Niederlande<br>B) Vereinigtes Königreich, Irland und Dänemark<br>C) Griechenland, Portugal und Spanien<br>D) Norwegen'''', ''''B) Vereinigtes Königreich, Irland und Dänemark'''', ''''B) Vereinigtes Königreich, Irland und Dänemark'''', 219, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (220, ''''Wie viele neue Mitgliedstaaten traten am 1. Mai 2004 der Europäischen Union bei?<br>A) 5<br>B) 8<br>C) 10<br>D) 12'''', ''''C) 10. Am 1. Mai 2004 traten zehn Staaten der Europäischen Union bei, darunter acht ehemals kommunistisch regierte mittel- und osteuropäische Länder, Malta und Zypern.'''', ''''C) 10'''', 220, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (221, ''''Welches Gebiet trat 1985 als erstes nach einem Referendum aus der Europäischen Gemeinschaft aus?<br>A) Grönland<br>B) Algerien<br>C) Saint-Barthélemy<br>D) Vereinigtes Königreich'''', ''''A) Grönland trat 1985 als erstes Gebiet nach einem Referendum aus der Europäischen Gemeinschaft aus.'''', ''''A) Grönland'''', 221, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (222, ''''Wie viele Mitgliedstaaten hat die Europäische Union (Stand: 1. Februar 2020)?<br>A) 25<br>B) 27<br>C) 30<br>D) 22'''', ''''B) 27'''', ''''27'''', 222, 2);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (223, ''''Welche der folgenden Aussagen über die Überseegebiete der EU-Mitgliedstaaten ist korrekt?<br>A) Alle Überseegebiete sind Teil der Europäischen Union.<br>B) Einige Überseegebiete sind vollständig in die nationale Verwaltungsstruktur einbezogen und Teil der EU.<br>C) Die meisten Überseegebiete sind von den EU-Verträgen nicht erfasst.<br>D) Alle autonomen Gebiete sind Teil der EU.'''', ''''B) Einige Überseegebiete sind vollständig in die nationale Verwaltungsstruktur einbezogen und Teil der EU.'''', ''''B) Einige Überseegebiete sind Teil der EU.'''', 223, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (224, ''''Was sind die Voraussetzungen für einen Staat, um die EU-Mitgliedschaft zu beantragen?'''', ''''Nach Art. 49 EU-Vertrag kann jeder europäische Staat, der die Werte der EU achtet und sich für ihre Förderung einsetzt, die EU-Mitgliedschaft beantragen. Der Beitritt kann jedoch nur dann vollzogen werden, wenn die sogenannten Kopenhagener Kriterien (insbesondere Demokratie und Rechtsstaatlichkeit) erfüllt sind.'''', ''''Die Voraussetzungen für einen Staat, um die EU-Mitgliedschaft zu beantragen, sind die Achtung der EU-Werte und die Erfüllung der Kopenhagener Kriterien, insbesondere Demokratie und Rechtsstaatlichkeit.'''', 224, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (225, ''''Welches der folgenden Länder wurde 2014 als Beitrittskandidat anerkannt?<br>A) Albanien<br>B) Bosnien und Herzegowina<br>C) Serbien<br>D) Ukraine'''', ''''A) Albanien wurde 2014 als Beitrittskandidat anerkannt.'''', ''''A) Albanien'''', 225, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (226, ''''Welche der folgenden Aussagen über die Beziehungen zwischen der EU und den EFTA-Ländern im EWR ist korrekt?<br>A) Die EFTA-Länder haben volles Stimmrecht in den EU-Organen.<br>B) Die EFTA-Länder haben lediglich ein Anhörungsrecht in gemeinsamen EWR-Ausschüssen.<br>C) Die EFTA-Länder sind politisch in die Strukturen der EU integriert.<br>D) Die EFTA-Länder sind nicht Mitglied des Schengener Abkommens.'''', ''''B) Die EFTA-Länder haben lediglich ein Anhörungsrecht in gemeinsamen EWR-Ausschüssen.'''', ''''B) Die EFTA-Länder haben lediglich ein Anhörungsrecht in gemeinsamen EWR-Ausschüssen.'''', 226, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (227, ''''In welchem Rahmen läuft die Europäische Nachbarschaftspolitik (ENP) ab?'''', ''''Die Europäische Nachbarschaftspolitik (ENP) läuft vollständig im Rahmen der Gemeinsamen Außen- und Sicherheitspolitik ab.'''', ''''Im Rahmen der Gemeinsamen Außen- und Sicherheitspolitik'''', 227, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (228, ''''Was ist das Hauptmerkmal des politischen Systems der Europäischen Union im Vergleich zu einzelstaatlichen politischen Systemen?'''', ''''Das Hauptmerkmal des politischen Systems der Europäischen Union ist seine supranationale Struktur, die sich von einzelstaatlichen politischen Systemen deutlich abhebt. Die EU besitzt eigene Souveränitätsrechte, aber keine Kompetenz-Kompetenz, d.h. sie kann die Verteilung der Zuständigkeiten innerhalb ihres Systems nicht selbst gestalten.'''', ''''Die EU hat eine supranationale Struktur mit eigenen Souveränitätsrechten, aber ohne Kompetenz-Kompetenz.'''', 228, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (229, ''''Was ist die Europäische Atomgemeinschaft (Euratom) und wie ist sie mit der EU verbunden?'''', ''''Die Europäische Atomgemeinschaft (Euratom) ist eine eigenständige internationale Organisation, die auf einem eigenen Gründungsvertrag von 1958 basiert. Sie ist die letzte der noch bestehenden Europäischen Gemeinschaften nach der Auflösung von EGKS und EG. Obwohl sie eigenständig ist, teilt sie ihre Organe mit der EU und ist in ihren Strukturen vollständig an die EU angegliedert.'''', ''''Die Euratom ist eine eigenständige Organisation, die auf einem eigenen Vertrag basiert, aber eng mit der EU verbunden ist und ihre Organe teilt.'''', 229, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (230, ''''Welche der folgenden Aussagen zur Rechtsetzung in der Europäischen Union ist korrekt?<br>A) Die EU hat in allen Politikfeldern Rechtsetzungskompetenz.<br>B) Die Beschlüsse der EU sind in allen Fällen einstimmig zu treffen.<br>C) Die supranationale Methode ermöglicht es, dass Regierungen einzelner Staaten überstimmt werden können.<br>D) Die offene Methode der Koordinierung führt zu bindenden Rechtsakten.'''', ''''C) Die supranationale Methode ermöglicht es, dass Regierungen einzelner Staaten überstimmt werden können.'''', ''''C) Die supranationale Methode ermöglicht Überstimmung von Regierungen.'''', 230, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (231, ''''Welche Politikfelder gehören zu den supranationalen Politikfeldern der EU?'''', ''''Zu den supranationalen Politikfeldern der EU gehören unter anderem die Zollunion, der Europäische Binnenmarkt, die Europäische Wirtschafts- und Währungsunion, die Forschungs- und Umweltpolitik, das Gesundheitswesen, der Verbraucherschutz, Bereiche der Sozialpolitik sowie der Raum der Freiheit, der Sicherheit und des Rechts.'''', ''''Zu den supranationalen Politikfeldern der EU gehören die Zollunion, der Binnenmarkt, die Wirtschafts- und Währungsunion, Forschung, Umwelt, Gesundheit, Verbraucherschutz, Sozialpolitik und der Raum der Freiheit, der Sicherheit und des Rechts.'''', 231, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (232, ''''Wie wirkt sich die qualifizierte Mehrheit im Rat der Europäischen Union auf die Veto-Möglichkeiten der Mitgliedstaaten aus?'''', ''''Die qualifizierte Mehrheit im Rat der Europäischen Union ermöglicht es, dass die Veto-Möglichkeiten der einzelnen Mitgliedstaaten stark eingeschränkt werden. In den meisten Politikfeldern können die Mitgliedstaaten durch eine qualifizierte Mehrheit überstimmt werden.'''', ''''Die qualifizierte Mehrheit im Rat der EU beschränkt die Veto-Möglichkeiten der Mitgliedstaaten, die in den meisten Politikfeldern überstimmt werden können.'''', 232, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (233, ''''Welche Organe der Europäischen Union sind die Legislative?'''', ''''Die Legislative der Europäischen Union besteht aus zwei Organen: dem Europäischen Parlament als Bürgerkammer und dem Rat der Europäischen Union als Länderkammer.'''', ''''Europäisches Parlament und Rat der Europäischen Union'''', 233, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (234, ''''Welches EU-Organ hat das alleinige Initiativrecht für neue Rechtsvorschriften?<br>A) Europäisches Parlament<br>B) Rat der Europäischen Union<br>C) Europäische Kommission<br>D) Europäischer Gerichtshof'''', ''''C) Europäische Kommission'''', ''''C) Europäische Kommission'''', 234, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (235, ''''Wo tagt der Europäische Rat generell?<br>A) Frankfurt am Main<br>B) Brüssel<br>C) Luxemburg<br>D) Straßburg'''', ''''B) Brüssel'''', ''''B) Brüssel'''', 235, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (236, ''''Wie viele Mitglieder hat der Rat der Europäischen Union?'''', ''''Der Rat der Europäischen Union setzt sich aus den jeweiligen Fachministern der nationalen Regierungen der Mitgliedstaaten zusammen. Die Anzahl der Mitglieder variiert je nach Politikfeld, da unterschiedliche Minister an den Sitzungen teilnehmen.'''', ''''Die Anzahl der Mitglieder variiert je nach Politikfeld.'''', 236, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (237, ''''Wie viele Mitglieder hat das Europäische Parlament nach der Europawahl 2014?'''', ''''Das Europäische Parlament hat nach der Europawahl 2014 751 Mitglieder.'''', ''''751 Mitglieder'''', 237, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (238, ''''Welche Funktionen hat die Europäische Kommission in der Europäischen Union?'''', ''''Die Europäische Kommission hat vornehmlich exekutive Funktionen und entspricht damit der „Regierung“ der EU. Sie hat nahezu das alleinige Initiativrecht in der EU-Rechtsetzung und schlägt Rechtsakte vor. Als Exekutivorgan sorgt sie für die korrekte Ausführung der europäischen Rechtsakte, die Umsetzung des Haushalts und der beschlossenen Programme. Sie überwacht die Einhaltung des Europarechts und erstattet gegebenenfalls Klage vor den Gerichten der Europäischen Union.'''', ''''Die Europäische Kommission hat exekutive Funktionen, initiiert Rechtsakte, sorgt für die Ausführung von Rechtsakten, überwacht die Einhaltung des Europarechts und vertritt die EU auf internationaler Ebene.'''', 238, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (239, ''''Wie viele Kommissare gehören der Europäischen Kommission an?'''', ''''Die Europäische Kommission besteht aus 27 Kommissaren, von denen je einer aus jedem Mitgliedstaat kommt.'''', ''''27'''', 239, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (240, ''''Wie viele Vizepräsidenten hat die Europäische Kommission?'''', ''''Die Europäische Kommission hat insgesamt acht Vizepräsidenten, davon drei geschäftsführende Vizepräsidenten und fünf weitere Vizepräsidenten.'''', ''''Acht Vizepräsidenten'''', 240, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (241, ''''Wer ist die Präsidentin der Europäischen Kommission?<br>A) Frans Timmermans<br>B) Ursula von der Leyen<br>C) Margrethe Vestager<br>D) Valdis Dombrovskis'''', ''''B) Ursula von der Leyen'''', ''''Ursula von der Leyen'''', 241, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (242, ''''Wer ist der EU-Kommissar für Justiz und Rechtsstaatlichkeit?<br>A) Didier Reynders<br>B) Marija Gabriel<br>C) Stella Kyriakides<br>D) Kadri Simson'''', ''''A) Didier Reynders'''', ''''A) Didier Reynders'''', 242, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (243, ''''Wie viele Generalanwälte unterstützen den Europäischen Gerichtshof?'''', ''''Der Europäische Gerichtshof wird von mindestens acht Generalanwälten unterstützt.'''', ''''Mindestens 8 Generalanwälte'''', 243, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (244, ''''Welche der folgenden Aussagen über die Europäische Zentralbank ist korrekt?<br>A) Die EZB wird vom Europäischen Parlament kontrolliert.<br>B) Die EZB ist politisch unabhängig und unterliegt nur den im AEU-Vertrag festgelegten Zielen der Währungspolitik.<br>C) Die EZB ist nur für die Wirtschaftspolitik der Euro-Länder zuständig.<br>D) Die EZB wird von der Europäischen Kommission geleitet.'''', ''''B) Die EZB ist politisch unabhängig und unterliegt nur den im AEU-Vertrag festgelegten Zielen der Währungspolitik.'''', ''''B) Die EZB ist politisch unabhängig.'''', 244, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (245, ''''Wie viele Mitglieder des Ausschusses der Regionen (AdR) stammen aus Deutschland?'''', ''''Aus Deutschland stammen 24 Mitglieder des Ausschusses der Regionen (AdR), davon werden 21 von den Bundesländern und drei von den Kommunen vorgeschlagen.'''', ''''24 Mitglieder'''', 245, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (246, ''''Welche Funktion hat der Europäische Bürgerbeauftragte?'''', ''''Der Europäische Bürgerbeauftragte ist der Ombudsmann der Europäischen Union und untersucht Beschwerden über Missstände in der Verwaltungstätigkeit ihrer Organe, Einrichtungen und sonstigen Stellen.'''', ''''Der Europäische Bürgerbeauftragte ist der Ombudsmann der EU und untersucht Beschwerden über Missstände in der Verwaltung.'''', 246, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (247, ''''Welche der folgenden Rechte gehören zu den Rechten der Unionsbürger?<br>A) Wahlrecht zum Deutschen Bundestag<br>B) Kommunalwahlrecht am Wohnort<br>C) Recht auf Sozialhilfe in anderen Mitgliedstaaten<br>D) Recht auf eine deutsche Staatsangehörigkeit'''', ''''B) Kommunalwahlrecht am Wohnort'''', ''''B) Kommunalwahlrecht am Wohnort'''', 247, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (248, ''''Wie werden die Beiträge der Mitgliedstaaten zur Finanzierung der Europäischen Union berechnet?<br>A) Nur durch Import-Zölle an den Außengrenzen.<br>B) Durch einen Anteil der Umsatzsteuer und Beiträge, die sich proportional aus dem Bruttonationaleinkommen ergeben.<br>C) Durch eine feste Summe, die von jedem Mitgliedstaat gezahlt wird.<br>D) Durch eine freiwillige Zuwendung der Mitgliedstaaten.'''', ''''B) Durch einen Anteil der Umsatzsteuer und Beiträge, die sich proportional aus dem Bruttonationaleinkommen ergeben.'''', ''''B) Durch einen Anteil der Umsatzsteuer und Beiträge, die sich proportional aus dem Bruttonationaleinkommen ergeben.'''', 248, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (249, ''''Wie viel Prozent des Bruttonationaleinkommens aller Mitgliedstaaten entspricht der Mehrjährige Finanzrahmen der EU für die Jahre 2007-2013?<br>A) 0,5%<br>B) 1,24%<br>C) 2,5%<br>D) 5%'''', ''''B) 1,24%'''', ''''1,24%'''', 249, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (250, ''''Welcher Anteil der Gesamtmittel im Mehrjährigen Finanzrahmen 2014-2020 ist für die Gemeinsame Agrarpolitik vorgesehen?<br>A) 13%<br>B) 34%<br>C) 39%<br>D) 6%'''', ''''C) 39%'''', ''''39%'''', 250, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (251, ''''Was ist das Subsidiaritätsprinzip in der Europäischen Union?'''', ''''Das Subsidiaritätsprinzip besagt, dass die Europäische Union in den Bereichen, die nicht in ihre ausschließliche Zuständigkeit fallen, nur tätig wird, sofern und soweit die verfolgten Ziele auf Unionsebene besser verwirklicht werden können als auf der Ebene der Mitgliedstaaten.'''', ''''Die EU ist nur tätig, wenn Ziele besser auf Unionsebene erreicht werden können als auf nationaler Ebene.'''', 251, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (252, ''''Welche der folgenden Zuständigkeiten hat die Europäische Union ausschließlich?<br>A) Europäischer Binnenmarkt<br>B) Sozialpolitik<br>C) Europäische Zollunion<br>D) Umweltpolitik'''', ''''C) Europäische Zollunion'''', ''''C) Europäische Zollunion'''', 252, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (253, ''''Welche der folgenden Politikbereiche fällt unter die Zuständigkeit der EU nach dem EU-Vertrag?<br>A) Bildungspolitik<br>B) Gesundheitspolitik<br>C) Verteidigungspolitik<br>D) Alle der oben genannten'''', ''''D) Alle der oben genannten. Laut dem EU-Vertrag fallen unter die Zuständigkeit der EU sowohl die Bildungspolitik als auch die Gesundheitspolitik als auch die Verteidigungspolitik.'''', ''''D) Alle der oben genannten'''', 253, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (254, ''''Welche Rolle spielt die Europäische Kommission bei der Gewährleistung eines fairen Wettbewerbs auf dem Binnenmarkt?'''', ''''Die Europäische Kommission spielt eine wichtige Rolle bei der Gewährleistung eines fairen Wettbewerbs auf dem Binnenmarkt. Der Wettbewerbskommissar der Europäischen Kommission ist für die Kontrolle der Wirtschaft und die Genehmigung von Subventionen in den Mitgliedstaaten zuständig. Er soll verhindern, dass einzelne Staaten nationale Unternehmen zum Schaden von Wettbewerbern aus dem Rest der EU unterstützen.'''', ''''Die Europäische Kommission kontrolliert die Wirtschaft und genehmigt Subventionen, um fairen Wettbewerb auf dem Binnenmarkt zu gewährleisten.'''', 254, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (255, ''''Wie fördert die EU die Zusammenarbeit zwischen Unternehmen?'''', ''''Die EU fördert die Zusammenarbeit zwischen Unternehmen, insbesondere kleinen und mittleren Unternehmen, bei der Forschung und Entwicklung innovativer Produkte für Wachstumsmärkte.'''', ''''Durch Förderung der Kooperation bei Forschung und Entwicklung innovativer Produkte.'''', 255, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (256, ''''Was war das Hauptziel des EWG-Vertrags von 1957?'''', ''''Das Hauptziel des EWG-Vertrags von 1957 war es, Handelshemmnisse zwischen den Mitgliedstaaten abzubauen, indem die vier Grundfreiheiten des freien Verkehrs von Waren, Kapital, Dienstleistungen und Arbeitskräften im Gebiet der Gemeinschaft schrittweise eingeführt wurden.'''', ''''Das Hauptziel des EWG-Vertrags von 1957 war die Beseitigung von Handelshemmnissen zwischen den Mitgliedstaaten.'''', 256, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (257, ''''Welche Länder gehören der Europäischen Zollunion an?'''', ''''Die Europäische Zollunion besteht aus der EU (dunkelblau) und den Partnerstaaten Türkei, Andorra und San Marino (lila). Mit den EWR-Staaten Island, Liechtenstein und Norwegen besteht eine Freihandelszone.'''', ''''Die EU, Türkei, Andorra, San Marino, Island, Liechtenstein und Norwegen.'''', 257, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (258, ''''Was war die Folge der Rechtsprechung des Europäischen Gerichtshofs zur Warenverkehrsfreiheit?'''', ''''Die Rechtsprechung des Europäischen Gerichtshofs zur Warenverkehrsfreiheit führte dazu, dass warenbezogene Vorschriften der Mitgliedstaaten, die den Warenhandel zwischen den Mitgliedstaaten erschweren, als unzulässig gelten. Dies bedeutete, dass die Mitgliedstaaten ihre Rechts- und Verwaltungsvorschriften anpassen mussten, um einheitliche Standards im Binnenmarkt zu schaffen.'''', ''''Die Rechtsprechung des EuGH führte zur Anpassung von Rechts- und Verwaltungsvorschriften, um einheitliche Standards im Binnenmarkt zu schaffen.'''', 258, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (259, ''''Welche Aufgaben hat der Wettbewerbskommissar der Europäischen Kommission?'''', ''''Der Wettbewerbskommissar der Europäischen Kommission unterstützt die Kartellbehörden der einzelnen Staaten, um wirtschaftliche Kartelle und Monopole in der EU zu verhindern und einen fairen Wettbewerb auf dem Binnenmarkt sicherzustellen. Er ist auch für die Genehmigung von Subventionen in den Mitgliedstaaten zuständig, um zu verhindern, dass einzelne Staaten bestimmte Firmen wettbewerbswidrig unterstützen.'''', ''''Der Wettbewerbskommissar unterstützt die Kartellbehörden, verhindert Kartelle und Monopole und genehmigt Subventionen.'''', 259, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (260, ''''Was ist das Ziel der Europäischen Dienstleistungsrichtlinie?'''', ''''Das Ziel der Europäischen Dienstleistungsrichtlinie ist die Förderung des grenzüberschreitenden Handels mit Dienstleistungen innerhalb der EU. Sie soll bestimmte Erleichterungen für niedergelassene Dienstleister schaffen, wie die Schaffung einheitlicher Ansprechpartner und einer elektronischen Verfahrensabwicklung.'''', ''''Die Förderung des grenzüberschreitenden Handels mit Dienstleistungen innerhalb der EU.'''', 260, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (261, ''''Wie viele Mitgliedstaaten gehören derzeit der Eurozone an?'''', ''''Derzeit gehören 20 Mitgliedstaaten der Eurozone an.'''', ''''20 Mitgliedstaaten'''', 261, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (262, ''''Welche Institution koordiniert die Wirtschafts- und Finanzpolitik der Mitgliedstaaten in der Eurozone?'''', ''''Die Koordination der Wirtschafts- und Finanzpolitik der Mitgliedstaaten in der Eurozone übernimmt die Eurogruppe, in der sich die Finanzminister der Eurozone treffen.'''', ''''Die Eurogruppe'''', 262, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (263, ''''Was ist das Hauptziel der Gemeinsamen Handelspolitik der EU?'''', ''''Das Hauptziel der Gemeinsamen Handelspolitik der EU ist der weltweite Freihandel. Die EU kann jedoch auch Schutzinstrumente tarifärer und nicht-tarifärer Art einsetzen, um wirtschaftliche Gefahren abzuwehren.'''', ''''Der weltweite Freihandel'''', 263, 11);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (264, ''''Was war das Hauptziel der Gemeinsamen Agrarpolitik der EU?'''', ''''Das Hauptziel der Gemeinsamen Agrarpolitik der EU war es, die landwirtschaftliche Produktivität zu erhöhen und Preisschwankungen zu vermeiden, um den Produzenten eine gut auskömmliche Lebenshaltung und den Verbrauchern eine stabile Versorgung zu angemessenen Preisen zu sichern.'''', ''''Das Hauptziel war die Erhöhung der landwirtschaftlichen Produktivität und die Vermeidung von Preisschwankungen.'''', 264, 12);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (265, ''''Was war das Hauptziel der MacSharry-Reform von 1992 in der Gemeinsamen Agrarpolitik?'''', ''''Das Hauptziel der MacSharry-Reform von 1992 war die Senkung der Agrarpreise, die Ausgleichszahlungen für die entstandenen Einkommensverluste, die Förderung von Marktmechanismen, die Umsetzung von Maßnahmen des Umweltschutzes und die schrittweise Senkung der Exporterstattungen.'''', ''''Senkung der Agrarpreise und Förderung von Marktmechanismen.'''', 265, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (266, ''''Welche Ziele verfolgt die Regionalpolitik der Europäischen Union?'''', ''''Die Regionalpolitik der Europäischen Union zielt darauf ab, Unterschiede im Entwicklungsstand der Gebiete anzupassen und regionale Disparitäten zurückzudrängen, indem benachteiligten Regionen eine spezielle Förderung gewährt wird. Dies soll den wirtschaftlichen Aufholprozess der ärmeren Regionen fördern und dauerhafte Arbeitsplätze schaffen.'''', ''''Die Regionalpolitik der EU zielt auf die Angleichung von Entwicklungsstand und die Verringerung regionaler Disparitäten ab.'''', 266, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (267, ''''Welches der folgenden Ziele des EFRE betrifft Regionen, deren Bruttoinlandsprodukt pro Einwohner unter 75 % des EU-Durchschnitts liegt?<br>A) Regionale Wettbewerbsfähigkeit und Beschäftigung<br>B) Konvergenz<br>C) Europäische territoriale Zusammenarbeit<br>D) Verbesserung der Bildungssysteme'''', ''''B) Konvergenz'''', ''''B) Konvergenz'''', 267, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (268, ''''Welche Länder sind förderfähig im Rahmen des Kohäsionsfonds der EU?'''', ''''Förderfähig im Rahmen des Kohäsionsfonds der EU sind Mitgliedstaaten, deren Bruttoinlandsprodukt pro Kopf unter 90 % des EU-Durchschnitts liegt. Dies sind Griechenland, Portugal, Spanien, die Republik Zypern, die Tschechische Republik, Estland, Ungarn, Lettland, Litauen, Malta, Polen, die Slowakei und Slowenien.'''', ''''Länder mit einem Bruttoinlandsprodukt pro Kopf unter 90 % des EU-Durchschnitts, z.B. Griechenland, Portugal, Spanien, etc.'''', 268, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (269, ''''Was ist das Hauptziel der Gemeinsamen Außen- und Sicherheitspolitik (GASP) der EU?'''', ''''Das Hauptziel der Gemeinsamen Außen- und Sicherheitspolitik (GASP) der EU ist die Wahrung der gemeinsamen Werte und Interessen der Union, die Stärkung der Sicherheit und des Friedens, die Förderung der internationalen Zusammenarbeit und die Stärkung von Demokratie, Rechtsstaatlichkeit und Menschenrechten.'''', ''''Die GASP zielt auf die Wahrung gemeinsamer Werte und Interessen, Sicherheit, Frieden, internationale Zusammenarbeit und die Stärkung von Demokratie, Rechtsstaatlichkeit und Menschenrechten ab.'''', 269, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (270, ''''Wer ist für die praktische Verhandlungs- und Koordinierungsarbeit in der GASP verantwortlich?'''', ''''Der Hohe Vertreter für Außen- und Sicherheitspolitik ist für die praktische Verhandlungs- und Koordinierungsarbeit in der GASP verantwortlich. Er ist zugleich Vizepräsident der Europäischen Kommission und (nicht stimmberechtigter) Vorsitzender im Rat für Auswärtige Angelegenheiten.'''', ''''Der Hohe Vertreter für Außen- und Sicherheitspolitik'''', 270, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (271, ''''Was ist das Hauptziel der Europäischen Verteidigungsagentur?'''', ''''Das Hauptziel der Europäischen Verteidigungsagentur ist es, zur Ermittlung von Maßnahmen zur Stärkung der industriellen und technologischen Basis des Verteidigungssektors beizutragen.'''', ''''Stärkung der industriellen und technologischen Basis des Verteidigungssektors'''', 271, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (272, ''''Was sind die Hauptziele der Gemeinsamen Sicherheits- und Verteidigungspolitik (GSVP) der Europäischen Union?'''', ''''Die Hauptziele der GSVP sind die Erfüllung der Petersberg-Aufgaben, nämlich humanitäre Aufgaben und Rettungseinsätze, friedenserhaltende Aufgaben und Kampfeinsätze bei der Krisenbewältigung inklusive Frieden schaffender Maßnahmen. Darüber hinaus könnte die GSVP auch zu einer gemeinsamen europäischen Verteidigung, also einer Europaarmee, führen.'''', ''''Die Hauptziele der GSVP sind humanitäre Aufgaben, Rettungseinsätze, friedenserhaltende Aufgaben und Kampfeinsätze bei der Krisenbewältigung.'''', 272, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (273, ''''Was ist das Hauptziel der Europäischen Nachbarschaftspolitik?'''', ''''Das Hauptziel der Europäischen Nachbarschaftspolitik ist die Stärkung von Demokratie und Rechtsstaatlichkeit in den unmittelbaren Nachbarländern der EU, sowie die wirtschaftliche Zusammenarbeit mit diesen Ländern.'''', ''''Stärkung von Demokratie und Rechtsstaatlichkeit und wirtschaftliche Zusammenarbeit.'''', 273, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (274, ''''Welche Rolle spielt die Europäische Investitionsbank in der Entwicklungspolitik der EU?'''', ''''Die Europäische Investitionsbank trägt zur Entwicklungspolitik der EU bei, indem sie gemeinsam mit dem Europäischen Entwicklungsfonds den Großteil der finanziellen Mittel bereitstellt.'''', ''''Die Europäische Investitionsbank stellt finanzielle Mittel bereit.'''', 274, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (275, ''''Was war das Ziel des Vertrags von Amsterdam 1997 in Bezug auf die Justiz- und Innenpolitik?'''', ''''Das Ziel des Vertrags von Amsterdam 1997 war die Einführung eines europaweiten Raums der Freiheit, der Sicherheit und des Rechts, der die Abschaffung der Personenkontrollen an den Binnengrenzen, die justizielle Zusammenarbeit in Zivil- und Strafsachen und die polizeiliche Zusammenarbeit zur Bekämpfung des Terrorismus und sonstiger schwerwiegender Formen der internationalen Kriminalität umfasst.'''', ''''Ein europaweiter Raum der Freiheit, der Sicherheit und des Rechts.'''', 275, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (276, ''''Welche europäische Behörde ist für die Koordinierung der Zusammenarbeit der nationalen Polizei- und Justizbehörden verantwortlich?'''', ''''Die europäische Behörde, die für die Koordinierung der Zusammenarbeit der nationalen Polizei- und Justizbehörden verantwortlich ist, ist Europol.'''', ''''Europol'''', 276, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (277, ''''Was ist das Ziel des Bologna-Prozesses in der europäischen Bildungspolitik?'''', ''''Der Bologna-Prozess zielt darauf ab, einen Europäischen Hochschulraum zu schaffen, indem er ein zweistufiges System von Studienabschlüssen einführt, das in Deutschland nach dem angelsächsischen Vorbild Bachelor und Master genannt wurde. Dieses System soll die Mobilität von Studierenden und die Vergleichbarkeit von Studienabschlüssen in Europa fördern.'''', ''''Der Bologna-Prozess zielt auf die Schaffung eines Europäischen Hochschulraumes durch ein zweistufiges Studiensystem.'''', 277, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (278, ''''Was ist das Hauptziel des Europäischen Qualifikationsrahmens (EQF)?'''', ''''Das Hauptziel des Europäischen Qualifikationsrahmens (EQF) ist es, Bildungsabschlüsse international besser vergleichbar zu machen, indem festgelegte Kompetenzen bestimmten Niveaustufen zugeordnet werden.'''', ''''Vergleichbarkeit von Bildungsabschlüssen'''', 278, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (279, ''''Welches Programm koordiniert seit 2014 die europäischen Bildungsprogramme?<br>A) Erasmus<br>B) Comenius<br>C) Leonardo<br>D) Erasmus+'''', ''''D) Erasmus+'''', ''''D) Erasmus+'''', 279, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (280, ''''Was ist der Hauptzweck des Europäischen Forschungsrats?'''', ''''Der Hauptzweck des Europäischen Forschungsrats ist es, die Grundlagenforschung zu unterstützen, indem er unabhängig von politischer Einflussnahme Projektmittel an Forschungsprojekte vergibt, die nach Exzellenzkriterien ausgewählt werden. Ziel ist es, die EU als Forschungsstandort attraktiver zu machen, herausragende Wissenschaftstalente zu identifizieren und personelle Lücken in der Spitzenforschung aufzufüllen.'''', ''''Der Europäische Forschungsrat unterstützt die Grundlagenforschung durch unabhängige Vergabe von Forschungsmitteln nach Exzellenzkriterien.'''', 280, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (281, ''''Welche Rolle spielt der Europäische Sozialfonds in der Sozialpolitik der EU?'''', ''''Der Europäische Sozialfonds spielt eine wichtige Rolle in der Sozialpolitik der EU, indem er finanzielle Mittel für Maßnahmen zur Berufsbildung, Umschulung, zur Bekämpfung der Jugendarbeitslosigkeit und zur Wiedereingliederung von Arbeitslosen bereitstellt.'''', ''''Der Europäische Sozialfonds unterstützt Maßnahmen zur Berufsbildung, Umschulung und Bekämpfung von Arbeitslosigkeit.'''', 281, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (282, ''''Was ist das Ziel der Beschäftigungspolitik der EU nach dem Vertrag von Amsterdam?'''', ''''Das Ziel der Beschäftigungspolitik der EU nach dem Vertrag von Amsterdam ist es, eine zwischen der EU und den Mitgliedstaaten koordinierte Strategie zu entwickeln, die auf bessere Qualifizierung der Arbeitsuchenden und auf Arbeitsmarktflexibilität gerichtet ist.'''', ''''Bessere Qualifizierung und Arbeitsmarktflexibilität'''', 282, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (283, ''''Was ist ein wichtiges Ziel des Verbraucherschutzes in der Europäischen Union?'''', ''''Ein wichtiges Ziel des Verbraucherschutzes in der Europäischen Union ist der Gesundheitsschutz der Verbraucher. Dazu gehören die Sicherstellung einheitlicher Qualitätsstandards in Produktion und Handel, die Aufklärung und Information der Verbraucher sowie die Kennzeichnung genmanipulierter Produkte.'''', ''''Gesundheitsschutz der Verbraucher'''', 283, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (284, ''''Was ist wichtig, um über geeignete Maßnahmen des Klimaschutzes zu entscheiden?'''', ''''Wissenschaftliche Erkenntnisse über die Prozesse im Klimasystem und die Auswirkungen des Klimawandels sind wichtig, um über geeignete Maßnahmen des Klimaschutzes zu entscheiden.'''', ''''Wissenschaftliche Erkenntnisse über das Klimasystem und den Klimawandel.'''', 284, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (285, ''''Was ist die Hauptursache des Klimawandels, die nach dem 6. Sachstandsbericht des Weltklimarates verantwortlich ist?'''', ''''Die Hauptursache des Klimawandels, die nach dem 6. Sachstandsbericht des Weltklimarates verantwortlich ist, ist die Freisetzung von Treibhausgasen durch menschliche Aktivitäten.'''', ''''Die Freisetzung von Treibhausgasen durch menschliche Aktivitäten.'''', 285, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (286, ''''Was ist der Hauptgrund für den Klimawandel?'''', ''''Der Hauptgrund für den Klimawandel ist die Emission von Treibhausgasen, insbesondere durch die Verbrennung von fossilen Energieträgern. Dieser menschengemachte Einfluss hat die Atmosphäre, den Ozean und die Landflächen erwärmt und führt zu weitverbreiteten und schnellen Veränderungen im gesamten Klimasystem.'''', ''''Der Hauptgrund für den Klimawandel ist die Emission von Treibhausgasen durch die Verbrennung von fossilen Energieträgern.'''', 286, 14);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (287, ''''Was versteht man unter dem ''''''''Klimasystem'''''''' der Erde?'''', ''''Das Klimasystem der Erde ist ein komplexes Zusammenwirken verschiedener Subsysteme, wie der Atmosphäre, der Hydrosphäre (Ozeane, Seen, Flüsse), der Kryosphäre (Eis und Schnee), der Lithosphäre (Böden und Gesteinsschichten) und der Biosphäre (Lebewesen an Land und im Meer). Diese Subsysteme stehen miteinander in Wechselwirkung und tauschen ständig untereinander Energie aus.'''', ''''Das Klimasystem der Erde ist ein komplexes System aus Atmosphäre, Hydrosphäre, Kryosphäre, Lithosphäre und Biosphäre, die miteinander in Wechselwirkung stehen.'''', 287, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (288, ''''Was ist das Klima und wie wird es definiert?'''', ''''Das Klima ist der mittlere Zustand der Atmosphäre an einem bestimmten Ort oder in einem bestimmten Gebiet über einen längeren Zeitraum. Es wird durch statistische Eigenschaften der Atmosphäre charakterisiert, wie Mittelwerte, Häufigkeiten, Andauer und Extremwerte meteorologischer Größen wie Luftdruck, Temperatur, Bewölkung, Niederschlag, Wind und Verdunstung.'''', ''''Das Klima ist der mittlere Zustand der Atmosphäre über einen längeren Zeitraum, definiert durch statistische Eigenschaften wie Mittelwerte und Häufigkeiten meteorologischer Größen.'''', 288, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (289, ''''Was ist der Motor des Klimas der Erde?'''', ''''Der Motor des Klimas der Erde ist die Strahlung der Sonne. Die Sonnenstrahlung wird auf der Erde ankommend reflektiert, absorbiert und in Wärmestrahlung umgesetzt. Die Energiebilanz zwischen ankommender und abgehender Strahlung bestimmt das Klima.'''', ''''Die Strahlung der Sonne.'''', 289, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (290, ''''Welche Faktoren beeinflussen das Klima auf der Erde?'''', ''''Das Klima auf der Erde wird von verschiedenen Faktoren beeinflusst, wie der Sonnenstrahlung, Aerosolen in der Atmosphäre, der Beschaffenheit der Erdoberfläche, Treibhausgasen und internen Klimaschwankungen. Die Sonnenstrahlung schwankt in langen und kurzen Zeiträumen und beeinflusst das Klima. Aerosole, wie zum Beispiel Vulkanausbrüche, tragen zur Reflektion der Sonnenstrahlung bei. Die Erdoberfläche beeinflusst die Reflektion der Sonnenstrahlung, und der Mensch verändert die Landoberfläche durch seine Tätigkeit. Treibhausgase absorbieren Wärmestrahlung und haben sich seit der Industrialisierung erhöht, was zu einer Erwärmung des Klimas geführt hat. Interne Klimaschwankungen entstehen durch Wechselwirkungen in und zwischen den Subsystemen des Klimasystems.'''', ''''Das Klima wird von Sonnenstrahlung, Aerosolen, Erdoberfläche, Treibhausgasen und internen Klimaschwankungen beeinflusst.'''', 290, 13);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (291, ''''Was ist das El-Niño-Phänomen und welche Auswirkungen hat es auf die atmosphärische Zirkulation?'''', ''''Das El-Niño-Phänomen ist ein Ereignis, bei dem die Oberflächentemperaturen des Ozeans in einem großen Gebiet im tropischen Pazifik ungewöhnlich erhöht sind. Diese Änderung der Meeresoberflächentemperaturen hat Folgen für die atmosphärische Zirkulation: In Regionen Südamerikas, in denen sonst Trockenheit herrscht, kann es zu starken Niederschlägen mit Überschwemmungen kommen, während in Indonesien und Australien Dürren auftreten, die teils verheerende Wald- und Buschbrände nach sich ziehen.'''', ''''El Niño ist ein Ereignis, bei dem die Oberflächentemperaturen des Ozeans im tropischen Pazifik erhöht sind, was zu Niederschlägen in Südamerika und Dürren in Indonesien und Australien führt.'''', 291, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (292, ''''Wie lange dauert es, bis CO2 durch geologische Prozesse in der Erdkruste gespeichert wird?'''', ''''Die längsten Zeiträume zur Aufnahme von CO2 und zur Speicherung von Kohlenstoff durch geologische Prozesse wie die Einlagerung von Sedimenten in die Erdkruste oder die Reaktionen mit Gesteinen, die beispielsweise bei der Verwitterung ablaufen, benötigen zehntausende bis hunderttausende von Jahren.'''', ''''Zehntausende bis hunderttausende von Jahren'''', 292, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (293, ''''Was sind die Folgen der Erwärmung des Klimas auf die Atmosphäre und den Ozean?'''', ''''Die Erwärmung des Klimas führt zu einer Zunahme von Extremwetterereignissen wie Hitzewellen, Starkniederschlägen und Dürren in der Atmosphäre. Die weltweite Mitteltemperatur in Bodennähe ist um etwa 1,09 Grad Celsius höher als in der Referenzperiode 1850 bis 1900. Im Ozean hat sich die Erwärmung der Wassermassen an der Meeresoberfläche seit 1970 beobachten lassen, was zu einer thermischen Ausdehnung des Meerwassers führt.'''', ''''Die Erwärmung des Klimas führt zu Extremwetterereignissen in der Atmosphäre und einer Erwärmung der Ozeane, die zu einer thermischen Ausdehnung des Meerwassers führt.'''', 293, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (294, ''''Wie viel ist der mittlere globale Meeresspiegel zwischen 1901 und 2018 gestiegen?'''', ''''Der mittlere globale Meeresspiegel ist zwischen 1901 und 2018 um etwa 20 Zentimeter gestiegen.'''', ''''20 Zentimeter'''', 294, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (295, ''''Wie viel hat die mittlere jährliche Ausdehnung des arktischen Meereises im Zeitraum 2011 bis 2020 im Vergleich zu früheren Jahren abgenommen?'''', ''''Die mittlere jährliche Ausdehnung des arktischen Meereises im Zeitraum 2011 bis 2020 war die geringste seit mindestens 1850. Im Zeitraum 1979 bis 1992 betrug die Ausdehnung des arktischen Meereises rund 6,1 Millionen Quadratkilometer, während sie im Zeitraum 2007 bis 2020 nur noch bei rund 4,4 Millionen Quadratkilometern lag.'''', ''''Die mittlere jährliche Ausdehnung des arktischen Meereises im Zeitraum 2011 bis 2020 war die geringste seit mindestens 1850 und hat sich im Vergleich zu früheren Jahren um etwa 1,7 Millionen Quadratkilometer verringert.'''', 295, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (296, ''''Was sind Kipp-Punkte im Klimasystem und welche Auswirkungen können sie haben?'''', ''''Kipp-Punkte im Klimasystem sind kritische Schwellen, die bei bestimmten Größenordnungen der globalen Erwärmung erreicht werden können. An diesen Punkten können starke oder sogar abrupte Klimaänderungen einschließlich irreversibler Prozesse einsetzen. Beispiele für solche Kipp-Punkte sind das Schmelzen des Meereises und die Abnahme der Albedo in der Arktis, das Schmelzen des Grönländischen Eisschildes oder andere Prozesse, die zu gravierenden Auswirkungen auf das Klima führen können.'''', ''''Kipp-Punkte sind kritische Schwellen im Klimasystem, die zu abrupten Klimaänderungen und irreversiblen Prozessen führen können.'''', 296, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (297, ''''Was sind einige Möglichkeiten, um persönlich zum Klimaschutz beizutragen?'''', ''''Einzelne können zum Klimaschutz beitragen, indem sie lernen, über die Klimakrise sprechen und handeln. Dazu gehören die Aneignung von Wissen über grundlegende Zusammenhänge, die Kommunikation über die Klimakrise, das Engagement in zivilgesellschaftlichen Aktionen, die Änderung des Konsumverhaltens und die Reduzierung von CO2-Emissionen durch die Wahl von umweltfreundlichen Verkehrsmitteln.'''', ''''Zum Klimaschutz kann man beitragen, indem man lernt, kommuniziert, engagiert ist, nachhaltig konsumiert und umweltfreundliche Verkehrsmittel wählt.'''', 297, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (298, ''''Wie können wir unseren CO2-Ausstoß bei der Ernährung reduzieren?'''', ''''Wir können unseren CO2-Ausstoß bei der Ernährung reduzieren, indem wir tierische Produkte wie Fleisch, Käse oder Butter reduzieren oder durch Bioprodukte ersetzen. Allein durch die Wahl von Bioprodukten lassen sich knapp 20 Prozent der CO2-Emissionen einsparen.'''', ''''Durch die Reduzierung von tierischen Produkten und die Wahl von Bioprodukten können wir unseren CO2-Ausstoß reduzieren.'''', 298, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (299, ''''Wie beeinflussen internationale Wirtschaftsbeziehungen die Umwelt?'''', ''''Internationale Wirtschaftsbeziehungen haben einen bedeutenden Einfluss auf die Umwelt, da sie von natürlichen Ressourcen abhängen und bei Produktion und Transport Schadstoffe und Treibhausgase entstehen. Der weltweite Handel mit Waren und Dienstleistungen führt zu umweltschädlichen Auswirkungen, die den ganzen Globus betreffen.'''', ''''Internationale Wirtschaftsbeziehungen beeinflussen die Umwelt durch den Einsatz natürlicher Ressourcen und die Entstehung von Schadstoffen und Treibhausgasen bei Produktion und Transport.'''', 299, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (300, ''''Was ist ein wichtiger Zusammenhang zwischen wirtschaftlicher Entwicklung und Umwelt- und Klimaschutz?'''', ''''Ein wichtiger Zusammenhang zwischen wirtschaftlicher Entwicklung und Umwelt- und Klimaschutz ist, dass die heutige, oft noch nicht nachhaltige Wirtschaftsweise die natürlichen Lebensgrundlagen beeinträchtigt oder gar zerstört und dass Veränderungen nötig sind, um den Klimaschutz und den Umweltschutz zu fördern.'''', ''''Der Zusammenhang zwischen wirtschaftlicher Entwicklung und Umwelt- und Klimaschutz liegt darin, dass die heutige Wirtschaftsweise die Umwelt schädigt und dass Veränderungen nötig sind, um den Klimaschutz und den Umweltschutz zu fördern.'''', 300, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (301, ''''Was bedeutet der Begriff ''''''''level playing field'''''''' in der Politik?'''', ''''Der Begriff ''''''''level playing field'''''''' bedeutet in der Politik, dass für alle einheitliche Regeln gelten, denen sich niemand entziehen kann. Dies schafft gleiche Wettbewerbsbedingungen für alle Unternehmen, insbesondere im globalen Kontext.'''', ''''Einheitliche Regeln für alle, um gleiche Wettbewerbsbedingungen zu schaffen.'''', 301, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (302, ''''Wie wirken sich Produktionsprozesse auf die Umwelt und das Klima aus?'''', ''''Produktionsprozesse können sich schädlich auf die Umwelt und das Klima auswirken, indem sie Treibhausgasemissionen und Luftschadstoffe freisetzen. Die meisten Emissionen stammen aus der Metallindustrie, zum Beispiel aus der Stahlproduktion, aus der Produktion von mineralischen Stoffen wie Zement sowie aus der chemischen Industrie.'''', ''''Produktionsprozesse setzen Treibhausgasemissionen und Luftschadstoffe frei, was sich negativ auf die Umwelt und das Klima auswirkt.'''', 302, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (303, ''''Wie beeinflussen internationale Wirtschaftsbeziehungen die Umwelt?'''', ''''Internationale Wirtschaftsbeziehungen haben einen bedeutenden Einfluss auf die Umwelt. Durch Produktion und Transport von Waren und Dienstleistungen entstehen Schadstoffe und Treibhausgase, die das Klima und die Umwelt schädigen. Der Handel mit Waren und Dienstleistungen ist weltweit vernetzt, somit umspannen auch die Auswirkungen auf die Umwelt den ganzen Globus.'''', ''''Internationale Wirtschaftsbeziehungen beeinflussen die Umwelt durch Schadstoffe und Treibhausgase, die bei Produktion und Transport entstehen.'''', 303, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (304, ''''Was ist ein wichtiger Zusammenhang zwischen wirtschaftlicher Entwicklung und Umwelt- und Klimaschutz?'''', ''''Ein wichtiger Zusammenhang zwischen wirtschaftlicher Entwicklung und Umwelt- und Klimaschutz ist, dass die heutige, oft nicht nachhaltige Wirtschaftsweise die natürlichen Lebensgrundlagen beeinträchtigt oder gar zerstört und dass Veränderungen nötig sind, um den Klimaschutz zu fördern. Dieser Zusammenhang spielt insbesondere bei Diskussionen über internationale Wirtschaftsbeziehungen und Umweltschutz eine Rolle.'''', ''''Der Zusammenhang zwischen wirtschaftlicher Entwicklung und Umwelt- und Klimaschutz liegt darin, dass die nicht nachhaltige Wirtschaftsweise die Umwelt beeinträchtigt und Veränderungen nötig sind.'''', 304, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (305, ''''Was bedeutet der Begriff ''''''''level playing field'''''''' in der Politik?'''', ''''Der Begriff ''''''''level playing field'''''''' bedeutet in der Politik, dass für alle einheitliche Regeln gelten, denen sich niemand entziehen kann. Das schafft gleiche Wettbewerbsbedingungen für alle. Es ist wichtig, Umweltstandards in Staatengemeinschaften festzulegen oder in internationale Verträge einfließen zu lassen, um einen fairen Wettbewerb zwischen Unternehmen zu gewährleisten.'''', ''''Ein ''''''''level playing field'''''''' bedeutet, dass alle Unternehmen gleiche Regeln und Bedingungen haben, um fairen Wettbewerb zu ermöglichen.'''', 305, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (306, ''''Wie wirken sich Produktionsprozesse auf die Umwelt und das Klima aus?'''', ''''Produktionsprozesse können sich schädlich auf die Umwelt und das Klima auswirken, indem sie Treibhausgasemissionen und Luftschadstoffe freisetzen. Die meisten Emissionen stammen aus der Metallindustrie, zum Beispiel aus der Stahlproduktion, aus der Produktion von mineralischen Stoffen wie Zement sowie aus der chemischen Industrie. In Deutschland ist die Industrie nach dem Energiesektor die zweitgrößte Quelle von Treibhausgasemissionen.'''', ''''Produktionsprozesse setzen Treibhausgasemissionen und Luftschadstoffe frei, was sich schädlich auf die Umwelt und das Klima auswirkt.'''', 306, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (307, ''''Welche Umweltprobleme entstehen durch die Produktion von Textilien?'''', ''''Durch die Produktion von Textilien entstehen Umweltprobleme wie der hohe Wasserverbrauch, die Verwendung von Düngemitteln und Pestiziden, die Belastung von Böden und Wasser mit Chemikalien, sowie die Gefährdung von Ressourcen wie Wasser und Boden. Beispielsweise hat der Anbau von Baumwolle in Usbekistan, Turkmenistan und Kirgistan dazu geführt, dass der Aralsee drei Viertel seiner Wassermenge verloren hat.'''', ''''Umweltprobleme durch Textilproduktion: hoher Wasserverbrauch, Düngemittel- und Pestizideinsatz, Boden- und Wasserverschmutzung.'''', 307, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (308, ''''Welche Umweltprobleme können durch die Produktion und Entsorgung von Elektro- und Elektronikprodukten entstehen?'''', ''''Durch die Produktion und Entsorgung von Elektro- und Elektronikprodukten können Umweltprobleme wie die Verletzung von Sozialstandards, die Zerstörung der Umwelt durch den Abbau von Rohstoffen und die unsachgemäße Entsorgung von Altgeräten in Ländern Asiens und Afrikas entstehen. Dies kann zu großen Umweltproblemen führen.'''', ''''Umweltprobleme durch Elektro- und Elektronikprodukte: Sozialstandardsverletzung, Umweltzerstörung durch Rohstoffabbau und unsachgemäße Entsorgung.'''', 308, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (309, ''''Was ist das Ziel der ''''''''Green Economy''''''''?'''', ''''Das Ziel der ''''''''Green Economy'''''''' ist die ökologische Modernisierung der gesamten Wirtschaft, indem Ökologie und Ökonomie positiv miteinander verbunden werden. Durch Investitionen in Zukunftstechnologien soll Innovation und nachhaltiges Wachstum geschaffen werden, um die natürlichen Lebensgrundlagen zu schützen und den Wohlstand kommender Generationen zu sichern.'''', ''''Die ''''''''Green Economy'''''''' zielt auf die ökologische Modernisierung der Wirtschaft ab, um nachhaltiges Wachstum und Innovation zu fördern.'''', 309, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (310, ''''Was fordern Fachleute und Regierungsstellen von Unternehmen, um ihre Auswirkungen auf die Umwelt und die Menschenrechte zu reduzieren?'''', ''''Fachleute und Regierungsstellen fordern von Unternehmen, dass sie stärker in die Verantwortung genommen werden, um ihre Auswirkungen auf die Umwelt und die Menschenrechte zu reduzieren. Beispielsweise soll der Emissionshandel Unternehmen dazu anregen, ihre Treibhausgasemissionen zu reduzieren, indem sie Rechte für die jeweilige Menge der Emissionen erwerben müssen. Außerdem soll das Lieferkettengesetz Unternehmen dazu verpflichten, ihre Lieferkette bis hin zum Rohstoffabbau zu überprüfen und Verbesserungen umzusetzen, um Menschenrechtsverstöße und Umweltbelastungen zu vermeiden.'''', ''''Fachleute und Regierungsstellen fordern von Unternehmen, dass sie ihre Auswirkungen auf die Umwelt und die Menschenrechte reduzieren, indem sie sich stärker in die Verantwortung nehmen, zum Beispiel durch den Emissionshandel und das Lieferkettengesetz.'''', 310, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (311, ''''Was ist das Ziel der Europäischen Union im Hinblick auf Klimaneutralität?'''', ''''Das Ziel der Europäischen Union ist es, bis 2050 klimaneutral zu werden, d.h. alle Treibhausgasemissionen in der Europäischen Union soweit wie möglich zu vermeiden.'''', ''''Bis 2050 klimaneutral zu werden.'''', 311, 17);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (312, ''''Was ist das Ziel der europäischen Wachstumsstrategie ''''''''Deal''''''''?'''', ''''Das Ziel der europäischen Wachstumsstrategie ''''''''Deal'''''''' ist es, Europa zur ersten klimaneutralen Industrieregion auf der Welt zu machen. Dazu muss die gesamte Wirtschaft transformiert werden, indem der Ausstieg aus der Nutzung fossiler Brennstoffe und die Reduzierung von CO2-Emissionen in allen Wirtschaftsbereichen erreicht wird.'''', ''''Europa soll die erste klimaneutrale Industrieregion auf der Welt werden.'''', 312, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (313, ''''Was ist das Hauptziel der regionalen Wirtschaftsblöcke und der Freihandelsabkommen?'''', ''''Das Hauptziel der regionalen Wirtschaftsblöcke und der Freihandelsabkommen ist es, den Handel zwischen den beteiligten Ländern zu erleichtern und zu fördern, indem sie die Ausfuhr von Waren und Dienstleistungen erleichtern und die Handelsbeziehungen ausweiten.'''', ''''Das Hauptziel ist die Erleichterung und Förderung des Handels zwischen den beteiligten Ländern.'''', 313, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (314, ''''Was soll die EU-Handelspolitik stärker auf ausrichten, um die Nachhaltigkeits- und Klimaziele zu erreichen?'''', ''''Die EU-Handelspolitik soll stärker auf die THG-Neutralität ausgerichtet werden, um die Nachhaltigkeits- und Klimaziele zu erreichen. Dies kann durch die Verknüpfung der Nachhaltigkeitsanforderungen mit Sanktionen in Handelsverträgen erreicht werden.'''', ''''Die EU-Handelspolitik soll auf die THG-Neutralität ausgerichtet werden.'''', 314, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (315, ''''Wie können Verbraucherinnen und Verbraucher durch ihre Konsumentscheidungen Umweltschäden verringern?'''', ''''Verbraucherinnen und Verbraucher können durch bewusste Konsumentscheidungen Umweltschäden verringern, indem sie nachhaltige Produkte kaufen und dadurch das Angebot an solchen Produkten steigern. Sie können sich bei ihrer Orientierung auf Umweltkennzeichen und Produktsiegel wie dem Blauen Engel verlassen.'''', ''''Durch den Kauf nachhaltiger Produkte und die Orientierung an Umweltkennzeichen wie dem Blauen Engel.'''', 315, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (316, ''''Warum sind wissenschaftliche Erkenntnisse über das Klimasystem und den Klimawandel wichtig?'''', ''''Wissenschaftliche Erkenntnisse über das Klimasystem und den Klimawandel sind wichtig, um geeignete Maßnahmen zum Klimaschutz zu entscheiden. Obwohl noch nicht alle Details erforscht sind, ist das Wissen über grundlegende Zusammenhänge gesichert. Viele Vorgänge im Klimasystem sind gut verstanden und können mithilfe von Klimamodellen realitätsnah simuliert werden. Diese Erkenntnisse helfen dabei, die komplexen Wechselwirkungen im Klimasystem zu verstehen und fundierte Entscheidungen zu treffen.'''', ''''Wissenschaftliche Erkenntnisse sind wichtig, um geeignete Klimaschutzmaßnahmen zu entscheiden und die komplexen Wechselwirkungen im Klimasystem zu verstehen.'''', 316, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (317, ''''Welche der folgenden Aussagen ist eine zentrale Aussage des 6. Sachstandsberichts des Weltklimarates?<br>A) Der Klimawandel wird hauptsächlich durch natürliche Prozesse verursacht.<br>B) Wetterextreme werden mit fortschreitendem Klimawandel zunehmen.<br>C) Der Klimawandel hat keine Auswirkungen auf die menschliche Infrastruktur.<br>D) Die Fridays-for-Future-Bewegung hat keinen Einfluss auf das Bewusstsein der Öffentlichkeit.'''', ''''B) Wetterextreme werden mit fortschreitendem Klimawandel zunehmen.'''', ''''B) Wetterextreme werden mit fortschreitendem Klimawandel zunehmen.'''', 317, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (318, ''''Welche der folgenden Aussagen gehört zu den Hauptaussagen des 6. Sachstandsberichts des Weltklimarates?<br>A) Der Klimawandel ist hauptsächlich durch natürliche Ursachen bedingt.<br>B) Der Einfluss des Menschen hat die Atmosphäre, den Ozean und die Landflächen erwärmt.<br>C) Es gibt keine signifikanten Veränderungen im Klimasystem.<br>D) Der Klimawandel hat keine Auswirkungen auf Wetter- und Klimaextreme.'''', ''''B) Der Einfluss des Menschen hat die Atmosphäre, den Ozean und die Landflächen erwärmt.'''', ''''B) Der Einfluss des Menschen hat die Atmosphäre, den Ozean und die Landflächen erwärmt.'''', 318, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (319, ''''Welche der folgenden Subsysteme gehören zum Klimasystem der Erde?<br>A) Atmosphäre, Hydrosphäre, Kryosphäre, Lithosphäre, Biosphäre<br>B) Atmosphäre, Hydrosphäre, Kryosphäre, Lithosphäre, Stratosphäre<br>C) Atmosphäre, Hydrosphäre, Kryosphäre, Lithosphäre, Troposphäre<br>D) Atmosphäre, Hydrosphäre, Kryosphäre, Lithosphäre, Mesosphäre'''', ''''A) Atmosphäre, Hydrosphäre, Kryosphäre, Lithosphäre, Biosphäre'''', ''''A) Atmosphäre, Hydrosphäre, Kryosphäre, Lithosphäre, Biosphäre'''', 319, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (320, ''''Welche der folgenden Aussagen beschreibt die Wirkung von Schnee- und Eisflächen auf das Klima?<br>A) Sie erhöhen die Temperaturen in Bodennähe.<br>B) Sie reflektieren die Sonnenstrahlung und wirken kühlend.<br>C) Sie haben keinen Einfluss auf die Temperaturen.<br>D) Sie absorbieren die Sonnenstrahlung und erwärmen die Atmosphäre.'''', ''''B) Sie reflektieren die Sonnenstrahlung und wirken kühlend.'''', ''''B) Sie reflektieren die Sonnenstrahlung und wirken kühlend.'''', 320, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (321, ''''Welche der folgenden Aussagen beschreibt eine Ursache für Klimaänderungen?<br>A) Änderungen der ankommenden Sonnenstrahlung<br>B) Änderungen der Erdrotation<br>C) Änderungen der Mondphasen<br>D) Änderungen der Erdanziehungskraft'''', ''''A) Änderungen der ankommenden Sonnenstrahlung. Diese Änderungen beeinflussen die Energiebilanz der Erde und können das Klima verändern. Andere Ursachen für Klimaänderungen sind Änderungen der reflektierten Sonnenstrahlung, Änderungen der in den Weltraum abgegebenen Wärmestrahlung und die interne Variabilität des Klimasystems.'''', ''''A) Änderungen der ankommenden Sonnenstrahlung'''', 321, 15);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (322, ''''Welche der folgenden Faktoren trägt zur Reflektion der Sonnenstrahlung bei?<br>A) Treibhausgase<br>B) Vulkanausbrüche<br>C) El-Niño-Phänomen<br>D) Landwirtschaft'''', ''''B) Vulkanausbrüche. Vulkanausbrüche setzen Aerosole frei, die in der Atmosphäre zur Reflektion der Sonnenstrahlung beitragen und tendenziell zu einer kurzzeitigen Abkühlung des Klimas führen.'''', ''''B) Vulkanausbrüche'''', 322, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (323, ''''Welche der folgenden Aussagen beschreibt das El-Niño-Phänomen korrekt?<br>A) El Niño führt zu einer Abkühlung der Meeresoberflächentemperaturen im tropischen Pazifik.<br>B) El Niño verursacht starke Niederschläge in normalerweise trockenen Regionen Südamerikas.<br>C) El Niño führt zu einer Erhöhung der CO2-Konzentration in der Atmosphäre.<br>D) El Niño hat keine Auswirkungen auf die atmosphärische Zirkulation.'''', ''''B) El Niño verursacht starke Niederschläge in normalerweise trockenen Regionen Südamerikas. Während des El-Niño-Ereignisses sind die Oberflächentemperaturen des Ozeans im tropischen Pazifik ungewöhnlich erhöht. Diese Temperaturänderung beeinflusst die atmosphärische Zirkulation und führt zu starken Niederschlägen in Regionen Südamerikas, die normalerweise trocken sind, was zu Überschwemmungen führen kann. Gleichzeitig können in Indonesien und Australien Dürren auftreten, die verheerende Wald- und Buschbrände verursachen.'''', ''''B) El Niño verursacht starke Niederschläge in normalerweise trockenen Regionen Südamerikas.'''', 323, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (324, ''''Welcher Prozess benötigt die längste Zeit zur Aufnahme und Speicherung von CO2?<br>A) Reaktionen mit Wassermolekülen im Ozean<br>B) Transport durch Strömungen im Ozean<br>C) Einlagerung von Sedimenten in die Erdkruste<br>D) Photosynthese der Vegetation'''', ''''C) Einlagerung von Sedimenten in die Erdkruste. Dieser geologische Prozess benötigt zehntausende bis hunderttausende von Jahren zur Aufnahme und Speicherung von CO2.'''', ''''C) Einlagerung von Sedimenten in die Erdkruste'''', 324, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (325, ''''Welche der folgenden Aussagen ist korrekt?<br>A) Die Ausdehnung des arktischen Meereises war zwischen 1979 und 1992 größer als zwischen 2007 und 2020.<br>B) Der Grönländische Eisschild verliert jedes Jahr mehr als 150 Milliarden Tonnen Eismasse.<br>C) Die globale Gesamtmasse der Gebirgsgletscher hat seit 1980 im Durchschnitt eine Eisschicht von mehr als 10 Metern Dicke verloren.<br>D) Die Wachstumsperiode in außertropischen Regionen der Nordhalbkugel hat seit den 1950er Jahren im Mittel um zwei Tage pro Jahr zugenommen.'''', ''''A) Die Ausdehnung des arktischen Meereises war zwischen 1979 und 1992 größer als zwischen 2007 und 2020. Zwischen 1979 und 1992 betrug die Ausdehnung des arktischen Meereises rund 6,1 Millionen Quadratkilometer, während sie im Zeitraum 2007 bis 2020 nur noch bei rund 4,4 Millionen Quadratkilometern lag. Dies zeigt eine deutliche Abnahme der Eisausdehnung über die Jahre.'''', ''''A) Die Ausdehnung des arktischen Meereises war zwischen 1979 und 1992 größer als zwischen 2007 und 2020.'''', 325, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (326, ''''Welcher der folgenden Kipp-Punkte könnte zu einem Anstieg des globalen Meeresspiegels um rund sieben Meter führen?<br>A) Schmelzen des arktischen Meereises<br>B) Abnahme der Albedo in der Arktis<br>C) Schmelzen des Grönländischen Eisschildes<br>D) Erhöhung der Sonnenstrahlung'''', ''''C) Schmelzen des Grönländischen Eisschildes. Wenn die jährlichen Eismassenverluste dauerhaft größer als die Zunahmen durch Schneefall sind und in den kommenden Jahrhunderten anhalten, könnte der Grönländische Eisschild vollständig abschmelzen. Dies würde zu einem Anstieg des mittleren globalen Meeresspiegels von rund sieben Metern führen – mit katastrophalen Auswirkungen in küstennahen Gebieten.'''', ''''C) Schmelzen des Grönländischen Eisschildes'''', 326, 16);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (327, ''''Welche der folgenden Maßnahmen trägt zur Reduzierung persönlicher CO2-Emissionen bei?<br>A) Mehr Fernreisen per Flugzeug<br>B) Reduzierung der zurückgelegten Autokilometer<br>C) Erhöhung des Kraftstoffverbrauchs des Autos<br>D) Konsum von nicht-nachhaltigen Produkten'''', ''''B) Reduzierung der zurückgelegten Autokilometer. Die Reduzierung der zurückgelegten Autokilometer trägt zur Verringerung der persönlichen CO2-Emissionen bei, da weniger Kraftstoff verbraucht wird und somit weniger CO2 in die Atmosphäre gelangt.'''', ''''B) Reduzierung der zurückgelegten Autokilometer'''', 327, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (328, ''''Welche Maßnahme trägt laut Text am meisten zur Reduzierung von CO2-Emissionen bei?<br>A) Nutzung von Bioprodukten<br>B) Reduzierung der Wohnfläche<br>C) Verringerung der Flugreisen<br>D) Verwendung von Fleisch-Ersatzprodukten'''', ''''A) Nutzung von Bioprodukten. Laut Text können allein durch die Wahl von Bioprodukten knapp 20 Prozent der CO2-Emissionen eingespart werden. Dies zeigt, dass die Ernährung, insbesondere die Wahl von Bioprodukten, einen erheblichen Einfluss auf die Reduzierung von CO2-Emissionen hat.'''', ''''A) Nutzung von Bioprodukten'''', 328, NULL);'');'),
('INSERT INTO "" ("insert_statement") VALUES (''INSERT INTO "Exercise" ("id", "question", "answer", "concise_answer", "source_id", "order_index") VALUES (329, ''''Welche der folgenden Aussagen zur Nutzung von Materialien unter der Creative Commons Namensnennung - Weitergabe unter gleichen Bedingungen 4.0 International Lizenz ist korrekt?<br>A) Man darf die Materialien nur mit besonderer Genehmigung verwenden.<br>B) Man darf die Materialien ohne besondere Genehmigung verwenden und bearbeiten.<br>C) Man darf die Materialien nicht weiterverbreiten.<br>D) Man darf die Materialien nur für private Zwecke nutzen.'''', ''''B) Man darf die Materialien ohne besondere Genehmigung verwenden und bearbeiten.'''', ''''B) Man darf die Materialien ohne besondere Genehmigung verwenden und bearbeiten.'''', 329, NULL);'');');