INSERT INTO "" ("insert_statement") VALUES
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (1, ''Wo sind die Grundrechte festgelegt?'', ''Die Grundrechte sind in den Artikeln 1 bis 19 des Grundgesetzes festgelegt. Darüber hinaus werden sie auch in Urteilen des Verfassungsgerichts und in der Europäischen Menschenrechtskonvention geschützt.'', ''In den Artikeln 1 bis 19 des Grundgesetzes, in Urteilen des Verfassungsgerichts und in der Europäischen Menschenrechtskonvention.'', 1, 13);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (2, ''Warum wurden die Grundrechte in Deutschland an die Spitze des Grundgesetzes gestellt?<br>A) Weil sie leicht durch Notverordnungen außer Kraft gesetzt werden können.<br>B) Aufgrund der Erfahrungen der Weimarer Republik und der nationalsozialistischen Herrschaft.<br>C) Weil sie keine besondere Bedeutung haben.<br>D) Um die Macht des Reichspräsidenten zu stärken.'', ''B) Aufgrund der Erfahrungen der Weimarer Republik und der nationalsozialistischen Herrschaft. Die Verfassungsgeber maßen den Grundrechten einen hohen Rang bei, weil die Weimarer Verfassung zwar Grundrechte enthielt, diese aber nicht einklagbar waren und durch Notverordnungen außer Kraft gesetzt werden konnten. Dies führte zur nationalsozialistischen Willkürherrschaft und zur Entwürdigung des Menschen.'', ''B) Aufgrund der Erfahrungen der Weimarer Republik und der nationalsozialistischen Herrschaft.'', 2, 30);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (3, ''Welche der folgenden Aussagen beschreibt korrekt die Unterscheidung zwischen Menschenrechten und Bürgerrechten?<br>A) Menschenrechte gelten nur für Staatsangehörige.<br>B) Bürgerrechte sind überstaatliche Rechte.<br>C) Menschenrechte sind natürliche, angeborene Rechte.<br>D) Bürgerrechte umfassen die Meinungsfreiheit.'', ''C) Menschenrechte sind natürliche, angeborene Rechte.'', ''C) Menschenrechte sind natürliche, angeborene Rechte.'', 3, 48);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (4, ''Welches Grundrecht ist nicht ausdrücklich im Grundgesetz verankert?<br>A) Widerstandsrecht<br>B) Wahlrecht<br>C) Datenschutz<br>D) Gleicher Zugang zu öffentlichen Ämtern'', ''C) Datenschutz. Der Datenschutz ist in Deutschland noch nicht ausdrücklich im Grundgesetz verankert. In viele Verfassungen der Länder ist das Recht auf den Schutz der personenbezogenen Daten allerdings aufgenommen. Mit dem Volkszählungsurteil des Bundesverfassungsgerichts von 1983 wurde früh das ''''Recht auf informationelle Selbstbestimmung'''' festgeschrieben. Das Bundesdatenschutzgesetz von 1990 trug in der Folge dazu bei, ''''den Einzelnen davor zu schützen, dass er durch den Umgang mit seinen personenbezogenen Daten in seinem Persönlichkeitsrecht beeinträchtigt wird'''' (Paragraf 1). Mit zwei weiteren Urteilen hat das Bundesverfassungsgericht 2008 das Recht auf informationelle Selbstbestimmung erheblich gestärkt und sogar ein neues Grundrecht zum Schutz der digitalen Kommunikation geschaffen.'', ''C) Datenschutz'', 4, 2);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (5, ''Wie sind die Grundrechte im Grundgesetz im Vergleich zur Weimarer Verfassung ausgestattet?'', ''Im Vergleich zur Weimarer Verfassung sind die Grundrechte im Grundgesetz mit besonderen Garantien ausgestattet. Artikel 1 (3) des Grundgesetzes besagt, dass die Grundrechte die Gesetzgebung, die vollziehende Gewalt und die Rechtsprechung als unmittelbar geltendes Recht binden. Das bedeutet, dass keine der drei Staatsgewalten etwas tun oder unterlassen kann, was im Widerspruch zu den Grundrechten steht. Diese Grundrechte können notfalls bis zum Bundesverfassungsgericht eingeklagt werden.'', ''Die Grundrechte im Grundgesetz sind mit besonderen Garantien ausgestattet und binden alle Staatsgewalten als unmittelbar geltendes Recht. Sie können bis zum Bundesverfassungsgericht eingeklagt werden.'', 5, 41);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (6, ''Welche der folgenden Aussagen über die Änderung des Grundgesetzes ist korrekt?<br>A) Das Grundgesetz kann durch ein einfaches Gesetz geändert werden.<br>B) Eine Änderung des Grundgesetzes bedarf der Zustimmung von zwei Dritteln der Mitglieder des Bundestages und des Bundesrates.<br>C) Grundrechte können ohne Einschränkungen geändert werden.<br>D) Die Gliederung des Bundes in Länder kann jederzeit geändert werden.'', ''B) Eine Änderung des Grundgesetzes bedarf der Zustimmung von zwei Dritteln der Mitglieder des Bundestages und des Bundesrates.'', ''B) Eine Änderung des Grundgesetzes bedarf der Zustimmung von zwei Dritteln der Mitglieder des Bundestages und des Bundesrates.'', 6, 68);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (7, ''Welche Aussage über die Grundrechte in Deutschland ist korrekt?<br>A) Grundrechte können jederzeit aberkannt werden.<br>B) Grundrechte dürfen nicht missbraucht werden, um die demokratische Ordnung zu bekämpfen.<br>C) Es gibt kein Recht zum Widerstand gegen verfassungsfeindliche Bestrebungen.<br>D) Soziale Grundrechte sind nicht Teil der deutschen Verfassung.'', ''B) Grundrechte dürfen nicht missbraucht werden, um die demokratische Ordnung zu bekämpfen.'', ''B) Grundrechte dürfen nicht missbraucht werden, um die demokratische Ordnung zu bekämpfen.'', 7, 33);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (8, ''Wie unterscheiden sich soziale Grundrechte von Freiheits- und Gleichheitsrechten?'', ''Soziale Grundrechte unterscheiden sich von Freiheits- und Gleichheitsrechten in einem zentralen Punkt: Während Freiheits- und Gleichheitsrechte die Bürger vor staatlichen Eingriffen schützen und gerichtlich einklagbar sind, begründen soziale Grundrechte Ansprüche der Bürger gegenüber dem Staat und fordern staatliche Eingriffe. Beispielsweise schützt das Grundrecht auf Unverletzlichkeit der Wohnung (Art. 13) den Einzelnen vor ungerechtfertigten staatlichen Eingriffen, während ein Grundrecht auf Wohnung den Staat verpflichten würde, jedem Bürger eine angemessene Wohnung zu verschaffen. Ebenso würde ein Grundrecht auf Arbeit den Staat verpflichten, jedem einen Arbeitsplatz zu garantieren, was staatliche Eingriffe in den Arbeitsmarkt und mögliche Einschränkungen der freien Berufswahl und Tarifautonomie zur Folge hätte.'', ''Soziale Grundrechte begründen Ansprüche der Bürger und fordern staatliche Eingriffe, während Freiheits- und Gleichheitsrechte die Bürger vor staatlichen Eingriffen schützen und gerichtlich einklagbar sind.'', 8, 69);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (9, ''Welche Aussage über soziale Grundrechte in den Verfassungen der Bundesländer ist korrekt?<br>A) Soziale Grundrechte sind in allen Bundesländern uneingeschränkt garantiert.<br>B) Soziale Grundrechte sind an die finanziellen Möglichkeiten des Landes gebunden.<br>C) In der brandenburgischen Verfassung gibt es keine sozialen Grundrechte.<br>D) Soziale Grundrechte sind nur in den alten Bundesländern verankert.'', ''B) Soziale Grundrechte sind an die finanziellen Möglichkeiten des Landes gebunden. In den Verfassungen zahlreicher Bundesländer, insbesondere in den neuen Bundesländern, sind soziale Rechte wie Arbeit, soziale Sicherheit und Wohnung festgelegt. Diese Rechte sind jedoch Staatszielbestimmungen, die allgemein verpflichtend sind und an die finanziellen Möglichkeiten des jeweiligen Landes gebunden sind. Zum Beispiel verpflichtet die brandenburgische Verfassung das Land, im Rahmen seiner Kräfte für die Verwirklichung des Rechts auf soziale Sicherung, eine angemessene Wohnung und Arbeit zu sorgen.'', ''B) Soziale Grundrechte sind an die finanziellen Möglichkeiten des Landes gebunden.'', 9, 35);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (10, ''Welche Pflichten sind im Grundgesetz ausdrücklich aufgeführt?'', ''Im Grundgesetz sind einige wenige Pflichten ausdrücklich aufgeführt. Dazu gehören die Pflicht zur Verfassungstreue für Inhaber des (wissenschaftlichen) Lehramts (Art. 5 Abs. 3), die Pflicht der Eltern zur Pflege und Erziehung der Kinder (Art. 6 Abs. 2), die Wehrpflicht und die Pflicht zum zivilen Ersatzdienst (Art. 12a). Diese Pflichten sind Beispiele dafür, wie das Grundgesetz bestimmte Verantwortlichkeiten festlegt, während es gleichzeitig betont, dass Demokratie auf der Eigenverantwortung der Bürger basiert, ihre Pflichten gegenüber der Gemeinschaft wahrzunehmen.'', ''Die Pflichten im Grundgesetz umfassen die Verfassungstreue für Lehramtsinhaber, die elterliche Pflege und Erziehung, die Wehrpflicht und den zivilen Ersatzdienst.'', 10, 79);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (11, ''Welche Aussage über den Europäischen Gerichtshof für Menschenrechte (EGMR) ist korrekt?<br>A) Der EGMR besteht aus Richtern, die von der Europäischen Union ernannt werden.<br>B) Bürger können nur dann Klage erheben, wenn sie den innerstaatlichen Rechtsweg ausgeschöpft haben.<br>C) Die Amtszeit der Richter beträgt zehn Jahre.<br>D) Russland hat die Reform zur Entlastung des EGMR ratifiziert.'', ''B) Bürger können nur dann Klage erheben, wenn sie den innerstaatlichen Rechtsweg ausgeschöpft haben.'', ''B) Bürger können nur dann Klage erheben, wenn sie den innerstaatlichen Rechtsweg ausgeschöpft haben.'', 11, 4);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (12, ''Welche Auswirkungen haben die Entscheidungen des Gerichtshofes auf die Mitgliedsstaaten?<br>A) Sie sind unverbindlich und dienen nur als Empfehlungen.<br>B) Sie müssen von den Staaten befolgt werden und führen oft zu Änderungen der Gesetzgebung.<br>C) Sie betreffen nur wirtschaftliche Angelegenheiten.<br>D) Sie haben keinen Einfluss auf die Menschenrechte.'', ''B) Sie müssen von den Staaten befolgt werden und führen oft zu Änderungen der Gesetzgebung. Die Tätigkeit des Gerichtshofes hat wesentlich dazu beigetragen, dass Menschenrechte und Grundfreiheiten in Europa einen hohen Wert darstellen. Viele grundlegende Urteile, in denen Staaten verurteilt wurden, haben zu Änderungen der Gesetzgebung und des Umganges der Mitgliedsstaaten mit den Menschenrechten geführt.'', ''B) Sie müssen von den Staaten befolgt werden und führen oft zu Änderungen der Gesetzgebung.'', 12, 1);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (13, ''Wo sind die Grundrechte in Deutschland festgelegt?'', ''Die Grundrechte sind in den Artikeln 1 bis 19 des Grundgesetzes festgelegt. Darüber hinaus werden sie auch durch Urteile des Verfassungsgerichts und die Europäische Menschenrechtskonvention geschützt.'', ''In den Artikeln 1-19 des Grundgesetzes, in Verfassungsgerichtsurteilen und der Europäischen Menschenrechtskonvention.'', 13, 39);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (14, ''Warum haben die Verfassungsgeber den Grundrechten im Grundgesetz einen hohen Rang eingeräumt?'', ''Die Verfassungsgeber haben den Grundrechten im Grundgesetz einen hohen Rang eingeräumt, weil sie aus den Erfahrungen der Weimarer Republik und der Zeit des nationalsozialistischen Regimes gelernt haben. Die Weimarer Verfassung enthielt zwar einen Katalog von Grundrechten, aber diese waren nicht einklagbar und konnten durch Notverordnungen außer Kraft gesetzt werden. Dies führte zur Entwürdigung des Menschen und zur Willkürherrschaft der Nationalsozialisten.'', ''Die Verfassungsgeber haben den Grundrechten einen hohen Rang eingeräumt, um die Erfahrungen der Weimarer Republik und des nationalsozialistischen Regimes zu berücksichtigen und die Menschenwürde zu schützen.'', 14, 38);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (15, ''Was ist der Zweck von Grundrechten in Deutschland?'', ''Der Zweck von Grundrechten in Deutschland ist es, den Freiheitsraum des Einzelnen vor Übergriffen der öffentlichen Gewalt zu schützen und als Abwehrrechte des Bürgers gegen den Staat zu dienen. Sie sind zugleich die Grundlage der Wertordnung der Bundesrepublik Deutschland und gehören zum Kern der freiheitlichen demokratischen Grundordnung des Grundgesetzes.'', ''Grundrechte schützen den Freiheitsraum des Einzelnen vor staatlichen Übergriffen und sind die Grundlage der Wertordnung Deutschlands.'', 15, 9);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (16, ''Welche drei Arten von Grundrechten werden in Deutschland unterschieden?'', ''In Deutschland werden drei Arten von Grundrechten unterschieden: Freiheitsrechte, wie das Recht auf freie Meinungsäußerung, Gleichheitsrechte, wie das Recht auf Gleichheit vor dem Gesetz und die Gleichberechtigung von Mann und Frau, und Unverletzlichkeitsrechte oder Abwehrrechte, wie die Unverletzlichkeit der Wohnung, Freizügigkeit, Brief-, Post- und Fernmeldegeheimnis.'', ''Freiheitsrechte, Gleichheitsrechte und Unverletzlichkeitsrechte.'', 16, 20);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (17, ''Was ist die Bedeutung der unmittelbaren Geltung der Grundrechte im Grundgesetz?'', ''Die unmittelbare Geltung der Grundrechte im Grundgesetz bedeutet, dass sie für alle Staatsgewalten, also Gesetzgebung, vollziehende Gewalt und Rechtsprechung, bindend sind. Keine der drei Staatsgewalten kann etwas tun oder unterlassen, was im Widerspruch zu ihnen steht. Die Grundrechte können notfalls bis zum Bundesverfassungsgericht eingeklagt werden.'', ''Die Grundrechte sind unmittelbar geltendes Recht, bindend für alle Staatsgewalten, und können notfalls bis zum Bundesverfassungsgericht eingeklagt werden.'', 17, 5);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (18, ''Was sind die Bedingungen für eine Änderung des Grundgesetzes in Deutschland?'', ''Eine Änderung des Grundgesetzes in Deutschland bedarf der Zustimmung von zwei Dritteln der Mitglieder des Bundestages und zwei Dritteln der Stimmen des Bundesrates. Darüber hinaus sind bestimmte Grundsätze, wie die Gliederung des Bundes in Länder, die Mitwirkung der Länder bei der Gesetzgebung und die in den Artikeln 1 und 20 niedergelegten Grundsätze, unveränderlich.'', ''Eine Verfassungsänderung benötigt eine Zweidrittelmehrheit im Bundestag und Bundesrat und darf bestimmte Grundsätze nicht antasten.'', 18, 76);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (19, ''Was passiert, wenn jemand bestimmte Grundrechte zum Kampf gegen die freiheitliche demokratische Grundordnung missbraucht?'', ''Wenn jemand bestimmte Grundrechte zum Kampf gegen die freiheitliche demokratische Grundordnung missbraucht, verwirkt er sie. Das bedeutet, dass er sich beim Einschreiten der Staatsgewalt nicht auf das betreffende Grundrecht berufen kann.'', ''Er verwirkt sie und kann sich nicht auf das Grundrecht berufen.'', 19, 17);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (20, ''Was ist der Hauptunterschied zwischen sozialen Grundrechten und den klassischen Freiheits- und Gleichheitsrechten?'', ''Der Hauptunterschied zwischen sozialen Grundrechten und den klassischen Freiheits- und Gleichheitsrechten liegt darin, dass die Letzteren Rechte der Bürger schützen und vom Staat fordern, Eingriffe zu unterlassen, während soziale Grundrechte Ansprüche von Bürgern begründen und vom Staat fordern, Eingriffe vorzunehmen.'', ''Soziale Grundrechte begründen Ansprüche auf staatliche Leistungen, während klassische Rechte den Staat zu Unterlassung verpflichten.'', 20, 43);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (21, ''Was ist das Ziel von Staatszielbestimmungen in den Verfassungen der Bundesländer?'', ''Das Ziel von Staatszielbestimmungen in den Verfassungen der Bundesländer ist es, die Verwirklichung sozialer Rechte, wie das Recht auf soziale Sicherung, auf eine angemessene Wohnung und auf Arbeit, zu gewährleisten. Diese Bestimmungen haben allgemein verpflichtenden Charakter und sind an die finanziellen Möglichkeiten des Landes gebunden.'', ''Das Ziel von Staatszielbestimmungen ist die Verwirklichung sozialer Rechte, wie soziale Sicherung, Wohnung und Arbeit, innerhalb der finanziellen Möglichkeiten.'', 21, 77);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (22, ''Warum enthält das Grundgesetz keinen Katalog von Grundpflichten?'', ''Ein Katalog von Grundpflichten würde dem Geist einer demokratischen Verfassung widersprechen, da Demokratie voraussetzt, dass jeder aus eigener Verantwortung seinen Pflichten gegenüber der Gemeinschaft nachkommt. Die selbstverständliche Verbindung von Rechten und Pflichten wird in Art. 33 Abs. 1 des Grundgesetzes hergestellt.'', ''Ein Katalog von Grundpflichten widerspricht dem Geist der Demokratie, da jeder Bürger aus eigener Verantwortung seine Pflichten erfüllen soll.'', 22, 83);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (23, ''Wie viele Richter sitzen im Europäischen Gerichtshof für Menschenrechte?'', ''Im Europäischen Gerichtshof für Menschenrechte sitzen 47 Richter, jeweils einer pro Mitgliedsstaat des Europarats.'', ''47 Richter, jeweils einer pro Mitgliedsstaat.'', 23, 62);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (24, ''Welche Auswirkungen haben die Entscheidungen des Gerichtshofes auf die Menschenrechte und Grundfreiheiten in Europa?'', ''Die Entscheidungen des Gerichtshofes haben wesentlich dazu beigetragen, dass Menschenrechte und Grundfreiheiten in Europa einen hohen Wert darstellen. Viele grundlegende Urteile, in denen Staaten verurteilt wurden, haben zu Änderungen der Gesetzgebung und des Umganges der Mitgliedsstaaten mit den Menschenrechten geführt.'', ''Die Entscheidungen des Gerichtshofes haben dazu beigetragen, dass Menschenrechte und Grundfreiheiten in Europa einen hohen Wert darstellen und haben zu Änderungen der Gesetzgebung und des Umganges der Mitgliedsstaaten mit den Menschenrechten geführt.'', 24, 55);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (25, ''Was ist die Hauptursache für den Anstieg der globalen Mitteltemperatur seit der Industrialisierung?'', ''Die Hauptursache für den Anstieg der globalen Mitteltemperatur seit der Industrialisierung ist die anthropogene Klimaänderung, also die vom Menschen verursachte Klimaänderung.'', ''Die vom Menschen verursachte Klimaänderung.'', 25, 18);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (26, ''Welche Gase werden durch die Verbrennung fossiler Energieträger und die Land- und Viehwirtschaft in der Atmosphäre freigesetzt?'', ''Durch die Verbrennung fossiler Energieträger werden Kohlendioxid (CO2) und durch die Land- und Viehwirtschaft werden Methan (CH4) und Distickstoffmonoxid (Lachgas, N2O) in der Atmosphäre freigesetzt. Diese Gase gehören zu den treibhauswirksamen Gasen, die zu einer Erwärmung der unteren Luftschichten beitragen.'', ''Kohlendioxid, Methan und Lachgas.'', 26, 27);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (27, ''Was versteht man unter anthropogener Klimaänderung?'', ''Unter anthropogener Klimaänderung versteht man die vom Menschen verursachte Veränderung des Klimas. Seit der Industrialisierung steigt die globale Mitteltemperatur der Luft in Bodennähe allmählich an. Wissenschaftliche Forschungen belegen, dass Menschen für einen bedeutenden Teil dieses Temperaturanstiegs verantwortlich sind.'', ''Vom Menschen verursachte Veränderung des Klimas.'', 27, 71);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (28, ''Welche der folgenden Gase gehören zu den treibhauswirksamen Gasen?<br>A) Sauerstoff (O2)<br>B) Kohlendioxid (CO2)<br>C) Methan (CH4)<br>D) Distickstoffmonoxid (N2O)'', ''B) Kohlendioxid (CO2), C) Methan (CH4), und D) Distickstoffmonoxid (N2O) gehören zu den treibhauswirksamen Gasen. Diese Gase tragen zur Erwärmung der unteren Luftschichten bei, indem sie in der Atmosphäre angereichert werden. Kohlendioxid wird hauptsächlich durch das Verbrennen fossiler Energieträger und Entwaldung freigesetzt, während Methan und Distickstoffmonoxid vor allem aus der Land- und Viehwirtschaft stammen.'', ''B) Kohlendioxid (CO2), C) Methan (CH4), und D) Distickstoffmonoxid (N2O)'', 28, 72);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (29, ''Welche der folgenden Aussagen beschreibt korrekt die Bedeutung von Kipp-Punkten im Klimasystem?<br>A) Kipp-Punkte sind stabile Zustände im Klimasystem.<br>B) Kipp-Punkte führen zu allmählichen Klimaänderungen.<br>C) Kipp-Punkte können abrupte und starke Klimaänderungen verursachen.<br>D) Kipp-Punkte sind unwichtig für das Verständnis des Klimawandels.'', ''C) Kipp-Punkte können abrupte und starke Klimaänderungen verursachen. Diese Punkte im Klimasystem sind kritische Schwellen, bei deren Überschreitung das Klima sich stark und möglicherweise abrupt ändern kann. Bereits geringe Änderungen im Klimasystem können bewirken, dass diese Kipp-Punkte erreicht werden, was zu signifikanten Klimaänderungen führt.'', ''C) Kipp-Punkte können abrupte und starke Klimaänderungen verursachen.'', 29, 50);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (30, ''Wie kann man nach Ansicht des UBA-Präsidenten Dirk Messner den Urbanen Hitzeinseleffekt in Innenstädten mildern?'', ''Laut UBA-Präsident Dirk Messner kann man den Urbanen Hitzeinseleffekt in Innenstädten mildern, indem man mehr Grün, insbesondere neue Bäume, anpflanzt und mehr Verschattung durch außenliegenden Sonnenschutz sowie Dach- und Fassadenbegrünung schafft. Darüber hinaus muss man den alten Baumbestand in den Städten schützen und ihn bei anhaltender Trockenheit regelmäßig bewässern.'', ''Durch mehr Grün, Verschattung und Schutz des Baumbestands kann man den Urbanen Hitzeinseleffekt mildern.'', 30, 80);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (31, ''Welche Maßnahme wird laut der Studie des Umweltbundesamtes NICHT zur Minderung des Hitzeinseleffekts in Städten empfohlen?<br>A) Mehr Grünflächen und neue Bäume<br>B) Verschattung durch Sonnenschutz<br>C) Erhöhung der Bebauungsdichte<br>D) Dach- und Fassadenbegrünung'', ''C) Erhöhung der Bebauungsdichte'', ''C) Erhöhung der Bebauungsdichte'', 31, 53);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (32, ''Welche Maßnahme hat laut der Studie die thermische Behaglichkeit im Außenraum am meisten verbessert?<br>A) Dachbegrünungen<br>B) Bäume mit großen Kronen<br>C) Helle Anstriche der Gebäude<br>D) Versprühen von Wasser'', ''B) Bäume mit großen Kronen haben laut der Studie die thermische Behaglichkeit im Außenraum am meisten verbessert. Sie bewirkten im Sommer eine Minderung der Physiologisch Äquivalenten Temperatur (PET) um 10 Kelvin und mehr. Diese Verschattungselemente sind besonders effektiv, um die Hitzeinseln in den Quartieren zu reduzieren und ein angenehmeres Mikroklima zu schaffen.'', ''B) Bäume mit großen Kronen'', 32, 42);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (33, ''Welcher Faktor trägt NICHT zur Bildung des urbanen Hitzeinseleffekts bei?<br>A) Großflächige Bodenversiegelungen<br>B) Fehlende Begrünung<br>C) Dunkle Flächen wie Asphalt<br>D) Hohe Luftzirkulation'', ''D) Hohe Luftzirkulation'', ''D) Hohe Luftzirkulation'', 33, 12);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (34, ''Welcher Begriff wird oft mit „negativen Emissionen“ gleichgesetzt?<br>A) Klimaneutral<br>B) Treibhausgasneutral<br>C) CCS<br>D) Senken'', ''C) CCS (Carbon Capture and Storage) wird oft mit „negativen Emissionen“ gleichgesetzt. CCS ist eine Technologie, die Kohlendioxid (CO2) aus der Atmosphäre entfernt und in unterirdischen geologischen Formationen speichert, um die Menge an Treibhausgasen in der Atmosphäre zu reduzieren.'', ''C) CCS'', 34, 46);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (35, ''Welcher Begriff beschreibt Produkte, die nicht nur treibhausgasneutral sind, sondern auch indirekte Klimaeffekte bei der Herstellung ausgleichen?<br>A) Klimaneutral<br>B) Treibhausgasneutral<br>C) Kompensation<br>D) Carbon Capture and Storage (CCS)'', ''A) Klimaneutral. Klimaneutralität bedeutet, dass Produkte nicht nur treibhausgasneutral sind, sondern auch indirekte Klimaeffekte bei der Herstellung beachtet und ausgeglichen werden. Dies umfasst beispielsweise die verminderte Sonnenlichtrückstrahlung durch Bebauung und Landnutzung an der Produktionsstätte.'', ''A) Klimaneutral'', 35, 82);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (36, ''Welche der folgenden Aussagen über CCS (Carbon Capture and Storage) ist korrekt?<br>A) CCS führt zu negativen Emissionen.<br>B) CCS ist eine Methode, um Emissionen abzufangen und in unterirdische Lagerstätten zu pumpen.<br>C) CCS ist eine natürliche Senke.<br>D) CCS eignet sich für alle Arten von Emissionen.'', ''B) CCS ist eine Methode, um Emissionen abzufangen und in unterirdische Lagerstätten zu pumpen.'', ''B) CCS ist eine Methode, um Emissionen abzufangen und in unterirdische Lagerstätten zu pumpen.'', 36, 64);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (37, ''Was bedeutet es, wenn ein Produkt als ''''klimaneutral'''' oder ''''treibhausgasneutral'''' bezeichnet wird?'', ''Wenn ein Produkt als ''''klimaneutral'''' oder ''''treibhausgasneutral'''' bezeichnet wird, bedeutet dies, dass die durch die Produktion und den Gebrauch des Produkts entstehenden Treibhausgasemissionen ausgeglichen oder kompensiert werden. Dies kann durch die Verwendung von erneuerbaren Energien, die Implementierung von Emissionsminderungstechnologien oder die Kompensation von Emissionen durch den Kauf von Emissionszertifikaten erreicht werden.'', ''Ein ''''klimaneutral'''' oder ''''treibhausgasneutral'''' Produkt bedeutet, dass die entstehenden Treibhausgasemissionen ausgeglichen oder kompensiert werden.'', 37, 51);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (38, ''Was ist der Unterschied zwischen Klimaneutralität und Treibhausgasneutralität?'', ''Klimaneutralität bedeutet, dass ein Produkt nicht nur die direkten Treibhausgasemissionen bei der Herstellung ausgleicht, sondern auch indirekte Klimaeffekte, wie zum Beispiel die verminderte Sonnenlicht-Rückstrahlung durch Bebauung und Landnutzung, beachtet und ausgeglichen werden. Treibhausgasneutralität hingegen bezieht sich nur auf den Ausgleich der direkten Treibhausgasemissionen.'', ''Klimaneutralität umfasst den Ausgleich von direkten und indirekten Klimaeffekten, während Treibhausgasneutralität nur die direkten Emissionen betrifft.'', 38, 24);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (39, ''Was bedeutet es, wenn ein Land netto-null Emissionen erreichen will?'', ''Wenn ein Land netto-null Emissionen erreichen will, bedeutet dies, dass es seine Treibhausgasemissionen so reduzieren muss, dass sie in der zweiten Hälfte des 21. Jahrhunderts auf null sinken. Dies kann erreicht werden, indem die verbleibenden Emissionen durch natürliche oder technische Senken wieder der Atmosphäre entzogen werden.'', ''Netto-null Emissionen bedeutet, dass ein Land seine Treibhausgasemissionen auf null reduziert, indem es verbleibende Emissionen durch Senken wieder entfernt.'', 39, 16);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (40, ''Wie viele Bundesländer gibt es in Deutschland?'', ''Deutschland besteht aus 16 Bundesländern.'', ''16'', 40, 6);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (41, ''Wie viele Bundesländer gibt es in Deutschland?<br>A) 10<br>B) 15<br>C) 16<br>D) 20'', ''C) 16'', ''16'', 41, 52);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (42, ''Welche vier Landschaftsräume gehören zu Deutschland?'', ''Deutschland gehört vier unterschiedlichen Landschaftsräumen an: dem Norddeutschen Tiefland, dem Alpenvorland, der Mittelgebirgsschwelle und den Alpen.'', ''Norddeutsches Tiefland, Alpenvorland, Mittelgebirgsschwelle und Alpen.'', 42, 63);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (43, ''Welche geografischen Regionen gliedern sich in die Mittelgebirgsschwelle in Deutschland?'', ''Die Mittelgebirgsschwelle in Deutschland gliedert sich in einige größere Mittelgebirgssysteme und einzelne Bergzüge, wie das Rheinische Schiefergebirge, das Weserbergland, das Hessische Bergland, den Harz, den Thüringer Wald, das Erzgebirge und das Elbsandsteingebirge.'', ''Die Mittelgebirgsschwelle gliedert sich in Rheinisches Schiefergebirge, Weserbergland, Hessisches Bergland, Harz, Thüringer Wald, Erzgebirge und Elbsandsteingebirge.'', 43, 26);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (44, ''Welche ist die höchste Erhebung Deutschlands?'', ''Die höchste Erhebung Deutschlands ist die 2962 m hohe Zugspitze im Wettersteingebirge.'', ''Die Zugspitze (2962 m) im Wettersteingebirge.'', 44, 29);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (45, ''Welcher Fluss ist der längste und wasserreichste in Deutschland?'', ''Der längste und wasserreichste Fluss Deutschlands ist der Rhein, der 1320 km lang ist und auf deutschem Gebiet 865 km lang verläuft.'', ''Der Rhein'', 45, 57);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (46, ''Was charakterisiert das Klima Deutschlands?'', ''Das Klima Deutschlands ist ein Übergangsklima zwischen dem maritimen Klima Westeuropas und dem Kontinentalklima Osteuropas. Es ist durch eine gemäßigte Temperatur und eine ausreichende Feuchtigkeit gekennzeichnet, die durch Westwinde vom Atlantik gebracht wird. Lokale Abweichungen im Temperaturverlauf und in der Niederschlagshöhe sind häufig.'', ''Ein Übergangsklima zwischen maritimem und kontinentalem Klima mit gemäßigter Temperatur und ausreichender Feuchtigkeit.'', 46, 8);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (47, ''Welche Gruppen von Menschen trugen zum Bevölkerungswachstum in den westlichen Bundesländern nach dem Zweiten Weltkrieg bei?'', ''Zum Bevölkerungswachstum in den westlichen Bundesländern nach dem Zweiten Weltkrieg trugen vor allem Heimatvertriebene und Flüchtlinge aus den ehemaligen deutschen Ostgebieten und den angrenzenden Staaten Ost- und Südosteuropas bei, die bis 1953 etwa 10,6 Millionen Menschen umfassten. Darüber hinaus kamen auch Menschen aus der DDR hinzu, insbesondere bis 1961. Ab den 1960er-Jahren spielte die Zuwanderung ausländischer Arbeitskräfte die größte Rolle.'', ''Heimatvertriebene, Flüchtlinge und ausländische Arbeitskräfte.'', 47, 15);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (48, ''Welche vier Volksgruppen sind in Deutschland als nationale Minderheiten anerkannt?'', ''Die vier anerkannten nationalen Minderheiten in Deutschland sind die Dänen, die Friesen, die Sorben und die Roma.'', ''Die vier anerkannten nationalen Minderheiten in Deutschland sind die Dänen, die Friesen, die Sorben und die Roma.'', 48, 60);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (49, ''Welche vier Volksgruppen sind in Deutschland als nationale Minderheiten anerkannt?'', ''Die vier anerkannten nationalen Minderheiten in Deutschland sind die Sinti und Roma, die Dänen in Südschleswig, die Sorben in der Lausitz und die deutschen Friesen.'', ''Sinti und Roma, Dänen, Sorben und deutsche Friesen'', 49, 25);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (50, ''Wie lange dauert die allgemeine Schulpflicht in Deutschland?'', ''Die allgemeine Schulpflicht in Deutschland dauert je nach Bundesland 9 bis 10 Jahre.'', ''9 bis 10 Jahre'', 50, 70);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (51, ''Welche landwirtschaftlichen Produkte sind in Deutschland besonders wichtig?'', ''In Deutschland sind landwirtschaftliche Produkte wie Getreide, Zuckerrüben, Milch, Butter, Käse, Quark, Schweine- und Rindfleisch von großer Bedeutung. Deutschland ist zusammen mit Frankreich an der Spitze der EU-Staaten in der Erzeugung hochwertiger tierischer Veredelungsprodukte.'', ''Getreide, Zuckerrüben, Milch, Butter, Käse, Quark, Schweine- und Rindfleisch.'', 51, 44);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (52, ''Welche Energieträger sind in Deutschland am wichtigsten?'', ''In Deutschland sind Erdöl, Erdgas, Steinkohle und Braunkohle die wichtigsten Energieträger. Erdöl ist der wichtigste Energieträger, gefolgt von Erdgas, Steinkohle und Braunkohle.'', ''Erdöl, Erdgas, Steinkohle und Braunkohle'', 52, 11);'),
