('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (58, ''Welche geografischen Strukturen gehören zur Mittelgebirgsschwelle in Deutschland?'', ''Die Mittelgebirgsschwelle gliedert sich in einige größere Mittelgebirgssysteme und einzelne Bergzüge, wie das Rheinische Schiefergebirge mit Eifel, Hunsrück, Bergischem Land, Sauerland, Rothaargebirge, Westerwald und Taunus, sowie Weserbergland, Hessisches Bergland, Harz, Thüringer Wald, Erzgebirge und Elbsandsteingebirge.'', ''Die Mittelgebirgsschwelle besteht aus verschiedenen Mittelgebirgssystemen und Bergzügen, wie dem Rheinischen Schiefergebirge, Weserbergland, Harz und Erzgebirge.'', 58, 74);'),
