('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (60, ''Welcher Fluss ist der längste und wasserreichste in Deutschland?'', ''Der Rhein ist der längste und wasserreichste Fluss Deutschlands mit einer Länge von 1320 km, davon 865 km auf deutschem Gebiet. Er ist die Hauptader eines ausgedehnten mitteleuropäischen Wasserstraßennetzes.'', ''Der Rhein'', 60, 54);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (61, ''Was charakterisiert das Klima Deutschlands?'', ''Das Klima Deutschlands ist ein Übergangsklima zwischen dem maritimen Klima Westeuropas und dem Kontinentalklima Osteuropas. Es ist gekennzeichnet durch eine gemäßigte Klimazone mit genügend Feuchtigkeit vom Atlantik, die zu Niederschlägen zu allen Jahreszeiten führt. Die Temperaturen erreichen ein Maximum im Juli und ein Minimum im Januar.'', ''Ein Übergangsklima zwischen maritimem und kontinentalem Klima mit gemäßigten Temperaturen und Niederschlägen zu allen Jahreszeiten.'', 61, 40);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (62, ''Welche Faktoren haben zum Bevölkerungswachstum in den westlichen Bundesländern Deutschlands nach dem Zweiten Weltkrieg beigetragen?'', ''Die Faktoren, die zum Bevölkerungswachstum in den westlichen Bundesländern Deutschlands nach dem Zweiten Weltkrieg beigetragen haben, waren vor allem der Zuzug von Heimatvertriebenen und Flüchtlingen aus den ehemaligen deutschen Ostgebieten und den angrenzenden Staaten Ost- und Südosteuropas, sowie die Zuwanderung ausländischer Arbeitskräfte seit den 1960er-Jahren.'', ''Zuzug von Heimatvertriebenen und Flüchtlingen, Zuwanderung ausländischer Arbeitskräfte'', 62, 31);'),
