('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (79, ''Welche Bedeutung hat Deutschland in der Erzeugung von tierischen Veredelungsprodukten in der EU?'', ''Deutschland steht zusammen mit Frankreich an der Spitze der EU-Staaten in der Erzeugung hochwertiger tierischer Veredelungsprodukte wie Milch, Butter, Käse und Quark sowie Schweine- und Rindfleisch.'', ''Deutschland ist Spitzenreiter in der EU bei der Erzeugung von Milch, Butter, Käse, Quark, Schweine- und Rindfleisch.'', 79, 56);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (80, ''Welche Energieträger sind in Deutschland am wichtigsten?'', ''Die wichtigsten Energieträger in Deutschland sind Erdöl, gefolgt von Erdgas, Steinkohle und Braunkohle.'', ''Erdöl, Erdgas, Steinkohle und Braunkohle'', 80, 47);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (81, ''Welcher Verkehrsträger überwiegt im Gütertransport und öffentlichen Personenverkehr in Deutschland?<br>A) Schienenverkehr<br>B) Straßenverkehr<br>C) Binnenschifffahrt<br>D) Luftverkehr'', ''B) Straßenverkehr'', ''Straßenverkehr'', 81, 66);'),
('INSERT INTO `Exercise` (`id`, `question`, `answer`, `concise_answer`, `source_id`, `order_index`) VALUES (82, ''Wann trat die Wirtschafts-, Währungs- und Sozialunion zwischen der DDR und der BRD in Kraft?'', ''Die Wirtschafts-, Währungs- und Sozialunion zwischen der DDR und der BRD trat am 1. Juli 1990 in Kraft.'', ''1. Juli 1990'', 82, 36);');